contracts/features/UniswapV3Executor.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.24;
pragma abicoder v1;

import "@uniswap/v3-core/contracts/interfaces/IUniswapV3Pool.sol";
import "@uniswap/v3-core/contracts/interfaces/callback/IUniswapV3SwapCallback.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/math/SafeCast.sol";
import "../Errors.sol";

/**
 * @title UniswapV3Executor
 * @notice Base contract that contains Uniswap V3 specific logic.
 * Uniswap V3 requires specific interface to be implemented so we have to provide a compliant implementation
 */
abstract contract UniswapV3Executor is IUniswapV3SwapCallback {
    using SafeERC20 for IERC20;
    using SafeCast for uint256;

    bytes32 private constant SELECTORS_UNI_SUSHI = 0x0dfe1681d21220a7ddca3f430000000000000000000000000000000000000000;
    bytes32 private constant SELECTORS_AERODROME = 0x0dfe1681d21220a7d0c93a7c0000000000000000000000000000000000000000;

    // init code hashes often follows minimal clone proxy pattern:
    // const initCode = `0x3d602d80600a3d3981f3363d3d373d3d3d363d73${implementationAddress.replace('0x','')}5af43d82803e903d91602b57fd5bf3`
    // where implementationAddress is the "master" address, one of the pools deployed
    // see https://github.com/OpenZeppelin/openzeppelin-contracts/blob/255e27e6d22934ddaf00c7f279039142d725382d/contracts/proxy/Clones.sol#L73
    //
    // Overall, you're looking for keccak256(inputForCreate2)
    // So you're best shot is to look for a tx where one of the pools is deployed, trace it in tenderly and look for the create2 call
    bytes32 private constant INIT_CODE_HASH_UNI_SUSHI = 0xe34f199b19b2b4f47f68442619d555527d244f78a3297ea89325f843f87b8b54;
    bytes32 private constant INIT_CODE_HASH_UNI_AERODROME = 0xffb9af9ea6d9e39da47392ecc7055277b9915b8bfc9f83f105821b7791a6ae30;
    uint256 private constant ADDRESS_MASK = 0x000000000000000000000000ffffffffffffffffffffffffffffffffffffffff;

    bytes32 private immutable PREFIXED_FACTORY_UNI3;
    bytes32 private immutable PREFIXED_FACTORY_SUSHI;
    bytes32 private immutable PREFIXED_FACTORY_AERODROME;

    constructor(address uniswapV3Factory, address sushiV3Factory, address aerodromeSlipStreamFactory) {
        PREFIXED_FACTORY_UNI3 = bytes32((0xff0000000000000000000000000000000000000000 | uint256(uint160(uniswapV3Factory))) << 88);
        PREFIXED_FACTORY_SUSHI = bytes32((0xff0000000000000000000000000000000000000000 | uint256(uint160(sushiV3Factory))) << 88);
        PREFIXED_FACTORY_AERODROME = bytes32((0xff0000000000000000000000000000000000000000 | uint256(uint160(aerodromeSlipStreamFactory))) << 88);
    }

    function uniswapV3SwapCallback(
        int256 amount0Delta,
        int256 amount1Delta,
        bytes calldata data
    ) external override {
        bool isBadPool;
        uint256 amount;
        IERC20 token;

        uint256 poolType;
        assembly {
            poolType := calldataload(data.offset)
        }

        bytes32 factoryHash;
        bytes32 initCodeHash;
        bytes32 selectors;
        if (poolType == 0) {
            factoryHash = PREFIXED_FACTORY_UNI3;
            initCodeHash = INIT_CODE_HASH_UNI_SUSHI;
            selectors = SELECTORS_UNI_SUSHI;
        } else if (poolType == 1) {
            factoryHash = PREFIXED_FACTORY_SUSHI;
            initCodeHash = INIT_CODE_HASH_UNI_SUSHI;
            selectors = SELECTORS_UNI_SUSHI;
        } else if (poolType == 2) {
            factoryHash = PREFIXED_FACTORY_AERODROME;
            initCodeHash = INIT_CODE_HASH_UNI_AERODROME;
            selectors = SELECTORS_AERODROME;
        } else {
            revert UnknownPoolType(poolType);
        }

        assembly {
            function reRevert() {
                let ptr := mload(0x40)
                returndatacopy(ptr, 0, returndatasize())
                revert(ptr, returndatasize())
            }

            let workingAddress := mload(0x40) // EVM free memory pointer
            mstore(workingAddress, selectors)

            // we need to write hash just after the address PREFIXED_FACTORY constant in place of its zeroes,
            // hence offset is 21 bytes
            let feeTokensAddress := add(workingAddress, 21)
            if iszero(staticcall(gas(), caller(), workingAddress, 0x4, feeTokensAddress, 0x20)) {
                reRevert()
            }
            if iszero(staticcall(gas(), caller(), add(workingAddress, 0x4), 0x4, add(feeTokensAddress, 32), 0x20)) {
                reRevert()
            }
            if iszero(staticcall(gas(), caller(), add(workingAddress, 0x8), 0x4, add(feeTokensAddress, 64), 0x20)) {
                reRevert()
            }

            switch sgt(amount0Delta, 0)
            case 1 {
                amount := amount0Delta
                token := mload(feeTokensAddress)
            }
            default {
                amount := amount1Delta
                token := mload(add(feeTokensAddress, 32))
            }

            mstore(workingAddress, factoryHash)
            mstore(feeTokensAddress, keccak256(feeTokensAddress, 96))
            mstore(add(feeTokensAddress, 32), initCodeHash)
            let pool := and(keccak256(workingAddress, 85), ADDRESS_MASK)
            isBadPool := xor(pool, caller())
        }

        if (isBadPool) {
            revert BadUniswapV3LikePool(UniswapV3LikeProtocol.Uniswap);
        }

        token.safeTransfer(msg.sender, amount);
    }
}

contracts/interfaces/Ekubo.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.24;

interface IFlashAccountant {
    // Withdraws a token amount from the accountant to the given recipient.
    // The contract must be locked, as it tracks the withdrawn amount against the current locker's delta.
    function withdraw(address token, address recipient, uint128 amount) external;
    function lock() external;
}

interface ICore is IFlashAccountant {
    function swap_611415377(
        PoolKey memory poolKey,
        int128 amount,
        bool isToken1,
        SqrtRatio sqrtRatioLimit,
        uint256 skipAhead
    ) external payable returns (int128 delta0, int128 delta1);
}

interface ILocker {
    function locked(uint256 id) external;
}

interface IPayer {
    function payCallback(uint256 id, address token) external;
}

address constant NATIVE_TOKEN_ADDRESS = address(0);

// address (20 bytes) | fee (8 bytes) | tickSpacing (4 bytes)
type Config is bytes32;

// Each pool has its own state associated with this key
struct PoolKey {
    address token0;
    address token1;
    Config config;
}

type SqrtRatio is uint96;

uint96 constant MIN_SQRT_RATIO_RAW = 4611797791050542631;
SqrtRatio constant MIN_SQRT_RATIO = SqrtRatio.wrap(MIN_SQRT_RATIO_RAW);
uint96 constant MAX_SQRT_RATIO_RAW = 79227682466138141934206691491;
SqrtRatio constant MAX_SQRT_RATIO = SqrtRatio.wrap(MAX_SQRT_RATIO_RAW);

