// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.28;

import {SafeERC20Upgradeable} from
    "openzeppelin-contracts-upgradeable/contracts/token/ERC20/utils/SafeERC20Upgradeable.sol";
import {IERC20Upgradeable} from "openzeppelin-contracts-upgradeable/contracts/token/ERC20/IERC20Upgradeable.sol";
import {SafeCast} from "openzeppelin/contracts/utils/math/SafeCast.sol";

import {SortedLinkedList} from "src/lib/SortedLinkedList.sol";

import {ISubscriptionBook} from "src/interfaces/ISubscriptionBook.sol";
import {ISettlementToken} from "src/interfaces/ISettlementToken.sol";
import {ISecurityToken} from "src/interfaces/ISecurityToken.sol";
import {IRoleRegistry} from "src/interfaces/IRoleRegistry.sol";
import {IInvestorRegistry} from "src/interfaces/IInvestorRegistry.sol";
import {IDealerRegistry} from "src/interfaces/IDealerRegistry.sol";
import {IInstrumentRegistry} from "src/interfaces/IInstrumentRegistry.sol";
import {IOperationsEngine} from "src/interfaces/IOperationsEngine.sol";
import {OrderDetails, Context} from "src/interfaces/IOrderPipeline.sol";
import {OPERATIONS} from "src/interfaces/operations/IOperationModule.sol";

import "src/utils/Constant.sol";

import {OrderPipeline} from "src/OrderPipeline.sol";
import {PermissionedContract} from "src/PermissionedContract.sol";

/**
 * @title SubscriptionBook
 * @author Libre
 * @notice This contract manages subscription orders for financial instruments, providing functionalities for order creation,
 *         settlement, cancellation, and instant settlement. It enforces business rules and ensures compliance with subscription policies.
 * @dev Utilizes `OrderPipeline` for shared order management functionalities and interacts with contracts like
 *      `ISettlementToken`, `ISecurityToken`, and `IOperationsEngine`.
 */
contract SubscriptionBook is OrderPipeline, ISubscriptionBook {
    using SortedLinkedList for SortedLinkedList.List;
    using SafeERC20Upgradeable for ISettlementToken;

    constructor() {
        _disableInitializers();
    }

    /**
     * @notice Initializes the SubscriptionBook contract with necessary dependencies.
     * @param _instrumentId The ID of the instrument associated with this subscription.
     * @param _settlementToken Address of the settlement token used for subscriptions.
     * @param _operationsEngine Address of the operations engine for processing business rules.
     * @param _securityToken Address of the security token contract.
     * @param _treasury Address of the treasury wallet.
     * @param _roleRegistry Address of the role registry for access control.
     * @param _investorRegistry Address of the investor registry.
     * @param _dealerRegistry Address of the dealer registry.
     * @param _instrumentRegistry Address of the instrument registry for managing instrument data.
     */
    function initialize(
        bytes32 _instrumentId,
        ISettlementToken _settlementToken,
        IOperationsEngine _operationsEngine,
        ISecurityToken _securityToken,
        address _treasury,
        address _policyViewer,
        IRoleRegistry _roleRegistry,
        IInvestorRegistry _investorRegistry,
        IDealerRegistry _dealerRegistry,
        IInstrumentRegistry _instrumentRegistry
    ) external initializer {
        OrderPipelineStorage storage ops = _getOrderPipelineStorage();
        ops.securityToken = _securityToken;
        ops.instrumentId = _instrumentId;
        ops.instrumentRegistry = _instrumentRegistry;
        ops.operationsEngine = _operationsEngine;
        ops.investorRegistry = _investorRegistry;
        ops.dealerRegistry = _dealerRegistry;
        ops.settlementToken = _settlementToken;
        ops.treasury = _treasury;
        ops.policyViewer = _policyViewer;

        ops.currentPeriod = 1;

        PermissionedContractStorage storage pcs = _getPermissionedContractStorage();
        pcs.roleRegistry = _roleRegistry;
    }

    function investorCreateOrder(OrderProofData calldata _proof) external returns (uint256 orderId) {
        OrderPipelineStorage storage ops = _getOrderPipelineStorage();
        Context memory ctx = _getContext(ops);
        _checkInstrumentNotPaused(ctx.instrumentRegistry, ctx.instrumentId);

        {
            (, orderId) = _beforeOrderCheck(ctx, ops, _proof, msg.sender, msg.sender, false);

            bytes memory data = abi.encode(_proof.journal.investorId, _proof.journal.amount);
            bytes memory result = ctx.operationsEngine.check(OPERATIONS.CREATE_SUBSCRIPTION, ctx.instrumentId, data);

            (bool checkSuccess, string memory errorMsg) = abi.decode(result, (bool, string));
            require(checkSuccess, errorMsg);

            ctx.operationsEngine.track(OPERATIONS.CREATE_SUBSCRIPTION, ctx.instrumentId, data);
        }

        _createOrder(
            ctx,
            ops,
            _proof.journal.investorId,
            _proof.journal.amount,
            msg.sender,
            msg.sender,
            orderId,
            OPERATIONS.SUBSCRIPTION_EXECUTION_DATE_CALCULATION,
            IERC20Upgradeable(address(ctx.settlementToken))
        );

        _afterOrderCheck(ctx, ops, orderId);

        return orderId;
    }

    function dealerCreateOrder(OrderProofData calldata _proof, address _investorWallet, address _beneficiaryWallet)
        external
        returns (uint256 orderId)
    {
        OrderPipelineStorage storage ops = _getOrderPipelineStorage();
        Context memory ctx = _getContext(ops);
        _checkInstrumentNotPaused(ctx.instrumentRegistry, ctx.instrumentId);

        address sender;
        (sender, orderId) = _beforeOrderCheck(ctx, ops, _proof, _investorWallet, _beneficiaryWallet, true);

        {
            bytes memory data = abi.encode(_proof.journal.investorId, _proof.journal.amount);
            bytes memory result =
                ctx.operationsEngine.check(OPERATIONS.ADVISED_CREATE_SUBSCRIPTION, ctx.instrumentId, data);
            (bool checkSuccess, string memory errorMsg) = abi.decode(result, (bool, string));
            require(checkSuccess, errorMsg);

            ctx.operationsEngine.track(OPERATIONS.ADVISED_CREATE_SUBSCRIPTION, ctx.instrumentId, data);
        }

        _createOrder(
            ctx,
            ops,
            _proof.journal.investorId,
            _proof.journal.amount,
            _beneficiaryWallet,
            sender,
            orderId,
            OPERATIONS.SUBSCRIPTION_EXECUTION_DATE_CALCULATION,
            IERC20Upgradeable(address(ctx.settlementToken))
        );
        _afterOrderCheck(ctx, ops, orderId);

        return orderId;
    }

    function settleOrders(uint256 _lastOrderId, uint256 _percentageToSettle, bytes32 _role) external {
        OrderPipelineStorage storage ops = _getOrderPipelineStorage();
        Context memory ctx = _getContext(ops);

        if (ctx.instrumentRegistry.getBool(ctx.instrumentId, INSTRUMENT_CLOSED)) {
            revert IOrderPipeline_InstrumentClosed();
        }

        _checkRoleHasAccess(_role);

        uint256[] memory allOrders = _beforeSettleOrders(ctx, ops, _percentageToSettle, _lastOrderId, true);

        uint256 settledAmount = _processSettlements(ctx, ops, allOrders, _lastOrderId, _percentageToSettle);

        ctx.operationsEngine.track(OPERATIONS.SETTLE_SUBSCRIPTIONS, ctx.instrumentId, abi.encode(settledAmount));
    }

    function getSubscriptionAmount(uint256 _orderId) external view returns (uint256 _amount) {
        OrderPipelineStorage storage ops = _getOrderPipelineStorage();

        _amount = ops.orders[_orderId].amount;
    }

    function adminCancelOrder(uint256 _orderId, bytes32 _role) public override {
        OrderPipelineStorage storage ops = _getOrderPipelineStorage();
        Context memory ctx = _getContext(ops);
        OrderDetails storage order = ops.orders[_orderId];

        // tracking just the unsettled amount (order.amount - order.settledAmount)
        bytes memory data = abi.encode(order.investorId, order.amount - order.settledAmount);
        ctx.operationsEngine.track(OPERATIONS.SUBSCRIPTION_CANCELLATION, ctx.instrumentId, data);

        super.adminCancelOrder(_orderId, _role);
    }

    function _processSettlements(
        Context memory _ctx,
        OrderPipelineStorage storage _ops,
        uint256[] memory _allOrders,
        uint256 _lastOrderId,
        uint256 _percentageToSettle
    ) internal returns (uint256 settledAmount) {
        uint256 nav = _ctx.instrumentRegistry.getUint(_ctx.instrumentId, AUDITED_NAV_PER_SHARE);
        uint256 decimals = 10 ** _ctx.securityToken.decimals();

        (uint256 totalToSettle, OrderDetails[] memory ordersToSettle) =
            _getTotalToSettle(_ctx, _ops, _allOrders, _lastOrderId, _percentageToSettle, nav);

        uint256 nextOrderId;

        for (uint256 i;; ++i) {
            OrderDetails memory order = ordersToSettle[i];

            // Calculate the amount of SET to be settled for this order
            uint256 toSettle = _checkSettleOrder(_ctx, _allOrders[i], order);

            // Determine the amount to settle by taking the minimum of:
            // - `totalToSettle`: The maximum settlement amount for all orders.
            // - `toSettle`: The allowed amount for this specific order, as checked by external modules.
            // - `percentageToSettle`: The requested settlement percentage of the remaining unsettled amount.
            // Note: remaining (un settled) amount is (_order.amount - _order.settledAmount)
            // Ensures that the final settlement amount does not exceed the order's allowed or available limits.
            {
                uint256 allowedAmountByPercentage = (order.amount - order.settledAmount) * _percentageToSettle / 100_00;
                uint256 minimumAmount = toSettle < allowedAmountByPercentage ? toSettle : allowedAmountByPercentage;
                toSettle = totalToSettle < minimumAmount ? totalToSettle : minimumAmount;
            }

            if (toSettle != 0) {
                _ops.orders[_allOrders[i]].settledAmount += SafeCast.toUint128(toSettle);
                _ops.settledAmount += toSettle;
                totalToSettle -= toSettle;
                settledAmount += toSettle;

                // cache nextOrderId if this iteration will break the loop
                if (_allOrders[i] == _lastOrderId || totalToSettle == 0) {
                    nextOrderId = _ops.ordersList.getNext(_allOrders[i]);
                }

                _partialSettlement(
                    _ctx,
                    _ops,
                    _allOrders[i],
                    order.investorId,
                    toSettle,
                    order.executionTimestamp,
                    order.beneficiary,
                    nav,
                    decimals
                );
            }
            if (_allOrders[i] == _lastOrderId || totalToSettle == 0) {
                // update storage nextOrderToSettle using cached value
                _ops.nextOrderToSettle = nextOrderId;
                break;
            }
        }
    }

    function _getTotalToSettle(
        Context memory _ctx,
        OrderPipelineStorage storage _ops,
        uint256[] memory _allOrders,
        uint256 _lastOrderId,
        uint256 _percentageToSettle,
        uint256 _nav
    ) internal returns (uint256 totalToSettle, OrderDetails[] memory ordersToSettle) {
        IOperationsEngine operationsEngine = _ctx.operationsEngine;

        ordersToSettle = new OrderDetails[](_allOrders.length);
        uint256 aggregate;
        for (uint256 i; i < _allOrders.length; ++i) {
            ordersToSettle[i] = _ops.orders[_allOrders[i]];
            aggregate +=
                (ordersToSettle[i].amount - ordersToSettle[i].settledAmount) * _percentageToSettle / PERCENTAGE_BASE;
        }

        bytes memory result = operationsEngine.check(
            OPERATIONS.SETTLE_SUBSCRIPTIONS,
            _ctx.instrumentId,
            abi.encode(_ops.settledAmount, _lastOrderId, aggregate, _percentageToSettle, _nav)
        );
        string memory errorMsg;
        (totalToSettle, errorMsg) = abi.decode(result, (uint256, string));
        require(totalToSettle != 0, errorMsg);
    }

    /**
     * @dev Performs pre-order checks to validate investor and subscription data.
     * @param _ctx The context of the order pipeline.
     * @param _ops The Order Pipeline Storage.
     * @param _proof The proof containing investor and subscription data.
     * @param _investorWallet The wallet address of the investor.
     * @param _beneficiaryWallet The address that will benefit from the subscription.
     * @param _isFromDealer Indicates whether the order is created by a dealer on behalf of an investor.
     * @return sender The address initiating the transaction.
     * @return orderId The generated order ID.
     */
    function _beforeOrderCheck(
        Context memory _ctx,
        OrderPipelineStorage storage _ops,
        OrderProofData calldata _proof,
        address _investorWallet,
        address _beneficiaryWallet,
        bool _isFromDealer
    ) internal returns (address sender, uint256 orderId) {
        if (_proof.journal.policyViewer != _ops.policyViewer) {
            revert IOrderPipeline_PolicyViewerProofMismatch();
        }

        if (_proof.journal.instrumentId != _ctx.instrumentId) {
            revert IOrderPipeline_InstrumentIdProofMismatch();
        }

        if (!_isFromDealer && _proof.journal.investorId != _ctx.investorRegistry.getIdFromWallet(msg.sender)) {
            revert IOrderPipeline_InvestorIdProofMismatch();
        }

        _ctx.investorRegistry.checkCredentialInstrument(_proof.journal.investorId, _ctx.instrumentId);
        _ctx.investorRegistry.checkSubscription(_proof);

        (sender, orderId) = super._beforeOrderCheck(
            _ctx,
            _ops,
            _proof.journal.amount,
            _proof.journal.investorId,
            _investorWallet,
            _beneficiaryWallet,
            _isFromDealer
        );

        if (_ctx.settlementToken.balanceOf(sender) < _proof.journal.amount) {
            revert IOrderPipeline_InsufficientAmount();
        }

        return (sender, orderId);
    }

    function _sendReceivedToken(Context memory _ctx, address _receiver, uint256 _amount) internal override {
        uint256 beforeTransferBalance = _ctx.settlementToken.balanceOf(_receiver);
        _ctx.settlementToken.safeTransfer(_receiver, _amount);
        if (_ctx.settlementToken.balanceOf(_receiver) != beforeTransferBalance + _amount) {
            revert IOrderPipeline_SettlementTokenTransferFailed();
        }
    }

    /**
     * @notice Instant settlement of an order when the all the 4 phases are executed in the same transaction.
     * @param _ctx The context of the order pipeline.
     * @param _ops The Order Pipeline Storage.
     * @param _orderId Id of the order to settle
     */
    function _instantSettlement(Context memory _ctx, OrderPipelineStorage storage _ops, uint256 _orderId)
        internal
        override
    {
        OrderDetails memory order = _ops.orders[_orderId];

        _beforeSettlementUpdates(_ctx, _ops, true);

        _ops.orders[_orderId].settledAmount += order.amount;
        _ops.settledAmount += order.amount;
        _partialSettlement(
            _ctx,
            _ops,
            _orderId,
            order.investorId,
            order.amount,
            order.executionTimestamp,
            order.beneficiary,
            _ctx.instrumentRegistry.getUint(_ctx.instrumentId, AUDITED_NAV_PER_SHARE),
            10 ** _ctx.securityToken.decimals()
        );
    }

    /**
     * @dev Handles the partial settlement of a subscription order.
     * Mints security tokens for the beneficiary and deducts the settled amount.
     * @param _ctx The context of the order pipeline.
     * @param _ops The storage struct containing pipeline data.
     * @param _orderId The ID of the order being settled.
     * @param _investorId The ID of the investor associated with the order.
     * @param _amountToSettle The amount to be settled in the current transaction.
     * @param _beneficiaryWallet The address receiving the security tokens.
     * @param _nav The Net Asset Value per share used for calculations.
     * @param _decimals The decimal precision of the security token.
     */
    function _partialSettlement(
        Context memory _ctx,
        OrderPipelineStorage storage _ops,
        uint256 _orderId,
        bytes32 _investorId,
        uint256 _amountToSettle,
        uint256 _executionTimestamp,
        address _beneficiaryWallet,
        uint256 _nav,
        uint256 _decimals
    ) internal {
        if (_amountToSettle != 0) {
            _ctx.settlementToken.burn(address(this), _amountToSettle, SETTLEMENT_TOKEN_BURNER_ROLE);
        }

        uint256 amountToIssue = (_amountToSettle * _decimals) / _nav;
        if (amountToIssue != 0) {
            _ctx.securityToken.issue(_beneficiaryWallet, amountToIssue);
        } else {
            revert ISubscriptionBook_AmountToIssueIsZero();
        }

        _updateOrder(_ops, _orderId, _ops.orders[_orderId].amount == (_ops.orders[_orderId].settledAmount));

        bytes memory data = abi.encode(_investorId, _amountToSettle, amountToIssue, _executionTimestamp);
        _ctx.operationsEngine.track(OPERATIONS.SETTLE_SUBSCRIPTION_PER_ORDER, _ctx.instrumentId, data);

        emit OrderSettled(_orderId, _amountToSettle);
    }

    /**
     * @notice Calculates the amount that can be settled for an order.
     * @dev Fetches allowed settlement from external modules, applies percentage-based limit,
     *      and returns the minimum of all constraints.
     * @param _ctx The context of the order pipeline.
     * @param _order The order details.
     * @return allowedToSettle The final calculated amount to settle for the given order.
     */
    function _checkSettleOrder(Context memory _ctx, uint256 _orderId, OrderDetails memory _order)
        internal
        returns (uint256 allowedToSettle)
    {
        // get allowed settlement amount from external modules for this specific order
        // Even if we can settle the whole order or the remaining amount in the order
        // the check module may return the allowed amount which may be less than the remaining amount
        // check the amount that can be settled after fetching some modules.
        // note: (_order.amount - _order.settledAmount) is the remaining amount to be settled (unsettledAmount)
        bytes memory perOrderResult = _ctx.operationsEngine.check(
            OPERATIONS.SETTLE_SUBSCRIPTION_PER_ORDER,
            _ctx.instrumentId,
            abi.encode(_order.executionTimestamp, 0, _order.amount - _order.settledAmount, 0, 0, _order.investorId) // 0 is for the check to work
        );

        // decode the response from external checks
        string memory errorMsg;
        (allowedToSettle, errorMsg) = abi.decode(perOrderResult, (uint256, string));
        if (allowedToSettle == 0) {
            emit OrderSettlementSkipped(_orderId, errorMsg);
        }
    }
}

