// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.28;

library SortedLinkedList {
    /**
     * @dev Pointers to the prev and next nodes in the list.
     */
    struct Node {
        uint256 prev;
        uint256 next;
    }

    struct List {
        uint256 begin; // The first id in the list.
        uint256 end; // The last id in the list.
        uint256 size; // The total amount of ids in the list.
        // Mapping of nodes ids to their prev and next nodes.
        mapping(uint256 nodeId => Node node) nodes;
    }

    error SortedLinked_ListIDNotInList();
    error SortedLinked_ListIDAlreadyInList();
    error SortedLinked_ListInvalidRange();
    error SortedLinked_ListInvalidId();

    /**
     * @notice Insert a node into the list while maintaining an ascending order for _l.nodes.
     * @param _l The sorted linked list to instert the node in
     * @param _id The ID of the node to insert.
     */
    function insert(List storage _l, uint256 _id) public {
        if (_id == 0) {
            revert SortedLinked_ListInvalidId();
        }
        uint256 begin = _l.begin;
        uint256 end = _l.end;
        uint256 size = _l.size;
        if (size == 0) {
            _l.begin = _id;
            _l.end = _id;
        } else if (_id > end) {
            _l.nodes[end].next = _id;
            _l.nodes[_id].prev = end;
            _l.end = _id;
        } else if (_id < begin) {
            _l.nodes[begin].prev = _id;
            _l.nodes[_id].next = begin;
            _l.begin = _id;
        } else {
            uint256 current = begin;
            for (uint256 i; i < size; ++i) {
                if (_id < current) {
                    uint256 prev = _l.nodes[current].prev;
                    _l.nodes[prev].next = _id;
                    _l.nodes[_id].prev = prev;
                    _l.nodes[current].prev = _id;
                    _l.nodes[_id].next = current;
                    break;
                }
                if (_id == current) {
                    revert SortedLinked_ListIDAlreadyInList();
                }
                current = _l.nodes[current].next;
            }
        }
        ++_l.size;
    }

    /**
     * @notice Remove a node from the list.
     * @param _l The sorted linked list to remove a node from
     * @param _id The ID to remove.
     */
    function erase(List storage _l, uint256 _id) public {
        uint256 begin = _l.begin;
        uint256 end = _l.end;
        uint256 size = _l.size;
        if (_id < begin || _id > end || size == 0 || ((size == 1) && (_id != begin))) {
            revert SortedLinked_ListIDNotInList();
        }
        if (size == 1) {
            delete _l.begin;
            delete _l.end;
        } else if (_id == begin) {
            uint256 next = _l.nodes[_id].next;
            _l.begin = next;
            delete _l.nodes[next].prev;
        } else if (_id == end) {
            uint256 prev = _l.nodes[_id].prev;
            _l.end = prev;
            delete _l.nodes[prev].next;
        } else {
            uint256 next = _l.nodes[_id].next;
            if (next == 0) {
                // no way 0 is next if node is on the list
                // and it is not the end one
                revert SortedLinked_ListIDNotInList();
            }

            uint256 prev = _l.nodes[_id].prev;
            _l.nodes[prev].next = next;
            _l.nodes[next].prev = prev;
        }
        delete _l.nodes[_id];
        _l.size--;
    }

    /**
     * @return An array of all ids of the nodes in the sorted list.
     * @param _l The sorted linked list to get the ids from
     */
    function getNodesId(List storage _l) public view returns (uint256[] memory) {
        uint256 size = _l.size;
        if (size == 0) return new uint256[](0);
        uint256[] memory result = new uint256[](size);
        uint256 current = _l.begin;
        uint256 i;
        while (i < size) {
            result[i] = current;
            current = _l.nodes[current].next;
            ++i;
        }
        return result;
    }

    /**
     * @notice An array of all ids of the nodes in the sorted list in range [_from, _to]
     * @param _l The sorted linked list to get the ids from
     * @param _from The ID to begin from.
     * @param _to The ID to end.
     */
    function getNodesIdInRange(List storage _l, uint256 _from, uint256 _to)
        public
        view
        returns (uint256[] memory allOrderIds)
    {
        if (_to != 0) {
            if (
                (_from > _to) || (_from != _l.begin && _l.nodes[_from].prev == 0)
                    || (_to != _l.end && _l.nodes[_to].next == 0)
            ) {
                revert SortedLinked_ListInvalidRange();
            }

            allOrderIds = new uint256[](_l.size);
            uint256 i;

            while (_from != 0 && _from <= _to) {
                allOrderIds[i] = _from;
                _from = _l.nodes[_from].next;
                ++i;
            }

            // resize the array dynamically using Yul
            assembly {
                mstore(allOrderIds, i)
            }
        }
    }

    /**
     * @notice Return the next of a node
     * @param _l The sorted linked list where the node belongs to
     * @param _id The ID of the node to get the next of
     * @dev Next is returned for any node existent or not. If the node
     * requested is the end of the list 0 is returned
     */
    function getNext(List storage _l, uint256 _id) internal view returns (uint256) {
        return _l.nodes[_id].next;
    }
}

