// SPDX-License-Identifier: BUSL-1.1
pragma solidity ^0.8.0;

import {IERC20} from "openzeppelin-contracts/contracts/token/ERC20/IERC20.sol";
import {IInvestorRegistry} from "src/interfaces/IInvestorRegistry.sol";
import {IDealerRegistry} from "src/interfaces/IDealerRegistry.sol";
import {IFundRegistry} from "src/interfaces/IFundRegistry.sol";
import {IInstrumentRegistry} from "src/interfaces/IInstrumentRegistry.sol";
import {IRoleRegistry} from "src/interfaces/IRoleRegistry.sol";
import {IOperationsEngine} from "src/interfaces/IOperationsEngine.sol";
import {IOperationsEngine} from "src/interfaces/IOperationsEngine.sol";
import {ISecurityToken} from "src/interfaces/ISecurityToken.sol";
import {ISettlementToken} from "src/interfaces/ISettlementToken.sol";
import {IOrderPipeline} from "src/interfaces/IOrderPipeline.sol";

/**
 * @title ISubscriptionBook
 * @author Libre
 * @notice Interface for managing subscription books, including initialization, subscription tracking, and investor information.
 * @dev Extends the `IOrderPipeline` interface for managing order pipelines.
 */
interface ISubscriptionBook is IOrderPipeline {
    /**
     * @dev Revert when the amount to issue is zero
     */
    error ISubscriptionBook_AmountToIssueIsZero();

    /**
     * @notice Initializes the subscription
     * @param _instrumentId Id of the instrument the subscription is associated to
     * @param _settlementToken Address of the settlement token
     * @param _operationsEngine Address of the operations engine
     * @param _securityToken Address of the instrument's security token
     * @param _treasury Address of the instrument's treasury
     * @param _policyViewer Address of the instrument's policy viewer
     * @param _roleRegistry Address of the role registry
     * @param _investorRegistry Address of the investor registry
     * @param _dealerRegistry Address of the dealer registry
     * @param _instrumentRegistry Address of the instrument registry
     */
    function initialize(
        bytes32 _instrumentId,
        ISettlementToken _settlementToken,
        IOperationsEngine _operationsEngine,
        ISecurityToken _securityToken,
        address _treasury,
        address _policyViewer,
        IRoleRegistry _roleRegistry,
        IInvestorRegistry _investorRegistry,
        IDealerRegistry _dealerRegistry,
        IInstrumentRegistry _instrumentRegistry
    ) external;

    /**
     * @notice Gets the subscription amount of an order
     * @param _orderId ID of the order
     * @return amount_ Amount of the subscription
     */
    function getSubscriptionAmount(uint256 _orderId) external view returns (uint256 amount_);
}

src/interfaces/ISettlementToken.sol.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity ^0.8.0;

import {IERC20MetadataUpgradeable} from
    "openzeppelin-contracts-upgradeable/contracts/token/ERC20/extensions/IERC20MetadataUpgradeable.sol";

/**
 * @title ISettlementToken
 * @author Libre
 * @notice Interface for settlement tokens, providing minting and burning capabilities with role-based access control.
 * @dev This interface extends the `IERC20MetadataUpgradeable` for ERC20 token standard compliance.
 */
interface ISettlementToken is IERC20MetadataUpgradeable {
    /**
     * @notice Thrown when a transfer operation is not allowed due to insufficient permissions or other restrictions.
     */
    error ISettlementToken_TransferNotAllowed();

    /**
     * @notice Thrown when a token is paused.
     */
    error ISettlementToken_TokenPaused();

    /**
     * @notice Emitted when the name and symbol of the settlement token are updated.
     * @param _name The new name of the token.
     * @param _symbol The new symbol of the token.
     */
    event NameAndSymbolChanged(string _name, string _symbol);

    /**
     * @notice Issue tokens to an address
     * @param _account Address to mint to
     * @param _amount Amount issued
     * @param _role Role of the minter
     */
    function mint(address _account, uint256 _amount, bytes32 _role) external;

    /**
     * @notice Burn tokens from an address
     * @param _account Address to burn from
     * @param _amount Amount burned
     * @param _role Role of the burner
     */
    function burn(address _account, uint256 _amount, bytes32 _role) external;

    /**
     * @notice Burn all tokens from an address
     * @param _account Address to burn from
     * @param _role Role of the burner
     */
    function burnAll(address _account, bytes32 _role) external;
}

