// SPDX-License-Identifier: BUSL-1.1
pragma solidity ^0.8.0;

import {ISecurityToken} from "./ISecurityToken.sol";
import {ISettlementToken} from "./ISettlementToken.sol";
import {IInstrumentRegistry} from "./IInstrumentRegistry.sol";
import {IInvestorRegistry} from "./IInvestorRegistry.sol";
import {IDealerRegistry} from "./IDealerRegistry.sol";
import {IOperationsEngine} from "./IOperationsEngine.sol";

struct OrderDetails {
    uint128 settledAmount; // The amount of tokens already settled.
    uint128 amount; // The amount involved in the order.
    uint32 timestamp; // The timestamp when the order was created or last updated.
    uint32 executionTimestamp; // The timestamp of the order's execution.
    address beneficiary; // The address that will benefit from the order.
    address sender; // The address that initiated the order.
    bytes32 investorId; // The unique ID of the investor associated with the order.
}

/**
 * @dev Context struct to hold frequently accessed storage variables in memory
 * to avoid repeated SLOADs across SubscriptionBook and RedemptionBook.
 * This struct is used to optimize gas usage by caching storage variables that are accessed multiple times
 * within a single transaction. Variables that are only used once should be accessed directly from storage
 * to avoid unnecessary memory operations.
 */
struct Context {
    bytes32 instrumentId;
    IOperationsEngine operationsEngine;
    IInstrumentRegistry instrumentRegistry;
    IInvestorRegistry investorRegistry;
    ISettlementToken settlementToken;
    ISecurityToken securityToken;
}

/**
 * @title Order Pipeline Interface
 * @author Libre
 * @notice Interface for managing order-related operations in the system.
 * @dev Defines errors, events, and function signatures for order processing.
 */
interface IOrderPipeline {
    // Errors
    /**
     * @dev Revert when an unauthorized action is attempted.
     */
    error IOrderPipeline_Unauthorized();

    /**
     * @dev Revert when the beneficiary is not the expected investor.
     */
    error IOrderPipeline_BeneficiaryIsNotInvestor();

    /**
     * @dev Revert when the provided amount is insufficient.
     */
    error IOrderPipeline_InsufficientAmount();

    /**
     * @dev Revert when the instrument is already closed.
     */
    error IOrderPipeline_InstrumentClosed();

    /**
     * @dev Revert when the order with the given ID is not found.
     */
    error IOrderPipeline_OrderNotFound();

    /**
     * @dev Revert when the provided percentage is invalid.
     */
    error IOrderPipeline_PercentageNotApt();

    /**
     * @dev Revert when the order cannot be locked.
     */
    error IOrderPipeline_OrderCannotBeLocked();

    /**
     * @dev Revert when the wrong investor is referenced.
     */
    error IOrderPipeline_WrongInvestor();

    /**
     * @dev Revert when no investor wallets are available.
     */
    error IOrderPipeline_NoInvestorWallets();

    /**
     * @dev Revert when the order is not locked.
     */
    error IOrderPipeline_OrderNotLocked();

    /**
     * @dev Revert when the order is already settled in the current round.
     */
    error IOrderPipeline_OrderAlreadySettledInCurrentRound();

    /**
     * @dev Revert when the order cannot be confirmed.
     */
    error IOrderPipeline_OrderCannotBeConfirmed();

    /**
     * @dev Revert when the settlement token transfer fails.
     */
    error IOrderPipeline_SettlementTokenTransferFailed();

    /**
     * @dev Revert when the security token transfer fails.
     */
    error IOrderPipeline_SecurityTokenTransferFailed();

    /**
     * @dev Revert when settlement is already executed.
     */
    error IOrderPipeline_SettlementAlreadyExecuted();

    /**
     * @dev Revert when there is a mismatch in the instrument ID proof.
     */
    error IOrderPipeline_InstrumentIdProofMismatch();

    /**
     * @dev Revert when there is a mismatch in the investor ID proof.
     */
    error IOrderPipeline_InvestorIdProofMismatch();

    /**
     * @dev Revert when there is a mismatch in the policy viewer proof.
     */
    error IOrderPipeline_PolicyViewerProofMismatch();

    /**
     * @dev Revert when the wallet used in the order details is blacklisted.
     */
    error IOrderPipeline_WalletBlacklisted();

    /**
     * @dev Revert if the period is decreasing
     */
    error IOrderPipeline_PeriodDecreasing();

    /**
     * @dev Revert when the instrument is paused.
     */
    error IOrderPipeline_InstrumentPaused();

    /**
     * @notice Used when orders are cancelled
     * @param orderId Id of the cancelled order
     * @param amount Numeric value to identify the cancelled order amount
     */
    event OrderCancelled(uint256 orderId, uint256 amount);

    /**
     * @notice Used when orders are settled
     * @param orderId Id of the settled order
     * @param amount Amount settled
     */
    event OrderSettled(uint256 orderId, uint256 amount);

    /// @dev Emitted when an order is created.
    event OrderCreated(uint256 orderId, bytes32 investorId, uint256 amount, address initiator);

    /**
     * @notice Emitted when an order is skipped during settlement.
     * @param orderId The ID of the skipped order.
     * @param errorMsg The error message explaining why the order was skipped.
     */
    event OrderSettlementSkipped(uint256 orderId, string errorMsg);

    /**
     * @notice Settles a batch of orders for the current period.
     * @param _lastOrderId The ID of the last order in the settlement batch.
     * @param _percentageToSettle The percentage of each order amount to settle.
     */
    function settleOrders(uint256 _lastOrderId, uint256 _percentageToSettle, bytes32 _role) external;

    /**
     * @notice Retrieves the total number of orders created.
     * @return The total count of orders.
     */
    function getTotalOrdersCount() external view returns (uint256);

    /**
     * @notice Retrieves the next order ID to be settled.
     * @return The next order ID for settlement.
     */
    function getNextOrderToSettle() external view returns (uint256);

    /**
     * @notice Retrieves the list of all active order IDs.
     * @return An array containing all active order IDs.
     */
    function getActiveOrderIds() external view returns (uint256[] memory);

    /**
     * @notice Get all details of an order
     * @return Order details
     */
    function getOrder(uint256 _orderId) external view returns (OrderDetails memory);
}

