// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.28;

import {IERC20Upgradeable} from "openzeppelin-contracts-upgradeable/contracts/token/ERC20/IERC20Upgradeable.sol";
import {SafeERC20Upgradeable} from
    "openzeppelin-contracts-upgradeable/contracts/token/ERC20/utils/SafeERC20Upgradeable.sol";
import {SafeCast} from "openzeppelin/contracts/utils/math/SafeCast.sol";

import {SortedLinkedList} from "src/lib/SortedLinkedList.sol";

import {ISecurityToken} from "src/interfaces/ISecurityToken.sol";
import {ISettlementToken} from "src/interfaces/ISettlementToken.sol";
import {IOrderPipeline, OrderDetails, Context} from "src/interfaces/IOrderPipeline.sol";
import {IInstrumentRegistry} from "src/interfaces/IInstrumentRegistry.sol";
import {IInvestorRegistry} from "src/interfaces/IInvestorRegistry.sol";
import {IDealerRegistry} from "src/interfaces/IDealerRegistry.sol";

import {LibreUUPSUpgradeable} from "src/utils/LibreUUPSUpgradeable.sol";
import "src/utils/Constant.sol";

import {IOperationsEngine, OPERATIONS} from "src/interfaces/IOperationsEngine.sol";

import "src/utils/Constant.sol";

/**
 * @title OrderPipeline
 * @author Libre
 * @notice This contract serves as the base for unified order pipeline management.
 * It provides methods for managing order lifecycle events such as creation, cancellation, and settlement.
 */
abstract contract OrderPipeline is LibreUUPSUpgradeable, IOrderPipeline {
    using SortedLinkedList for SortedLinkedList.List;
    using SafeERC20Upgradeable for IERC20Upgradeable;

    // keccak256(abi.encode(uint256(keccak256("OrderPipeline.storage")) - 1)) & ~bytes32(uint256(0xff));
    bytes32 private constant ORDER_PIPELINE_STORAGE_SLOT =
        0x110c926fa16509da894f0a2a08278b82b2a2f9ffc38d1ed1975f32ef34198800;

    /// @custom:storage-location erc7201:OrderPipeline.storage
    struct OrderPipelineStorage {
        /// @notice The token used for settlement of orders.
        ISettlementToken settlementToken;
        /// @notice The security token associated with the instrument.
        ISecurityToken securityToken;
        /// @notice Registry for instrument details.
        IInstrumentRegistry instrumentRegistry;
        /// @notice Operations engine for executing modules on operations.
        IOperationsEngine operationsEngine;
        /// @notice Registry for investor information.
        IInvestorRegistry investorRegistry;
        /// @notice Registry for dealer information.
        IDealerRegistry dealerRegistry;
        /// @notice Address of the treasury.
        address treasury;
        /// @notice Address of the policy viewer.
        address policyViewer;
        /// @notice ID of the associated instrument.
        bytes32 instrumentId;
        /// @notice Current period of the pipeline.
        uint256 currentPeriod; // should be initialized as 1 in the initialize function
        /// @notice Counter for the number of orders created.
        uint256 ordersCount;
        /// @notice ID of the next order to be settled.
        uint256 nextOrderToSettle;
        /// @notice Total amount settled in the current period.
        uint256 settledAmount;
        /// @notice The orders mapping contains all the orders.
        mapping(uint256 orderId => OrderDetails order) orders;
        /// @notice Linked list containing all active orders.
        SortedLinkedList.List ordersList;
    }

    /**
     * @notice Retrieves order details by ID.
     * @param _orderId The ID of the order.
     * @return The details of the order.
     */
    function getOrder(uint256 _orderId) external view returns (OrderDetails memory) {
        OrderPipelineStorage storage s = _getOrderPipelineStorage();
        return s.orders[_orderId];
    }

    /**
     * @notice Allows an admin to cancel an order.
     * @param _orderId The ID of the order to cancel.
     * @param _role The role of the admin performing the cancellation.
     */
    function adminCancelOrder(uint256 _orderId, bytes32 _role) public virtual {
        OrderPipelineStorage storage ops = _getOrderPipelineStorage();
        Context memory ctx = _getContext(ops);
        _checkRoleHasAccess(_role);
        OrderDetails memory order = ops.orders[_orderId];
        if (order.sender == address(0)) {
            revert IOrderPipeline_OrderNotFound();
        }

        uint256 amountLeft = order.amount - order.settledAmount;

        _afterCancelOrderCheck(ctx, ops, _orderId, order, amountLeft);

        _updateOrder(ops, _orderId, true);
    }

    /**
     * @notice Retrieves the total number of orders created.
     * @return The total count of orders.
     */
    function getTotalOrdersCount() public view returns (uint256) {
        OrderPipelineStorage storage s = _getOrderPipelineStorage();
        return s.ordersCount;
    }

    /**
     * @notice Retrieves the next order ID to be settled.
     * @return The next order ID for settlement.
     */
    function getNextOrderToSettle() public view returns (uint256) {
        OrderPipelineStorage storage s = _getOrderPipelineStorage();
        return s.nextOrderToSettle;
    }

    /**
     * @notice Retrieves the total settled amount for the current period.
     * @return The total settled amount.
     */
    function getTotalSettledAmount() public view returns (uint256) {
        OrderPipelineStorage storage s = _getOrderPipelineStorage();
        return s.settledAmount;
    }

    /**
     * @notice Retrieves the list of all active order IDs.
     * @return An array containing all active order IDs.
     */
    function getActiveOrderIds() public view returns (uint256[] memory) {
        OrderPipelineStorage storage s = _getOrderPipelineStorage();
        return s.ordersList.getNodesId();
    }

    /**
     * @notice Retrieves the settlement token address used for orders.
     * @return The address of the settlement token.
     */
    function getSettlementTokenAddress() public view returns (address) {
        OrderPipelineStorage storage s = _getOrderPipelineStorage();
        return address(s.settlementToken);
    }

    /**
     * @notice Retrieves the current instrument ID.
     * @return The ID of the instrument associated with the pipeline.
     */
    function getInstrumentId() public view returns (bytes32) {
        OrderPipelineStorage storage s = _getOrderPipelineStorage();
        return s.instrumentId;
    }

    /**
     * @notice Creates a new order and transfers tokens.
     * @dev Stores order details, validates token transfer, and emits an `OrderCreated` event.
     * @param _ctx The context of the order pipeline.
     * @param _ops The Order Pipeline Storage.
     * @param _investorId Investor's unique identifier.
     * @param _amount Amount of tokens to transfer.
     * @param _beneficiaryWallet Address receiving the settlement.
     * @param _sender Address initiating the order.
     * @param _orderId Unique identifier of the order.
     * @param _operation The operation to be tracked.
     * @param _token The token to be transferred(settlement token for subscription
     * and security token for redemption book)
     */
    function _createOrder(
        Context memory _ctx,
        OrderPipelineStorage storage _ops,
        bytes32 _investorId,
        uint256 _amount,
        address _beneficiaryWallet,
        address _sender,
        uint256 _orderId,
        OPERATIONS _operation,
        IERC20Upgradeable _token
    ) internal {
        bytes memory result = _ctx.operationsEngine.check(
            _operation,
            _ctx.instrumentId,
            abi.encode(block.timestamp, uint256(_investorId), block.timestamp, 0, 0) //default execution is current time
        );
        (uint256 executionTimestamp, string memory errorMsg) = abi.decode(result, (uint256, string));
        require(executionTimestamp != 0, errorMsg);

        _ops.orders[_orderId] = OrderDetails({
            amount: SafeCast.toUint128(_amount),
            beneficiary: _beneficiaryWallet,
            investorId: _investorId,
            sender: _sender,
            timestamp: uint32(block.timestamp),
            executionTimestamp: uint32(executionTimestamp),
            settledAmount: 0
        });

        uint256 beforeTransferBalance = _token.balanceOf(address(this));
        _token.safeTransferFrom(_sender, address(this), _amount);

        if (_token.balanceOf(address(this)) != beforeTransferBalance + _amount) {
            revert IOrderPipeline_InsufficientAmount();
        }

        emit OrderCreated(_orderId, _investorId, _amount, _sender);
    }

    /**
     * @notice Pre-checks and processes order creation.
     * @param _ctx The context of the order pipeline.
     * @param _ops The Order Pipeline Storage.
     * @param _amount The amount of the order.
     * @param _investorId The ID of the investor.
     * @param _investorWallet The wallet address of the investor.
     * @param _beneficiaryWallet The beneficiary address for the order.
     * @param _isFromDealer Whether the order is from a dealer.
     * @return sender The address of the sender.
     * @return orderId The ID of the created order.
     */
    function _beforeOrderCheck(
        Context memory _ctx,
        OrderPipelineStorage storage _ops,
        uint256 _amount,
        bytes32 _investorId,
        address _investorWallet,
        address _beneficiaryWallet,
        bool _isFromDealer
    ) internal virtual returns (address sender, uint256 orderId) {
        if (_amount == 0) {
            revert IOrderPipeline_InsufficientAmount();
        }

        if (_ops.instrumentRegistry.getBool(_ops.instrumentId, INSTRUMENT_CLOSED)) {
            revert IOrderPipeline_InstrumentClosed();
        }

        if (_isFromDealer) {
            _checkCorrectDealer(_ops, _investorId);
            if (_ctx.investorRegistry.getIdFromWallet(_investorWallet) != _investorId) {
                revert IOrderPipeline_WrongInvestor();
            }
            sender = msg.sender; // sender is the dealer
        } else {
            _checkCorrectInvestor(_ctx, _investorId);
            sender = _investorWallet; // sender is the investor
        }

        if (_investorId != _ctx.investorRegistry.getIdFromWallet(_beneficiaryWallet)) {
            revert IOrderPipeline_BeneficiaryIsNotInvestor();
        }

        orderId = ++_ops.ordersCount;
        _ops.ordersList.insert(orderId);

        return (sender, orderId);
    }

    /**
     * @notice Executes post-order creation actions.
     * @dev This hook is triggered after an order has passed all pre-checks and been created.
     * @param _ctx The context of the order pipeline.
     * @param _ops The Order Pipeline Storage.
     * @param _orderId The ID of the newly created order.
     */
    function _afterOrderCheck(Context memory _ctx, OrderPipelineStorage storage _ops, uint256 _orderId) internal {
        if (_ctx.instrumentRegistry.getBool(_ctx.instrumentId, INSTANT_SETTLEMENT)) {
            _instantSettlement(_ctx, _ops, _orderId);
        }
    }

    /**
     * @notice Instant settlement of an order when the all the 4 phases are executed in the same transaction.
     * @param _ctx The context of the order pipeline.
     * @param _ops The Order Pipeline Storage.
     * @param _orderId Id of the order to settle
     */
    function _instantSettlement(Context memory _ctx, OrderPipelineStorage storage _ops, uint256 _orderId)
        internal
        virtual;

    /**
     * @notice Processes the cancellation of an order.
     * @param _ctx The context of the order pipeline.
     * @param _ops The Order Pipeline Storage.
     * @param _orderId The ID of the order to cancel.
     * @param _amount The cancelled order amount .
     */
    function _afterCancelOrderCheck(
        Context memory _ctx,
        OrderPipelineStorage storage _ops,
        uint256 _orderId,
        OrderDetails memory _order,
        uint256 _amount
    ) internal virtual {
        if (_amount != 0) {
            _sendReceivedToken(_ctx, _order.sender, _amount);
        }

        if (_ops.nextOrderToSettle == _orderId) {
            _ops.nextOrderToSettle = _ops.ordersList.getNext(_orderId);
        }

        emit OrderCancelled(_orderId, _order.amount);
    }

    /**
     * @notice Updates an order's status and storage after settlement or cancellation.
     * @param _ops The order pipeline storage.
     * @param _orderId The ID of the order being updated.
     * @param _isFullSettlement Whether the order was fully settled.
     */
    function _updateOrder(OrderPipelineStorage storage _ops, uint256 _orderId, bool _isFullSettlement) internal {
        if (_isFullSettlement) {
            _ops.ordersList.erase(_orderId);
            delete _ops.orders[_orderId];
        }
    }

    /**
     * @notice Hook for custom settlement updates.
     * @dev Updates the settlement period and resets state variables when a new period starts.
     * @param _ctx The context of the order pipeline.
     * @param _ops The Order Pipeline Storage.
     * @param _isSubscription Whether the order is a subscription (true) or redemption (false)
     * This ensures that all orders are settled correctly for the current period.
     */
    function _beforeSettlementUpdates(Context memory _ctx, OrderPipelineStorage storage _ops, bool _isSubscription)
        internal
    {
        (uint256 period,,) = _isSubscription
            ? _ctx.instrumentRegistry.currentSubscriptionPeriod(_ctx.instrumentId)
            : _ctx.instrumentRegistry.currentRedemptionPeriod(_ctx.instrumentId);

        if (period < _ops.currentPeriod) {
            revert IOrderPipeline_PeriodDecreasing();
        }

        if (period != 0 && _ops.currentPeriod != period) {
            _ops.currentPeriod = period;
            _ops.settledAmount = 0;
            _ops.nextOrderToSettle = 0;
        }
    }

    /**
     * @notice Sends tokens to the receiver.
     * @param _ctx The context of the order pipeline.
     * @param _receiver The address of the receiver.
     * @param _amount The amount of tokens to send.
     */
    function _sendReceivedToken(Context memory _ctx, address _receiver, uint256 _amount) internal virtual;

    /**
     * @notice Prepares data for order settlement.
     * @param _ctx The context of the order pipeline.
     * @param _ops The Order Pipeline Storage.
     * @param _percentageToSettle The percentage of the order to settle.
     * @param _lastOrderId The ID of the last order in the settlement range.
     * @param _isSubscription If the settlement is for subscriptions or not
     * @return allOrders The list of orders to settle.
     */
    function _beforeSettleOrders(
        Context memory _ctx,
        OrderPipelineStorage storage _ops,
        uint256 _percentageToSettle,
        uint256 _lastOrderId,
        bool _isSubscription
    ) internal virtual returns (uint256[] memory allOrders) {
        if (_percentageToSettle == 0 || _percentageToSettle > PERCENTAGE_BASE) {
            revert IOrderPipeline_PercentageNotApt();
        }

        _beforeSettlementUpdates(_ctx, _ops, _isSubscription);

        if (_lastOrderId < _ops.nextOrderToSettle) {
            revert IOrderPipeline_OrderAlreadySettledInCurrentRound();
        }

        allOrders = _ops.ordersList.getNodesIdInRange(
            _ops.nextOrderToSettle == 0 ? _ops.ordersList.begin : _ops.nextOrderToSettle, _lastOrderId
        );
    }

    /**
     * @notice Ensures that the caller is one of the wallets of the investor.
     * @param _ctx The context of the order pipeline.
     * @param _investor Id of the investor to verify
     */
    function _checkCorrectInvestor(Context memory _ctx, bytes32 _investor) internal view {
        bytes32 investorId = _ctx.investorRegistry.getIdFromWallet(msg.sender);
        if (investorId == bytes32(0) || _investor != investorId) {
            revert IOrderPipeline_Unauthorized();
        }
    }

    /**
     * @notice Ensures that the caller is an authorized dealer for the investor.
     * @param _ops The Order Pipeline Storage.
     * @param _investorId The ID of the investor for which the dealer is verified.
     */
    function _checkCorrectDealer(OrderPipelineStorage storage _ops, bytes32 _investorId) internal view {
        bytes32 dealerId = _ops.dealerRegistry.getIdFromWallet(msg.sender);
        if (dealerId == bytes32(0) || _ops.investorRegistry.getDealer(_investorId) != dealerId) {
            revert IOrderPipeline_Unauthorized();
        }
    }

    /**
     * @notice Checks if the instrument is not paused.
     */
    function _checkInstrumentNotPaused(IInstrumentRegistry _instrumentRegistry, bytes32 _instrumentId) internal view {
        if (_instrumentRegistry.getBool(_instrumentId, INSTRUMENT_PAUSED)) {
            revert IOrderPipeline_InstrumentPaused();
        }
    }

    function _getContext(OrderPipelineStorage storage _ops) internal view returns (Context memory ctx) {
        ctx = Context({
            instrumentId: _ops.instrumentId,
            operationsEngine: _ops.operationsEngine,
            instrumentRegistry: _ops.instrumentRegistry,
            investorRegistry: _ops.investorRegistry,
            settlementToken: _ops.settlementToken,
            securityToken: _ops.securityToken
        });
    }

    /**
     * @dev Retrieves the storage structure for the contract.
     * @return store The storage object for the order pipeline.
     */
    function _getOrderPipelineStorage() internal pure returns (OrderPipelineStorage storage store) {
        assembly {
            store.slot := ORDER_PIPELINE_STORAGE_SLOT
        }
    }
}

