import "../interfaces/ConduitInterFace.sol";
import "../interfaces/IERC721TL.sol";
import "../interfaces/IERC1155TL.sol";

contract ConduitImpl is
    Initializable,
    ConduitInterFace,
    OwnableUpgradeable,
    PausableUpgradeable
{
    using AddressUpgradeable for address;
    address internal constant ADDRESS_ZERO = address(0);

    // Only this address is authorized to call conduit contract functions
    address public transferHelperContractAddress;

    // This event emits when user update the transfer helper address
    event UpdateTransferHelperEvent(address indexed updatedAddress);

    /**
     * @dev Constructor function to initialize the ConduitImp contract.
     * @param addressTransferHelperContract The address of the TransferHelper contract.
     * Requirements:
     * - The transferHelperContractAddress must not be the zero address.
     * - The transferHelperContractAddress is the address of the TransferHelper contract
     *   which provides helper functions for transferring tokens.
     */
    function initialize(
        address addressTransferHelperContract
    ) external initializer {
        require(
            addressTransferHelperContract != ADDRESS_ZERO,
            "ConduitImp: Address should not be equal to zero address"
        );
        __Ownable_init();
        __Pausable_init();
        transferHelperContractAddress = addressTransferHelperContract;
    }

    /**
     * @dev Transfers an ERC721 token from one address to another.
     * @param from The address from which the token is being transferred.
     * @param to The address to which the token is being transferred.
     * @param tokenId The ID of the token being transferred.
     * @param contractAddress The address of the ERC721 contract.
     * Requirements:
     * - Only the helper contract is allowed to call this function.
     * - The contract must not be paused.
     */
    function transferERC721(
        address from,
        address to,
        uint256 tokenId,
        address contractAddress
    ) external override onlyHelperContract whenNotPaused {
        IERC721Upgradeable(contractAddress).transferFrom(from, to, tokenId);
    }

    /**
     * @dev Transfers ERC1155 tokens from one address to another.
     * @param quantity The quantity of tokens being transferred.
     * @param from The address from which the tokens are being transferred.
     * @param to The address to which the tokens are being transferred.
     * @param tokenId The ID of the token being transferred.
     * @param contractAddress The address of the ERC1155 contract.
     * Requirements:
     * - Only the helper contract is allowed to call this function.
     * - The contract must not be paused.
     */
    function transferERC1155(
        uint256 quantity,
        address from,
        address to,
        uint256 tokenId,
        address contractAddress
    ) external override onlyHelperContract whenNotPaused {
        IERC1155Upgradeable(contractAddress).safeTransferFrom(
            from,
            to,
            tokenId,
            quantity,
            ""
        );
    }

    function transferLazyERC721(
        address contractAddress,
        address to,
        uint256 tokenId,
        string calldata uri
    ) external override onlyHelperContract whenNotPaused {
        IERC721TL(contractAddress).lazyMint(to, tokenId, uri);
    }

    function transferLazyERC1155(
        address contractAddress,
        address to,
        uint256 tokenId,
        uint256 quantity,
        string calldata uri
    ) external override onlyHelperContract whenNotPaused {
        IERC1155TL(contractAddress).lazyMint(
            to,
            tokenId,
            quantity,
            uri
        );
    }

    /**
     * @dev Updates the address of the TransferHelper contract.
     * @param newAddress The new address of the TransferHelper contract.
     * Requirements:
     * - Only the owner is allowed to call this function.
     * - The contract must not be paused.
     * - The new address should not be equal to the previous address.
     * - The new address should not be the zero address.
     */
    function updateTransferHelper(
        address newAddress
    ) external override onlyOwner whenNotPaused {
        require(
            transferHelperContractAddress != newAddress,
            "ConduitImp: New address should not be equal to the previous address"
        );
        require(
            newAddress != ADDRESS_ZERO,
            "ConduitImp: New address should not be equal to zero address"
        );
        transferHelperContractAddress = newAddress;
        emit UpdateTransferHelperEvent(transferHelperContractAddress);
    }

    /**
     * @dev Withdraws the contract's balance to the owner.
     * Requirements:
     * - Only the owner is allowed to call this function.
     * - The contract must not be paused.
     * - The contract must have a positive balance.
     */
    // function withdraw() external onlyOwner override whenNotPaused {
    //     uint256 balance = address(this).balance;
    //     require(balance > 0, "No Ether to withdraw");

    //     // Transfer the balance to the contract owner
    //     payable(msg.sender).transfer(balance);
    // }

    modifier onlyHelperContract() {
        require(
            msg.sender == transferHelperContractAddress,
            "ConduitImp : Only transfer helper contract can call this function"
        );
        _;
    }
}

