pragma solidity ^0.8.7;

interface ConduitInterFace { 

    function transferERC721(address _from, address _to, uint256 _tokenId, address _contractAddress) external;

    function transferERC1155(uint256 _quantity, address _from, address _to, uint256 _tokenId, address _contractAddress) external;

    function updateTransferHelper(address _newAddress) external;

    function transferLazyERC721(address contractAddress, address to, uint256 tokenId, string calldata uri) external;

    function transferLazyERC1155(address contractAddress, address to, uint256 tokenId, uint256 quantity, string calldata uri) external;
}

