contracts/ethereum/UsdVault/Logic.sol.content:
// SPDX-License-Identifier: GPL-3.0-only
pragma solidity ^0.8.24;

import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "../../vaults/FundBaseVault.sol";
import "hardhat/console.sol";

contract UsdVaultLogic is Initializable, UUPSUpgradeable, OwnableUpgradeable, FundBaseVault {
    using SafeERC20 for IERC20;

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        // 确保实现合约本身无法被初始化，只有通过代理合约才能进行初始化
        _disableInitializers();
    }

    function initialize(address initialOwner) public initializer {
        // 设置owner
        __Ownable_init(initialOwner);
    }

    // 授权升级 只有owner可以升级
    function _authorizeUpgrade(address newImplementation) internal override onlyOwner {}

    address private constant crvusdAddress = address(0xf939E0A03FB07F59A73314E73794Be0E57ac1b4E);
    address public defaultCvcrvusdtAddress = address(0x4a7999c55d3a93dAf72EA112985e57c2E3b9e95D);
    address public defaultSdcvcrvusdAddress = address(0x64DAa88D2377706C5953F6bC606684F2037f0f78);

    address private constant scrvusdAddress = address(0x0655977FEb2f289A4aB78af67BAB0d17aAb84367);
    address private constant reusdAddress = address(0x57aB1E0003F623289CD798B1824Be09a793e4Bec);
    address private constant poolAddress = address(0xc522A6606BBA746d7960404F22a3DB936B6F4F50);

    address private constant convexBoostAddress =
        address(0xF403C135812408BFbE8713b5A23a04b3D48AAE31);
    address private constant convexRewardAddress =
        address(0x7Fafc1876970dBD9F6568586EFa7d0FAc0FE8EA8);

    address private constant stakerDaoProxyAddress =
        address(0x539e65190a371cE73244A98DEc42BA635cCa512c);

    event CrvusdToReusd(uint256 crvusdAmount, uint256 reusdAmount);
    event ReusdToCrvusd(uint256 reusdAmount, uint256 crvusdAmount);
    event ClaimRewards(uint256 crvusdAmount, uint256 cvxAmount, uint256 crvAmount);

    /**
     * 默认地址设置
     */
    function setDefaultAddress(
        address cvcrvusdtAddress,
        address sdcvcrvusdAddress
    ) external onlyOwnerCan {
        defaultCvcrvusdtAddress = cvcrvusdtAddress;
        defaultSdcvcrvusdAddress = sdcvcrvusdAddress;
    }

    /**
     * crvusd -> reusd/scrvusd pool
     */
    function crvusdToReusd(
        uint256 crvusdAmount,
        uint256 minMintAmount,
        address cvcrvusdtAddress,
        address sdcvcrvusdAddress
    ) external onlyOperatorCan {
        //  minMintAmount 判断，不能太小
        require(
            minMintAmount >= (crvusdAmount * 99) / 100,
            "lpAmount must bigger than crvusdAmount"
        );

        // crvusd 提现
        withdrawCrvusd(crvusdAmount, cvcrvusdtAddress, sdcvcrvusdAddress);
        // crvusd deposit 成 scrvusd
        uint256 scrvusdAmount = IScrvusd(scrvusdAddress).deposit(crvusdAmount, address(this));
        IERC20(scrvusdAddress).approve(poolAddress, scrvusdAmount);

        // scrvusd 添加到池子
        uint256[] memory _amounts = new uint256[](2);
        _amounts[0] = 0;
        _amounts[1] = scrvusdAmount;
        uint256 lpAmount = ICurvePool(poolAddress).add_liquidity(_amounts, minMintAmount); // 添加流动性，获得LP代币

        // 池子 boost
        IConvexBoost(convexBoostAddress).depositAll(440, true); // 将获得的 LP 代币存入 Convex 协议以获得额外收益
        emit CrvusdToReusd(crvusdAmount, lpAmount);
    }

    /**
     * reusd/scrvusd pool -> crvusd
     */
    function reusdToCrvusd(
        uint256 lpAmount,
        uint256 minScrvusdAmount,
        address cvcrvusdtAddress,
        address sdcvcrvusdAddress
    ) external onlyOperatorCan {
        // 池子取消质押
        IConvexReward(convexRewardAddress).withdrawAndUnwrap(lpAmount, false);

        // 池子移除流动性
        uint256 scrvusdAmount = ICurvePool(poolAddress).remove_liquidity_one_coin(
            lpAmount,
            1,
            minScrvusdAmount
        );

        // scrvusd -> crvusd
        uint256 crvusdAmount = IScrvusd(scrvusdAddress).redeem(
            scrvusdAmount,
            address(this),
            address(this)
        );

        // 滑点控制
        require(crvusdAmount > (lpAmount * 95) / 100, "crvusdAmount must bigger than lpAmount");

        // crvusd 存入
        //depositCrvusd(crvusdAmount, cvcrvusdtAddress, sdcvcrvusdAddress);
        emit ReusdToCrvusd(lpAmount, crvusdAmount);
    }

    function depositCrvusd(
        uint256 crvusdAmount,
        address cvcrvusdtAddress,
        address sdcvcrvusdAddress
    ) public {
        require(
            IERC20(crvusdAddress).balanceOf(address(this)) >= crvusdAmount,
            "Insufficient contract balance"
        );

        IERC20(crvusdAddress).approve(cvcrvusdtAddress, crvusdAmount);

        uint256 cvcrvusdAmount = ILendVault(cvcrvusdtAddress).deposit(crvusdAmount, address(this));

        IERC20(cvcrvusdtAddress).approve(sdcvcrvusdAddress, cvcrvusdAmount);

        IBoostVault(sdcvcrvusdAddress).deposit(address(this), cvcrvusdAmount, true);
    }

    function withdrawCrvusd(
        uint256 crvusdAmount,
        address cvcrvusdtAddress,
        address sdcvcrvusdAddress
    ) public {
        uint256 cvCrvusdPrice = ILendVault(cvcrvusdtAddress).pricePerShare();

        uint256 sdcrvusdAmount = (crvusdAmount * 1e18) / cvCrvusdPrice;

        // 授权
        IBoostVault(sdcvcrvusdAddress).withdraw(sdcrvusdAmount);

        uint256 cvcrvusdAmount = IERC20(cvcrvusdtAddress).balanceOf(address(this));

        ILendVault(cvcrvusdtAddress).redeem(cvcrvusdAmount, address(this), address(this));
    }

    /**
     * approve all contract
     */
    function approveAll() external onlyOperatorCan {
        IERC20(crvusdAddress).approve(defaultCvcrvusdtAddress, type(uint256).max);
        IERC20(defaultCvcrvusdtAddress).approve(defaultSdcvcrvusdAddress, type(uint256).max);
        IERC20(scrvusdAddress).approve(poolAddress, type(uint256).max);
        IERC20(reusdAddress).approve(poolAddress, type(uint256).max);
        IERC20(poolAddress).approve(convexBoostAddress, type(uint256).max);
        IERC20(poolAddress).approve(convexRewardAddress, type(uint256).max);
    }

    function approve(address token, address spender) external onlyOwnerCan {
        IERC20(token).approve(spender, type(uint256).max);
    }

    // Convex 网站领取奖励
    function convexClaim(address _account, bool _claimExtras) public onlyOperatorCan {
        // 通过接口调用convex合约的getReward方法
        bool result = IConvexReward(convexRewardAddress).getReward(_account, _claimExtras);
        require(result, "Convex getReward returned false"); // 检查调用是否成功
    }

    // StakerDao 网站领取奖励
    function stakerDaoClaim(address[] calldata _gauges, bool _claimVeSDT) public onlyOperatorCan {
        // 通过接口调用代理合约地址的claimRewards方法
        IStakerDao(stakerDaoProxyAddress).claimRewards(_gauges, _claimVeSDT);
    }

    /**
     * 奖励代币交换为crvUSD
     * 将Convex和StakerDao奖励(CRV, CVX) 转换为 crvUSD
     * 在cvxETH池子中wETH的索引是0，CVX的索引是1
     * 在TriCRV池中crvUSD的索引是0，WETH的索引是1，CRV的索引是2
     */
    function claimAndSwapRewards(
        uint256 minCrvusdAmount,
        address cvcrvusdtAddress,
        address sdcvcrvusdAddress
    ) external onlyOperatorCan {
        // 池子地址
        address triCrvPoolAddress = 0x4eBdF703948ddCEA3B11f675B4D1Fba9d2414A14; // TriCRV池地址
        address cvxEthPoolAddress = 0xB576491F1E6e5E62f1d8F26062Ee822B40B0E0d4; // cvxeth池地址

        // 代币地址
        address crvAddress = 0xD533a949740bb3306d119CC777fa900bA034cd52; // CRV 代币地址
        address cvxAddress = 0x4e3FBD56CD56c3e72c1403e103b45Db9da5B9D2B; // CVX 代币地址
        address wethAddress = 0xC02aaA39b223FE8D0A0e5C4F27eAD9083C756Cc2; // WETH 地址
        // 检查并交换CRV
        uint256 crvBalance = IERC20(crvAddress).balanceOf(address(this));
        if (crvBalance > 0) {
            // 使用TriCRV池直接将CRV交换为crvUSD (CRV的索引是2，crvUSD的索引是0)
            IERC20(crvAddress).approve(triCrvPoolAddress, crvBalance);
            ICurvePool(triCrvPoolAddress).exchange(2, 0, crvBalance, 0);
        }
        // 检查并交换CVX
        uint256 cvxBalance = IERC20(cvxAddress).balanceOf(address(this));
        if (cvxBalance > 0) {
            // 第一步：CVX->WETH，使用cvxEthPool池（CVX的索引是1，WETH的索引是0）
            IERC20(cvxAddress).approve(cvxEthPoolAddress, cvxBalance);

            // 执行CVX到WETH的交换
            uint256 wethAmount = ICurvePool(cvxEthPoolAddress).exchange(1, 0, cvxBalance, 0);

            // 第二步：WETH->crvUSD，使用TriCRV池（WETH的索引是1，crvUSD的索引是0）
            IERC20(wethAddress).approve(triCrvPoolAddress, wethAmount);
            ICurvePool(triCrvPoolAddress).exchange(1, 0, wethAmount, 0);
        }
        require(
            IERC20(crvusdAddress).balanceOf(address(this)) >= minCrvusdAmount,
            "Insufficient contract balance"
        );

        // 将获得的crvUSD存入Vault
        uint256 crvusdBalance = IERC20(crvusdAddress).balanceOf(address(this));
        if (crvusdBalance > 0) {
            depositCrvusd(crvusdBalance, cvcrvusdtAddress, sdcvcrvusdAddress);
        }
        emit ClaimRewards(crvusdBalance, cvxBalance, crvBalance);
    }
}

/**
 * stakerdao boost
 */
interface IBoostVault {
    function deposit(address _staker, uint256 _amount, bool _doEarn) external;

    function withdraw(uint256 amount) external;
}

/**
 * crvusd lend
 */
interface ILendVault {
    function deposit(uint256 assets, address receiver) external returns (uint256);

    function redeem(uint256 shares, address receiver, address owner) external returns (uint256);

    function pricePerShare() external view returns (uint256);
}

/**
 * Curve Pool
 */
interface ICurvePool {
    // 添加流动性
    function add_liquidity(
        uint256[] calldata _amounts,
        uint256 _min_mint_amount
    ) external returns (uint256);

    // 移除流动性
    function remove_liquidity(
        uint256 _burn_amount,
        uint256[] calldata _min_amounts
    ) external returns (uint256[] memory);

    // 移除流动性变成一个币
    function remove_liquidity_one_coin(
        uint256 _burn_amount,
        int128 i,
        uint256 _min_received
    ) external returns (uint256);

    function exchange(
        uint256 i,
        uint256 j,
        uint256 dx,
        uint256 min_dy
    ) external payable returns (uint256);
}

/**
 * scrvusd
 */
interface IScrvusd {
    function deposit(uint256 assets, address receiver) external returns (uint256);

    function mint(uint256 shares, address receiver) external returns (uint256);

    function withdraw(uint256 assets, address receiver, address owner) external returns (uint256);

    function redeem(uint256 shares, address receiver, address owner) external returns (uint256);

    function convertToShares(uint256 assets) external view returns (uint256);

    function convertToAssets(uint256 shares) external view returns (uint256);
}

/**
 * Convex Boost
 */
interface IConvexBoost {
    function depositAll(uint256 _pid, bool _stake) external returns (bool);
}

/**
 * Convex Reward Pool
 */
interface IConvexReward {
    function withdrawAndUnwrap(uint256 amount, bool claim) external returns (bool);

    // 添加getReward接口方法
    function getReward(address _account, bool _claimExtras) external returns (bool);
}

// StakerDao 接口定义
interface IStakerDao {
    function claimRewards(address[] calldata _gauges, bool _claimVeSDT) external;
}

