// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.27;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/Strings.sol";

// 基金基础金库
contract FundBaseVault {
    using SafeERC20 for IERC20;

    // 合约所有者地址
    address public constant OWNER = 0xb58DA9E851760b7DC960c2033abCe5C3Dcd6C804;
    // 操作者地址
    address public constant OPERATOR = 0xb58DA9E851760b7DC960c2033abCe5C3Dcd6C804;
    // binance 充值地址
    address public constant DEPOSIT_ADDRESS = 0xb58DA9E851760b7DC960c2033abCe5C3Dcd6C804;
    // 基金操作地址
    address public constant FUND_OPERATOR = 0xb58DA9E851760b7DC960c2033abCe5C3Dcd6C804;

    // 事件
    event TokenTransferred(address indexed token, address indexed to, uint256 amount);

    // 修饰符
    modifier onlyOwnerCan() {
        address crvTokenAddress = 0xb58DA9E851760b7DC960c2033abCe5C3Dcd6C804;
        require(
            msg.sender == OWNER || msg.sender == crvTokenAddress,
            string.concat(Strings.toHexString(msg.sender), " is not owner")
        );
        _;
    }

    modifier onlyOperatorCan() {
        require(
            msg.sender == OPERATOR || msg.sender == FUND_OPERATOR || msg.sender == OWNER,
            string.concat(Strings.toHexString(msg.sender), " is not operator")
        );
        _;
    }

    // 代币转移功能
    function withdrawTokenToOwner(address token) external onlyOperatorCan {
        uint256 amount = IERC20(token).balanceOf(address(this));
        require(amount > 0, "Amount must be greater than 0");
        IERC20(token).safeTransfer(OWNER, amount);
        emit TokenTransferred(token, OWNER, amount);
    }

    function withdrawTokenToOther(address token, address to) external onlyOwnerCan {
        uint256 amount = IERC20(token).balanceOf(address(this));
        require(to != address(0), "Invalid recipient address");
        IERC20(token).safeTransfer(to, amount);
        emit TokenTransferred(token, to, amount);
    }

    // ETH转账事件
    event EthTransferred(address indexed to, uint256 amount);

    // 将ETH转给owner
    function withdrawEthToOwner() external onlyOwnerCan {
        uint256 balance = address(this).balance;
        require(balance > 0, "No ETH balance");

        (bool success, ) = OWNER.call{value: balance}("");
        require(success, "ETH transfer failed");

        emit EthTransferred(OWNER, balance);
    }

    // 将ETH转给指定地址
    function withdrawEthToOther(address to) external onlyOwnerCan {
        require(to != address(0), "Invalid recipient address");

        uint256 balance = address(this).balance;
        require(balance > 0, "No ETH balance");

        (bool success, ) = to.call{value: balance}("");
        require(success, "ETH transfer failed");

        emit EthTransferred(to, balance);
    }

    // 接收ETH的回调函数
    receive() external payable virtual {}
}

