contracts/utils/CommitteeUpgradeable.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/utils/ReentrancyGuardUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import "../interfaces/IBridgeCommittee.sol";
import "./MessageVerifier.sol";

/// @title CommitteeUpgradeable
/// @notice This contract enables message signature verification using a BridgeCommittee contract,
/// in addition to providing an interface for upgradeability via signed message verification.
/// @dev The contract is intended to be inherited by contracts that require message verification and
/// upgradeability.
abstract contract CommitteeUpgradeable is
    UUPSUpgradeable,
    MessageVerifier,
    ReentrancyGuardUpgradeable
{
    /* ========== STATE VARIABLES ========== */

    bool private _upgradeAuthorized;
    // upgradeablity storage gap
    uint256[50] private __gap;

    /* ========== INITIALIZER ========== */

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    function __CommitteeUpgradeable_init(address _committee) internal onlyInitializing {
        __ReentrancyGuard_init();
        __MessageVerifier_init(_committee);
        committee = IBridgeCommittee(_committee);
    }

    /* ========== EXTERNAL FUNCTIONS ========== */

    /// @notice Enables the upgrade of the inheriting contract by verifying the provided signatures.
    /// @dev The function will revert if the provided signatures or message is invalid.
    /// @param signatures The array of signatures to be verified.
    /// @param message The BridgeUtils to be verified.
    function upgradeWithSignatures(bytes[] memory signatures, BridgeUtils.Message memory message)
        external
        nonReentrant
        verifyMessageAndSignatures(message, signatures, BridgeUtils.UPGRADE)
    {
        // decode the upgrade payload
        (address proxy, address implementation, bytes memory callData) =
            BridgeUtils.decodeUpgradePayload(message.payload);

        // verify proxy address
        require(proxy == address(this), "CommitteeUpgradeable: Invalid proxy address");

        // authorize upgrade
        _upgradeAuthorized = true;
        // upgrade contract
        upgradeToAndCall(implementation, callData);

        emit ContractUpgraded(message.nonce, proxy, implementation);
    }

    /* ========== INTERNAL FUNCTIONS ========== */

    /// @notice Authorizes the upgrade of the inheriting contract.
    /// @dev The _upgradeAuthorized state variable can only be set with the upgradeWithSignatures
    /// function, meaning that the upgrade can only be authorized by the committee.
    function _authorizeUpgrade(address) internal override {
        require(_upgradeAuthorized, "CommitteeUpgradeable: Unauthorized upgrade");
        _upgradeAuthorized = false;
    }

    /// @notice Event emitted when the contract is upgraded
    /// @param nonce The nonce of the upgrade message.
    /// @param proxy The address of the proxy contract.
    /// @param implementation The address of the new implementation.
    event ContractUpgraded(uint256 nonce, address proxy, address implementation);
}

contracts/interfaces/IBridgeConfig.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

/// @title IBridgeConfig
/// @dev Interface for the BridgeConfig contract.
interface IBridgeConfig {
    /* ========== STRUCTS ========== */

    /// @notice The data struct for the supported bridge tokens.
    struct Token {
        address tokenAddress;
        uint8 suiDecimal;
        bool native;
    }

    /* ========== VIEW FUNCTIONS ========== */

    /// @notice Returns the address of the token with the given ID.
    /// @param tokenID The ID of the token.
    /// @return address of the provided token.
    function tokenAddressOf(uint64 tokenID) external view returns (address);

    /// @notice Returns the sui decimal places of the token with the given ID.
    /// @param tokenID The ID of the token.
    /// @return amount of sui decimal places of the provided token.
    function tokenSuiDecimalOf(uint64 tokenID) external view returns (uint8);

    /// @notice Returns the price of the token with the given ID.
    /// @param tokenID The ID of the token.
    /// @return price of the provided token.
    function tokenPriceOf(uint64 tokenID) external view returns (uint64);

    /// @notice Returns the supported status of the token with the given ID.
    /// @param tokenID The ID of the token.
    /// @return true if the token is supported, false otherwise.
    function isTokenSupported(uint64 tokenID) external view returns (bool);

    /// @notice Returns whether a chain is supported in SuiBridge with the given ID.
    /// @param chainId The ID of the chain.
    /// @return true if the chain is supported, false otherwise.
    function isChainSupported(uint8 chainId) external view returns (bool);

    /// @notice Returns the chain ID of the bridge.
    function chainID() external view returns (uint8);

    /// @notice Event for the addition of a new token.
    /// @param nonce The governance action nonce.
    /// @param tokenIDs The IDs of the tokens added.
    /// @param tokenAddresses The addresses of the tokens added.
    /// @param suiDecimals The added token's decimal places on Sui.
    /// @param tokenPrices The prices of the tokens added in USD.
    event TokensAddedV2(
        uint64 nonce,
        uint64[] tokenIDs,
        address[] tokenAddresses,
        uint8[] suiDecimals,
        uint64[] tokenPrices
    );

    /// @dev (deprecated in favor of TokensAddedV2)
    event TokenAdded(uint64 tokenID, address tokenAddress, uint8 suiDecimal, uint64 tokenPrice);

    /// @notice Event for the price update of a token.
    /// @param nonce The governance action nonce.
    /// @param tokenID The ID of the token updated.
    /// @param tokenPrice The new price of the token in USD.
    event TokenPriceUpdatedV2(uint64 nonce, uint64 tokenID, uint64 tokenPrice);

    /// @dev (deprecated in favor of TokenPriceUpdatedV2)
    event TokenPriceUpdated(uint64 tokenID, uint64 tokenPrice);
}

