// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import { SafeTransferLib } from "solady/utils/SafeTransferLib.sol";

contract AssetPool is AccessControlUpgradeable {
  // 定义提取资金角色的常量
  bytes32 public constant WITHDRAW_ROLE = keccak256("WITHDRAW_ROLE");

  string public name;

  // 接收以太币的回退函数
  receive() external payable {}

  function initialize(string memory _name) public initializer {
    __AccessControl_init();

    // 将部署者设置为默认管理员
    _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);

    // 可选：将部署者赋予提取资金的角色
    // _grantRole(WITHDRAW_ROLE, msg.sender);

    name = _name;
  }

  // 提取指定数量的以太币到目标地址，仅限被授予WITHDRAW_ROLE的地址调用
  function withdrawEther(address payable _to, uint256 _amount) external onlyRole(WITHDRAW_ROLE) {
    require(address(this).balance >= _amount, "Not enough ETH");
    SafeTransferLib.safeTransferETH(_to, _amount);
  }

  // 提取指定数量的 ERC20 代币到目标地址，仅限被授予WITHDRAW_ROLE的地址调用
  function withdrawToken(address _tokenAddress, address _to, uint256 _amount) external onlyRole(WITHDRAW_ROLE) {
    require(SafeTransferLib.balanceOf(_tokenAddress, address(this)) >= _amount, "Not enough ERC20 tokens");
    SafeTransferLib.safeTransfer(_tokenAddress, _to, _amount);
  }

  // 允许管理员将WITHDRAW_ROLE授予其他地址
  function grantWithdrawRole(address _account) external onlyRole(DEFAULT_ADMIN_ROLE) {
    grantRole(WITHDRAW_ROLE, _account);
  }

  // 允许管理员撤销其他地址的WITHDRAW_ROLE
  function revokeWithdrawRole(address _account) external onlyRole(DEFAULT_ADMIN_ROLE) {
    revokeRole(WITHDRAW_ROLE, _account);
  }

  function changeAdmin(address newAdmin) public onlyRole(DEFAULT_ADMIN_ROLE) {
    require(msg.sender != newAdmin, "new admin cannot be same as old one");
    _grantRole(DEFAULT_ADMIN_ROLE, newAdmin);
    _revokeRole(DEFAULT_ADMIN_ROLE, msg.sender);
  }
}

