// SPDX-License-Identifier: GPL-3.0
pragma solidity 0.8.22;

import { ud, UD60x18, UNIT } from "@prb/math/src/UD60x18.sol";
import { IERC20Metadata } from "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import { IInternalAccount } from "../interfaces/IInternalAccount.sol";
import { IAccountManager } from "../interfaces/IAccountManager.sol";
import { IProtocolGovernor } from "../interfaces/IProtocolGovernor.sol";
import { IAssetPriceProvider } from "@omega/shared/src/interfaces/IAssetPriceProvider.sol";
import { GovernorLib } from "../libraries/GovernorLib.sol";
import { ProtocolModule } from "../system/ProtocolModule.sol";
import { AccountLib } from "../libraries/AccountLib.sol";
import { ILendingPool } from "../interfaces/ILendingPool.sol";
import { IAccount } from "../interfaces/IAccount.sol";
import { IStrategyVault } from "@omega/shared/src/interfaces/IStrategyVault.sol";
import { Vault } from "@omega/shared/src/types/Vault.sol";

/// @dev For frontend and backend usage.
contract ProtocolViewer {
  struct AccountPosition {
    IStrategyVault vault;
    address baseAsset;
    uint256 value;
    uint256 amount;
    uint256 shares;
    uint256 depositAllowance;
  }

  struct AccountOverview {
    address manager;
    address account;
    AccountPosition[] positions;
  }

  struct Idle {
    address addr;
    uint256 amount;
    uint256 value;
  }

  struct AccountHealthOverview {
    address manager;
    address account;
    address collateralAsset;
    uint256 collateralValue;
    uint256 collateralAmount;
    uint256 totalInvestmentValue;
    UD60x18 healthFactor;
    bool isLiquidatable;
    bool isRisky;
    bool hasBadDebt;
    /// Loan information
    uint256 totalDebtValue;
    // Based off collateral value
    uint256 totalBorrowLimit;
    uint256 borrowFactoredDebtValue;
    AccountLib.Debt[] debts;
    Idle[] idleAssets;
  }

  struct LenderOverview {
    address lendingPool;
    address lendAsset;
    uint256 lendAssetBalance;
    uint256 lendAssetBalanceUsd;
  }

  struct LocalVars {
    address lendAsset;
    uint8 lendAssetDecimals;
    IAccountManager manager;
  }

  struct CollateralOverview {
    address manager;
    address collateralAsset;
    uint256 collateralValue;
    uint256 collateralAmount;
  }

  struct LendOverview {
    address pool;
    address asset;
    uint256 depositAmount;
    uint256 depositAmountUsd;
    uint256 borrowAmount;
    uint256 borrowAmountUsd;
    UD60x18 borrowRate;
    UD60x18 liquidityRate;
  }

  struct StrategyOverview {
    IStrategyVault strategy;
    address baseAsset;
    uint256 maxTotalDepositCap;
    uint256 maxDepositPerAccount;
    uint256 remainingTotalDepositCap;
    uint256 totalShares;
    uint256 totalValue;
    UD60x18 depositFee;
    UD60x18 withdrawalFee;
  }

  IProtocolGovernor protocolGovernor;

  constructor(address _protocolGovernor) {
    protocolGovernor = IProtocolGovernor(_protocolGovernor);
  }

  function estimateMaximumBorrow(
    address account,
    address strategy_,
    address asset
  ) external view returns (uint256 maxBorrowAmount) {
    IAccountManager accountManager = IAccount(account).getManager();
    IStrategyVault strategy = IStrategyVault(strategy_);
    Vault.Parameters memory params = strategy.getVaultParams();
    UD60x18 depositFee = params.depositFee;
    IAssetPriceProvider priceProvider = protocolGovernor.getUsdPriceProvider();
    AccountLib.Health memory health = accountManager.getAccountHealth(account);
    if (health.isRisky || health.hasBadDebt) {
      return 0;
    }
    uint256 price = priceProvider.getAssetPrice(asset);
    AccountLib.DebtConfig memory debtConfig = accountManager.getDebtConfig(asset);
    (, UD60x18 riskThreshold, ) = accountManager.getLiquidationParameters();

    uint256 maxBorrowValue = 0;

    {
      uint256 remainingMaxBorrowValue = health.totalBorrowLimit - health.borrowFactoredDebtValue;
      uint256 maxBorrowValueBasedOnCollateral = ud(remainingMaxBorrowValue).div(debtConfig.borrowFactor).unwrap();
      uint256 maxBorrowValueBasedOnEquity = ud(health.totalInvestmentValue + health.collateralValue)
        .sub(riskThreshold.mul(ud(health.totalDebtValue)))
        .div(riskThreshold.sub(UNIT.sub((depositFee))))
        .unwrap();

      if (maxBorrowValueBasedOnEquity > maxBorrowValueBasedOnCollateral) {
        maxBorrowValue = maxBorrowValueBasedOnCollateral;
      } else {
        maxBorrowValue = maxBorrowValueBasedOnEquity;
      }
    }

    maxBorrowAmount = (maxBorrowValue * (10 ** IERC20Metadata(asset).decimals())) / price;
    // reduce the amount slightly to prevent the AccountInsolvent error
    maxBorrowAmount = (maxBorrowAmount * 9999) / 1e4;
  }

  /// @dev Estimate new health factor after depositing or withdrawing collateral.
  function estimateCollateralAction(
    address account,
    uint256 depositAmount,
    uint256 withdrawAmount
  ) external view returns (AccountLib.Health memory newHealth) {
    IAccountManager accountManager = IAccount(account).getManager();
    IAssetPriceProvider oracle = protocolGovernor.getUsdPriceProvider();
    AccountLib.Health memory health = accountManager.getAccountHealth(account);
    (, UD60x18 riskThreshold, UD60x18 liquidationThreshold) = accountManager.getLiquidationParameters();
    uint256 price = oracle.getAssetPrice(accountManager.getCollateral());

    newHealth = health;

    uint256 collateralValueToWithdraw = ud(withdrawAmount).mul(ud(price)).unwrap();
    uint256 collateralValueToDeposit = ud(depositAmount).mul(ud(price)).unwrap();

    uint256 totalEquity = newHealth.totalInvestmentValue + newHealth.collateralValue + collateralValueToDeposit;
    totalEquity = totalEquity > collateralValueToWithdraw ? totalEquity - collateralValueToWithdraw : 0;

    if (newHealth.totalDebtValue > 0 && totalEquity > 0) {
      newHealth.healthFactor = ud(totalEquity).div(ud(newHealth.totalDebtValue));
      newHealth.isRisky = newHealth.healthFactor < riskThreshold;
      newHealth.isLiquidatable = newHealth.healthFactor < liquidationThreshold;
    } else if (newHealth.totalDebtValue > 0) {
      newHealth.hasBadDebt = true;
    }
  }

  function getCollateralOverview(address[] memory managers) external view returns (CollateralOverview[] memory) {
    CollateralOverview[] memory overviews = new CollateralOverview[](managers.length);
    IAssetPriceProvider oracle = protocolGovernor.getUsdPriceProvider();

    for (uint256 i = 0; i < managers.length; i++) {
      CollateralOverview memory overview;

      overview.manager = managers[i];
      overview.collateralAsset = IAccountManager(managers[i]).getCollateral();
      overview.collateralAmount = IERC20(overview.collateralAsset).balanceOf(managers[i]);
      overview.collateralValue = ud(oracle.getAssetPrice(overview.collateralAsset))
        .mul(ud(overview.collateralAmount))
        .div(ud(10 ** IERC20Metadata(overview.collateralAsset).decimals()))
        .unwrap();

      overviews[i] = overview;
    }

    return overviews;
  }

  function getLendOverview(address[] memory pools) external view returns (LendOverview[] memory) {
    LendOverview[] memory overviews = new LendOverview[](pools.length);
    IAssetPriceProvider oracle = protocolGovernor.getUsdPriceProvider();

    for (uint256 i = 0; i < pools.length; i++) {
      LendOverview memory overview;

      overview.pool = pools[i];

      ILendingPool.Reserve memory reserve = ILendingPool(pools[i]).getReserve();
      overview.borrowRate = reserve.borrowRate;
      overview.liquidityRate = reserve.liquidityRate;
      overview.asset = address(reserve.asset);

      uint8 assetDecimals = IERC20Metadata(overview.asset).decimals();

      overview.depositAmount = ILendingPool(pools[i]).getTotalSupply();
      overview.depositAmountUsd = ud(oracle.getAssetPrice(overview.asset))
        .mul(ud(overview.depositAmount))
        .div(ud(10 ** assetDecimals))
        .unwrap();

      overview.borrowAmount = ILendingPool(pools[i]).getTotalBorrow();
      overview.borrowAmountUsd = ud(oracle.getAssetPrice(overview.asset))
        .mul(ud(overview.borrowAmount))
        .div(ud(10 ** assetDecimals))
        .unwrap();

      overviews[i] = overview;
    }

    return overviews;
  }

  function getStrategyOverviews(address[] memory strategies) external view returns (StrategyOverview[] memory) {
    StrategyOverview[] memory overviews = new StrategyOverview[](strategies.length);
    IAssetPriceProvider oracle = protocolGovernor.getUsdPriceProvider();

    for (uint256 i = 0; i < strategies.length; i++) {
      StrategyOverview memory overview;

      overview.strategy = IStrategyVault(strategies[i]);
      overview.baseAsset = address(overview.strategy.getBaseAsset());
      overview.totalShares = IERC20(strategies[i]).totalSupply();
      overview.totalValue = overview.strategy.getTotalUsdValue();

      uint256 usedDepositCap = ud(overview.totalValue)
        .mul(ud(10 ** IERC20Metadata(overview.baseAsset).decimals()))
        .div(ud(oracle.getAssetPrice(overview.baseAsset)))
        .unwrap();

      Vault.Parameters memory params = overview.strategy.getVaultParams();

      overview.maxTotalDepositCap = params.totalDepositCap;
      overview.maxDepositPerAccount = params.maxDepositPerAccount;
      overview.remainingTotalDepositCap = usedDepositCap > params.totalDepositCap
        ? 0
        : params.totalDepositCap - usedDepositCap;
      overview.depositFee = params.depositFee;
      overview.withdrawalFee = params.withdrawalFee;

      overviews[i] = overview;
    }

    return overviews;
  }

  function getLenderOverviews(address user, address[] memory pools) external view returns (LenderOverview[] memory) {
    LenderOverview[] memory overviews = new LenderOverview[](pools.length);
    IAssetPriceProvider oracle = protocolGovernor.getUsdPriceProvider();

    for (uint256 i = 0; i < pools.length; i++) {
      try ILendingPool(pools[i]).getDepositAmount(user) returns (uint256 depositAmount) {
        LocalVars memory vars;
        vars.lendAsset = address(ILendingPool(pools[i]).getAsset());
        vars.lendAssetDecimals = IERC20Metadata(vars.lendAsset).decimals();

        LenderOverview memory overview;

        overview.lendingPool = pools[i];
        overview.lendAsset = vars.lendAsset;
        overview.lendAssetBalance = depositAmount;
        overview.lendAssetBalanceUsd = ud(oracle.getAssetPrice(vars.lendAsset))
          .mul(ud(overview.lendAssetBalance))
          .div(ud(10 ** vars.lendAssetDecimals))
          .unwrap();

        overviews[i] = overview;
      } catch {
        continue;
      }
    }

    return overviews;
  }

  function getAccountHealthOverviews(
    address user,
    address[] memory managers
  ) external view returns (AccountHealthOverview[] memory) {
    AccountHealthOverview[] memory overviews = new AccountHealthOverview[](managers.length);
    IAssetPriceProvider oracle = protocolGovernor.getUsdPriceProvider();

    for (uint256 i = 0; i < managers.length; i++) {
      try IAccountManager(managers[i]).getAccount(user) returns (address account) {
        if (account == address(0)) {
          continue;
        }

        AccountHealthOverview memory overview;

        LocalVars memory vars;
        vars.manager = IAccountManager(managers[i]);

        AccountLib.Health memory health = vars.manager.getAccountHealth(account);

        overview = AccountHealthOverview({
          manager: managers[i],
          account: account,
          collateralAsset: vars.manager.getCollateral(),
          collateralValue: health.collateralValue,
          collateralAmount: 0,
          totalInvestmentValue: health.totalInvestmentValue,
          healthFactor: health.healthFactor,
          isLiquidatable: health.isLiquidatable,
          isRisky: health.isRisky,
          hasBadDebt: health.hasBadDebt,
          totalDebtValue: health.totalDebtValue,
          totalBorrowLimit: health.totalBorrowLimit,
          borrowFactoredDebtValue: health.borrowFactoredDebtValue,
          debts: health.debts,
          idleAssets: new Idle[](health.debts.length)
        });

        for (uint256 j = 0; j < health.debts.length; j++) {
          Idle memory idle;
          idle.addr = health.debts[j].asset;
          idle.amount = IERC20(idle.addr).balanceOf(account);
          idle.value = ud(idle.amount).mul(ud(oracle.getAssetPrice(idle.addr))).unwrap();
          overview.idleAssets[j] = idle;
        }

        uint256 collateralPrice = oracle.getAssetPrice(overview.collateralAsset);
        overview.collateralAmount = ud(overview.collateralValue)
          .mul(ud(10 ** IERC20Metadata(overview.collateralAsset).decimals()))
          .div(ud(collateralPrice))
          .unwrap();

        overviews[i] = overview;
      } catch {
        continue;
      }
    }

    return overviews;
  }

  function getAccountPosition(
    address user,
    address manager,
    address strategy_
  ) external view returns (AccountPosition memory) {
    IAccountManager accountManager = IAccountManager(manager);
    address account = accountManager.getAccount(user);
    IStrategyVault strategy = IStrategyVault(strategy_);
    IAssetPriceProvider oracle = protocolGovernor.getUsdPriceProvider();
    Vault.Parameters memory params = strategy.getVaultParams();

    AccountPosition memory position;
    position.vault = strategy;
    position.shares = IERC20(address(strategy)).balanceOf(account);
    position.value = strategy.getPositionValue(account);
    position.baseAsset = address(strategy.getBaseAsset());

    uint256 usedDepositCap = ud(position.value)
      .mul(ud(10 ** IERC20Metadata(position.baseAsset).decimals()))
      .div(ud(oracle.getAssetPrice(position.baseAsset)))
      .unwrap();
    position.depositAllowance = usedDepositCap > params.maxDepositPerAccount
      ? 0
      : params.maxDepositPerAccount - usedDepositCap;

    uint8 baseAssetDecimals = IERC20Metadata(position.baseAsset).decimals();
    uint256 assetPrice = oracle.getAssetPrice(position.baseAsset);
    position.amount = ud(position.value).mul(ud(10 ** baseAssetDecimals)).div(ud(assetPrice)).unwrap();

    return position;
  }

  function getAccountPositions(
    address user,
    address[] memory managers
  ) external view returns (AccountOverview[] memory) {
    AccountOverview[] memory overviews = new AccountOverview[](managers.length);
    IAssetPriceProvider oracle = protocolGovernor.getUsdPriceProvider();

    for (uint256 i = 0; i < managers.length; i++) {
      try IAccountManager(managers[i]).getAccount(user) returns (address account) {
        LocalVars memory vars;
        vars.manager = IAccountManager(managers[i]);

        uint256 totalPositions = vars.manager.getActiveStrategyCount(account);

        if (totalPositions == 0) {
          continue;
        }

        AccountPosition[] memory positions = new AccountPosition[](totalPositions);

        for (uint256 j = 0; j < totalPositions; j++) {
          AccountPosition memory position;

          position.vault = IStrategyVault(vars.manager.getActiveStrategy(account, j));
          position.shares = IERC20(address(position.vault)).balanceOf(account);
          position.value = position.vault.getPositionValue(account);
          position.baseAsset = address(position.vault.getBaseAsset());

          Vault.Parameters memory params = position.vault.getVaultParams();
          uint256 usedDepositCap = ud(position.value)
            .mul(ud(10 ** IERC20Metadata(position.baseAsset).decimals()))
            .div(ud(oracle.getAssetPrice(position.baseAsset)))
            .unwrap();
          position.depositAllowance = usedDepositCap > params.maxDepositPerAccount
            ? 0
            : params.maxDepositPerAccount - usedDepositCap;

          uint8 baseAssetDecimals = IERC20Metadata(position.baseAsset).decimals();

          uint256 assetPrice = oracle.getAssetPrice(position.baseAsset);
          position.amount = ud(position.value).mul(ud(10 ** baseAssetDecimals)).div(ud(assetPrice)).unwrap();

          positions[j] = position;
        }

        overviews[i] = AccountOverview({ manager: address(vars.manager), account: account, positions: positions });
      } catch {
        continue;
      }
    }

    return overviews;
  }
}

