// SPDX-License-Identifier: GPL-3.0
pragma solidity 0.8.22;

import "solady/src/tokens/ERC20.sol";
import "./IAccount.sol";

abstract contract InternalAccountEvents {
  /// @notice The owner made a deposit of `amount` into `strategy`
  event StrategyDeposit(address indexed strategy, uint256 amount, uint256 borrowedAmount, uint256 shares);
  /// @notice The owner withdrew `amount` from `strategy`
  event StrategyWithdraw(address indexed strategy, uint256 amount, uint256[] repaidAmounts, uint256 shares);
  /// @notice The deposits into `strategy` have been forcibly withdrawn and `receveredAmount` was returned
  /// @dev When strategy == address(0) it indicates a liquidation of the balance in the account
  event StrategyLiquidated(address indexed strategy, uint256 amount, uint256 shares);
}

interface IInternalAccount is IAccount {
  function strategyDeposit(
    address strategy,
    uint256 amount,
    bytes memory data
  ) external returns (uint256 receivedShares, bytes memory result, uint256 borrowedAmount);
  function strategyWithdraw(
    address strategy,
    uint256 shares,
    bool willRepay,
    bytes memory data
  ) external returns (uint256 receivedAssets, bytes memory result, uint256[] memory repaidAmounts);
  function liquidateStrategy(
    address strategy,
    address recipient,
    uint256 sharesToLiquidate,
    bytes memory data
  ) external returns (uint256 receivedAssets, uint256 sharesLiquidated, bytes memory result);
}

// SPDX-License-Identifier: GPL-3.0
pragma solidity 0.8.22;

import { IERC20 } from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import { UD60x18, ud } from "@prb/math/src/UD60x18.sol";
import "./ILiquidationReceiver.sol";
import { AccountLib } from "../libraries/AccountLib.sol";
import { ILendingPool } from "./ILendingPool.sol";
import { IAccountBeacon } from "./IAccountBeacon.sol";

interface IAccountManager {
  struct StrategyDepositParams {
    address owner;
    address strategy;
    address depositAsset;
    uint256 depositAmount;
    uint256 amountToBorrow;
    bytes data;
  }

  struct StrategyWithdrawalParams {
    address owner;
    address strategy;
    address depositAsset;
    uint256 withdrawnAssets;
    bool willRepay;
  }

  function getAccountBeacon() external view returns (IAccountBeacon);
  function createAccount() external returns (address payable account);
  function getAccountLiquidationStatus(address account) external view returns (AccountLib.LiquidationStatus memory);
  function isApprovedStrategy(address strategy) external view returns (bool);
  function updateStrategyApproval(address strategy, bool approval) external;

  function pauseBorrow() external;
  function unpauseBorrow() external;
  function pauseAccount(address account) external;
  function unpauseAccount(address account) external;
  function getDebtAssets() external view returns (address[] memory);
  function getCollateral() external view returns (address);

  function setLiquidationSettings(AccountLib.LiquidationSettings memory liquidationSettings) external;
  function getLiquidationSettings() external view returns (AccountLib.LiquidationSettings memory);
  function isLiquidationReceiver(address receiver) external view returns (bool);

  function emitLiquidationFeeEvent(
    address feeCollector_,
    address liquidationFeeTo,
    uint256 protocolShare,
    uint256 liquidatorShare,
    address asset
  ) external;

  function transferRepaymentFromAccount(address asset, address account, uint256 amount) external;

  /// @notice Get an active strategy's address by index
  /// @param index The index of the active strategy
  function getActiveStrategy(address account, uint256 index) external view returns (address);

  function getAccountHealth(address account) external view returns (AccountLib.Health memory health);

  /// @notice Get the number of active strategies
  function getActiveStrategyCount(address account) external view returns (uint256);

  /// @dev Deposits underlying collateral into the account and mints collateral shares to the receiver.
  /// @dev Collateral is assigned to the receiver's account.
  function deposit(uint256 assets, address receiver) external returns (uint256 shares);

  /// @dev Burns collateral shares from the account of `msg.sender` and sends underlying collateral to the receiver.
  /// `msg.sender` must be owner of account that owns the shares.
  function withdraw(uint256 shares, address receiver) external returns (uint256 assets);

  // Following three functions are only callable by the target Account itself.
  function repay(
    address account,
    address[] calldata assets,
    uint256[] calldata amounts
  ) external returns (uint256[] memory repaidAmounts);

  function claim(address asset, uint256 amount, address recipient) external;

  /// @notice Borrows assets if needed and deposits them into a strategy on behalf of msg.sender, which must be an
  /// Account.
  function strategyDeposit(
    StrategyDepositParams calldata params
  ) external returns (uint256 shares, bytes memory, uint256 borrowedAmount);

  /// @notice Withdraws assets from a strategy on behalf of msg.sender and repays debt.
  function strategyWithdrawal(
    StrategyWithdrawalParams calldata params
  ) external returns (uint256[] memory repaidAmounts);

  function liquidate(
    address account,
    address liquidationFeeTo
  ) external returns (ILiquidationReceiver liquidationReceiver_);
  function liquidateStrategy(
    address account,
    address liquidationFeeTo,
    address strategy,
    uint256 sharesToLiquidate,
    bytes memory data
  )
    external
    returns (
      ILiquidationReceiver liquidationReceiver_,
      uint256 liquidatedAssets,
      uint256 liquidatedShares,
      bytes memory
    );
  function liquidateCollateral(
    address account,
    uint256 debtToCover,
    address debt,
    address collateral,
    address liquidationFeeTo,
    bool isCollateralIdleAsset
  ) external returns (AccountLib.CollateralLiquidation memory);
  function simulateCollateralLiquidation(
    address account,
    uint256 debtToCover,
    address debt,
    address collateral,
    bool isCollateralIdleAsset
  ) external view returns (AccountLib.CollateralLiquidation memory);
  function simulateLiquidationRepayment(
    address account,
    address debt,
    uint256 idleAssetAmount
  ) external view returns (AccountLib.LiquidationRepayment memory);
  function getLiquidationParameters()
    external
    view
    returns (UD60x18 maxBorrowFactor, UD60x18 riskThreshold, UD60x18 liquidationThreshold);
  function getAccount(address user) external view returns (address);

  /// @notice Called by the Account if an action has been performed that may have changed the account's health.
  function afterHealthUpdate() external;
  function getDebtConfig(address asset) external view returns (AccountLib.DebtConfig memory);
  function getCollateralConfig(address asset) external view returns (AccountLib.CollateralConfig memory);

  function updateDebtConfig(address asset, UD60x18 borrowFactor, bool status) external;
  function updateCollateralConfig(address asset, UD60x18 collateralLiquidationBonus) external;

  function acquireLock() external;
  function releaseLock() external;

  error LendingPoolMismatch(address caller, address expectedLendingPool, address asset);
  error InvalidAccount();
  error BorrowLimitExceeded();
  error InvalidLiquidationReceiver();
  error AccountExists();
  error InvalidParams();
  error DebtNotEnabled();

  // ACCOUNTS
  /// @notice Account failed solvency check after some action.
  /// @dev The account's debt isn't sufficiently collateralized and/or the account is liquidatable.
  error AccountInsolvent();
  /// @dev Account health has worsened
  error AccountHealthWorsened();
  /// @dev Account cannot be liquidated
  error AccountHealthy();
  /// @notice Account is being liquidated
  error AccountBeingLiquidated();
  /// @notice Account is not being liquidated
  error AccountNotBeingLiquidated();
  /// @notice Interaction with a strategy that is not approved
  error StrategyNotApproved();
  error StrategyNotLiquidatable();

  error InvalidActiveStrategy();
  error InvalidDebtAsset();
  error ApproveDisabled();
  error TransferDisabled();
}

/// @title Account Factory Events
/// @dev Place all events used by the AccountManager contract here
abstract contract AccountManagerEvents {
  ///////////// ACCOUNT /////////////
  event AccountCreated(address indexed owner, address account);
  /// @notice A user has borrowed.
  event AccountBorrowed(address indexed owner, address indexed account, address indexed asset, uint256 amount);
  /// @notice A user has repaid.
  event AccountRepaid(address indexed owner, address indexed account, address[] assets, uint256[] amounts);
  /// @notice User claimed assets from their account.
  event AccountClaimed(address indexed owner, address indexed account, address indexed asset, uint256 amount);
  /// @notice The owner has made their first deposit into `strategy`
  event StrategyActivated(address indexed owner, address indexed account, address indexed strategy);
  /// @notice The owner has withdrawn their last deposit from `strategy`
  event StrategyDeactivated(address indexed owner, address indexed account, address indexed strategy);

  ///////////// COLLATERAL /////////////
  event CollateralDeposit(address indexed owner, address account, address sender, uint256 amount);
  event CollateralWithdrawal(address indexed owner, address account, address receiver, uint256 amount);

  ///////////// CONFIG /////////////
  event LiquidationParametersUpdated(UD60x18 maxBorrowFactor, UD60x18 riskThreshold, UD60x18 liquidationThreshold);
  event CollateralLiquidationBonusUpdated(UD60x18 collateralLiquidationBonus);
  /// @notice The admin updated the approval status of `strategy` within manager
  event StrategyUpdated(address strategy, bool approval);

  ///////////// LIQUIDATION /////////////

  /// @notice Account liquidation started
  event AccountLiquidationStarted(address indexed account);
  /// @notice Account liquidation completed
  event AccountLiquidationCompleted(address indexed account);

  event LiquidationFeeTaken(
    address indexed feeCollector,
    address indexed liquidator,
    uint256 protocolShare,
    uint256 liquidatorShare,
    address indexed asset
  );
  /// @dev LiquidationReceiver is created per (account, liquidationFeeTo).
  event LiquidationReceiverCreated(
    address indexed account,
    address indexed liquidationFeeTo,
    address liquidationReceiver
  );
  event CollateralLiquidation(
    address indexed account,
    address indexed debt,
    address indexed collateral,
    uint256 debtAmountNeeded,
    uint256 collateralAmount,
    uint256 bonusCollateral
  );

  ///////////// UPGRADEABILITY /////////////
  event AccountImplUpgraded(address indexed newImpl);
  event LiquidationReceiverImplUpgraded(address indexed newImpl);
}

