/// @dev All values are all denominated in 18 decimal USD value.
library AccountLib {
  // keccak256(abi.encode(uint256(keccak256("omega.proxy.AccountManager")) - 1)) & ~bytes32(uint256(0xff))
  bytes32 internal constant MANAGER_SLOT = 0x682ae3b68a7b5d15dd8bd673ca40bd86b95abd914383b3c598933bb6c6089200;

  // keccak256(abi.encode(uint256(keccak256("omega.proxy.AccountBeacon")) - 1)) & ~bytes32(uint256(0xff))
  bytes32 internal constant BEACON_SLOT = 0x256f316b85adfbc7347d9cf8f11503728a2117f9e08bed62716a29b5d00afb00;

  function manager() internal view returns (IAccountManager) {
    return IAccountManager(StorageSlot.getAddressSlot(MANAGER_SLOT).value);
  }

  function beacon() internal view returns (IAccountBeacon) {
    return IAccountBeacon(StorageSlot.getAddressSlot(BEACON_SLOT).value);
  }

  error MulticallSimulation(SimulationResult sim);
  error MulticallSimulationFailed();

  struct Debt {
    address asset;
    uint256 amount;
    uint256 value;
    /// @notice The value this debt asset contributes towards the borrowFactoredValue.
    uint256 borrowFactoredValue;
  }

  struct CollateralConfig {
    address asset;
    UD60x18 collateralLiquidationBonus;
  }

  struct DebtConfig {
    address asset;
    ILendingPool lendingPool;
    /// @notice The borrow factor of debt asset.
    UD60x18 borrowFactor;
    bool isEnabled;
  }

  /// @notice The health of the account
  struct Health {
    uint256 collateralValue;
    uint256 totalInvestmentValue;
    UD60x18 healthFactor;
    bool isLiquidatable;
    bool isRisky;
    bool hasBadDebt;
    /// Loan information
    uint256 totalDebtValue;
    // Based off collateral value
    uint256 totalBorrowLimit;
    uint256 borrowFactoredDebtValue;
    Debt[] debts;
  }

  struct SimulationResult {
    Health health;
    bytes[] results;
  }

  /// @notice Expected values resulting from a collateral liquidation.
  /// @param actualDebtToLiquidate the amount of debt to cover for the account
  /// @param collateralAmount the amount of collateral to receive excluding bonus collateral.
  struct CollateralLiquidation {
    address collateral;
    address debt;
    uint256 actualDebtToLiquidate;
    uint256 collateralAmount;
    CollateralBonus collateralBonus;
  }

  /// @dev bonusCollateral is the reward on top of collateralAmount. It is the sum of protocolShare and liquidatorShare.
  struct CollateralBonus {
    uint256 protocolShare;
    uint256 liquidatorShare;
  }

  struct LiquidationStatus {
    bool isLiquidating;
    uint256 liquidationStartTime;
  }

  struct LiquidationSettings {
    /// @dev Fee applied to assets used towards debt repayment during liquidation.
    /// @dev Fee is not applied if debt is being repaid in exchange for collateral. In this case, see collateralLiquidationBonus.
    UD60x18 repaymentFee;
    /// @dev Share of liquidation repayment fee that goes towards protocol.
    /// @dev Ex. If protocolShare is 10%, then 10% of whatever the fee is goes to the protocol, and 90% goes to the liquidator.
    UD60x18 protocolRepaymentShare;
    /// @dev Share of collateral liquidation fee that goes towards protocol.
    UD60x18 protocolCollateralShare;
  }

  /// @dev Total amount of idle assets used is sum of repaymentAmount, protocolShare and liquidatorShare.
  struct LiquidationRepayment {
    /// @dev The amount of asset that is used to repay debt.
    uint256 amount;
    /// @dev The repayment fee
    LiquidationRepaymentFee fee;
  }

  struct LiquidationRepaymentFee {
    uint256 protocolShare;
    uint256 liquidatorShare;
  }

  function parseSimulationError(bytes memory reason) internal pure returns (SimulationResult memory sim) {
    if (reason.length < 484) revert MulticallSimulationFailed();

    bytes4 errorSelector;
    assembly {
      errorSelector := mload(add(reason, 32)) // Load first 32 bytes, take first 4.
    }

    if (errorSelector != MulticallSimulation.selector) revert MulticallSimulationFailed();

    assembly {
      reason := add(reason, 0x04)
    }
    return abi.decode(reason, (SimulationResult));
  }
}

