    if (_protocolGovernor().getOwner() != account) {
      return false;
    }
    return true;
  }

  function getProtocolGovernor() external view returns (IProtocolGovernor) {
    return _protocolGovernor();
  }

  /// @notice Returns fee collector
  function _transferProtocolFee(address asset, uint256 fee) internal returns (address feeCollector) {
