pragma solidity 0.8.22;

import { UD60x18, ud, UNIT, ZERO } from "@prb/math/src/UD60x18.sol";
import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";
import "@openzeppelin/contracts/access/Ownable2Step.sol";
import { IAccessControl } from "@openzeppelin/contracts/access/IAccessControl.sol";

import "@omega/shared/src/libraries/traits/AddressCheckerTrait.sol";
import { IAssetPriceProvider } from "@omega/shared/src/interfaces/IAssetPriceProvider.sol";
import "../libraries/GovernorLib.sol";
import "../interfaces/IProtocolGovernor.sol";
import { IAccount } from "../interfaces/IAccount.sol";

abstract contract ProtocolGovernorEvents {
  event FeeUpdated(bytes32 indexed id, UD60x18 newFee);
  event AddressSet(bytes32 indexed id, address newAddress);
  event ImmutableAddressSet(bytes32 indexed id, address newAddress);
  event ManagerStatusUpdated(address indexed manager, bool status);
  event RoleSet(bytes32 indexed role, address indexed account, bool status);
  event ProtocolDeprecatedStatusSet(bool status);
  event AccountManagerUpdated(address indexed manager, bool status);
  event LendingPoolSet(address indexed asset, address pool);
  event GasLeftMinimumSet(bytes32 indexed id, uint256 minimumGasLeft);
  event AccountManagerLockerSet(address indexed locker, bool status);
}

/**
 * @title ProtocolGovernor
 * @dev Allows for storing and management of common protocol data (roles, addresses, configuration).
 */
contract ProtocolGovernor is Ownable2Step, AddressCheckerTrait, ProtocolGovernorEvents, IProtocolGovernor {
  uint256 private constant ACCOUNT_MANAGER_NOT_ENTERED = 0;
  uint256 private constant ACCOUNT_MANAGER_ENTERED = 1;

  /**
   * @dev Unauthorized reentrant call.
   */
  error AccountManagerReentrancyGuardReentrantCall();

  /// @notice Map of contract names to their contract addresses.
  mapping(bytes32 => address) internal _addresses;

  /// @notice Immutable map of contract names to their contract addresses.
  mapping(bytes32 => address) internal _immutableAddresses;

  Settings internal _settings;

  /// @dev Parameters for initializing the Protocol Governor
  struct InitParams {
    address feeCollector;
  }

  /**
   * @dev Only allows addresses that are the protocol admin to call the function.
   */
  modifier onlyProtocolOwner() {
    if (owner() != _msgSender()) {
      revert UnauthorizedRole(_msgSender(), "PROTOCOL_ADMIN");
    }
    _;
  }

  /// @dev Account managers and account manager lockers can acquire and release the account manager lock.
  modifier onlyAccountManagerLocker() {
    if (_settings.managers[msg.sender] != true && _settings.accountManagerLocker[msg.sender] != true) {
      revert UnauthorizedRole(msg.sender, "ACCOUNT_MANAGER_LOCKER");
    }
    _;
  }

  constructor(InitParams memory params) Ownable(msg.sender) nonZeroAddress(params.feeCollector) {
    _setAddress(GovernorLib.FEE_COLLECTOR, params.feeCollector);
  }

  //////////////////////////
  // ADMIN FUNCTIONS
  //////////////////////////

  function setProtocolDeprecatedStatus(bool status) external onlyProtocolOwner {
    _settings.isProtocolDeprecated = status;
    emit ProtocolDeprecatedStatusSet(status);
  }

  /// @dev Sets an address by id
  function setAddress(bytes32 id, address addr) public onlyProtocolOwner {
    _setAddress(id, addr);
  }

  // @dev Initialize an address by id, this cannot be changed after being set.
  function setImmutableAddress(bytes32 id, address addr) public onlyProtocolOwner {
    _setImmutableAddress(id, addr);
  }

  function setLendingPool(address asset, address pool) external onlyProtocolOwner {
    if (_settings.assetToLendingPool[asset] != address(0)) {
      revert DuplicateLendingPool(asset);
    }

    _settings.assetToLendingPool[asset] = pool;
    emit LendingPoolSet(asset, pool);
  }

  /// @notice Account managers can deposit into strategies and be whitelisted to borrow assets from lending pools.
  function updateAccountManagerStatus(address manager, bool status) external onlyProtocolOwner {
    _settings.managers[manager] = status;
    emit ManagerStatusUpdated(manager, status);
  }

  function updateAccountManagerLockerStatus(address locker, bool status) external onlyProtocolOwner {
    _settings.accountManagerLocker[locker] = status;
    emit AccountManagerLockerSet(locker, status);
  }

  function setUsdPriceProvider(address provider) external onlyProtocolOwner {
    _settings.usdPriceProvider = IAssetPriceProvider(provider);
  }

  function grantRole(address destination, bytes32 role, address target) external onlyProtocolOwner {
    IAccessControl(destination).grantRole(role, target);
  }

  function revokeRole(address destination, bytes32 role, address target) external onlyProtocolOwner {
    IAccessControl(destination).revokeRole(role, target);
  }

  function setGasLeftMinimum(bytes32 id, uint256 minimumGasLeft) external onlyProtocolOwner {
    _settings.gasLeftMinimum[id] = minimumGasLeft;
    emit GasLeftMinimumSet(id, minimumGasLeft);
  }

  function acquireAccountManagerLock() external onlyAccountManagerLocker {
    if (_settings.accountManagerGuardStatus == ACCOUNT_MANAGER_ENTERED) {
      revert AccountManagerReentrancyGuardReentrantCall();
    }

    _settings.accountManagerGuardStatus = ACCOUNT_MANAGER_ENTERED;
  }

  function releaseAccountManagerLock() external onlyAccountManagerLocker {
    _settings.accountManagerGuardStatus = ACCOUNT_MANAGER_NOT_ENTERED;
  }

  //////////////////////////
  // VIEW FUNCTIONS
  //////////////////////////

  function getOwner() external view returns (address) {
    return Ownable.owner();
  }

  function isProtocolDeprecated() external view returns (bool) {
    return _settings.isProtocolDeprecated;
  }

  /// @dev Returns an address by id
  function getAddress(bytes32 id) external view returns (address) {
    return _addresses[id];
  }

  /// @dev Returns an immutable address by id
  function getImmutableAddress(bytes32 id) external view returns (address) {
    return _immutableAddresses[id];
  }

  function getLendingPool(address asset) external view returns (address) {
    return _settings.assetToLendingPool[asset];
  }

  function isAccountManager(address manager) external view returns (bool) {
    return _settings.managers[manager];
  }

  function isAccountManagerLocker(address locker) external view returns (bool) {
    return _settings.accountManagerLocker[locker];
  }

  function getUsdPriceProvider() external view returns (IAssetPriceProvider) {
    return _settings.usdPriceProvider;
  }

  function getGasLeftMinimum(bytes32 id) external view returns (uint256) {
    return _settings.gasLeftMinimum[id];
  }

  //////////////////////////
  // HELPER FUNCTIONS
  //////////////////////////

  function _setAddress(bytes32 id, address addr) internal nonZeroAddress(addr) {
    _addresses[id] = addr;
    emit AddressSet(id, addr);
  }

  function _setImmutableAddress(bytes32 id, address addr) internal nonZeroAddress(addr) {
    if (_immutableAddresses[id] != address(0)) {
      revert InvalidParams();
    }
    _immutableAddresses[id] = addr;
    emit ImmutableAddressSet(id, addr);
  }
