// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import { SafeTransferLib } from "solady/utils/SafeTransferLib.sol";
import { IRWAERC20 } from "./interfaces/IRWAERC20.sol";
import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import { IERC20Metadata } from "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";

import { IRWAMinter } from "./interfaces/IRWAMinter.sol";
import { IAssetPool } from "./interfaces/IAssetPool.sol";

import { OwnableUpgradeable } from "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";

//import "forge-std/test.sol";

contract RWAMinter is IRWAMinter, OwnableUpgradeable {
  address public rwa20;
  uint256 public soldNum;
  address public pool;
  uint256 public buybackStartAmount;
  uint256 public sellStartPrice;
  uint256 public buyStartPrice;
  uint256 public sellSlopeNum;
  uint256 public sellSlopeDen;
  uint256 public buySlopeNum;
  uint256 public buySlopeDen;
  uint256 public quantity;
  uint256 public startTime;
  uint256 public endTime;
  bool public paused;

  mapping(address => bool) tokenList;

  function initialize(MinterConstructParam calldata param) public initializer {
    __Ownable_init(msg.sender);
    rwa20 = param.rwa20;
    pool = param.raisePool;
    buybackStartAmount = param.buybackStartAmount; // 2999000001
    sellStartPrice = param.sellStartPrice; //900
    buyStartPrice = param.buyStartPrice; // 1200
    sellSlopeNum = param.sellSlopeNum; // 1
    sellSlopeDen = param.sellSlopeDen; // 10
    buySlopeNum = param.buySlopeNum; // 1
    buySlopeDen = param.buySlopeDen; // 10
    quantity = param.quantity;
    startTime = param.startTime;
    endTime = param.endTime;
    require(param.endTime > block.timestamp, "end time should be greater than current time");
    require(param.startTime < endTime, "start time should be less than end time");
  }

  function getBuyPrice(uint256 fromNum, uint256 amount, address token) public view returns (uint256) {
    uint256 dec = 10 ** IERC20Metadata(rwa20).decimals();
    require(amount % dec == 0, "amount should be divisible by decimals");
    require(fromNum + amount <= quantity, "insufficient stock");
    amount = amount / dec;
    fromNum = fromNum / dec + 1;
    dec = 10 ** IERC20Metadata(token).decimals();
    return
      buyStartPrice *
      amount *
      dec +
      ((fromNum * amount + ((amount - 1) * (amount)) / 2) * dec * buySlopeNum) /
      buySlopeDen;
  }

  function getSellPrice(uint256 fromNum, uint256 amount, address token) public view returns (uint256) {
    uint256 dec = 10 ** IERC20Metadata(rwa20).decimals();
    require(amount % dec == 0, "amount should be divisible by decimals");
    require(fromNum + 1 >= buybackStartAmount + amount, "left amount not reach buyback start amount");
    amount = amount / dec;
    fromNum = fromNum / dec;

    dec = 10 ** IERC20Metadata(token).decimals();
    return
      sellStartPrice *
      amount *
      dec +
      ((fromNum * amount - ((amount - 1) * (amount)) / 2) * dec * sellSlopeNum) /
      sellSlopeDen;
  }

  function getCurrentBuyPrice(uint256 amount, address token) public view returns (uint256) {
    return getBuyPrice(soldNum, amount, token);
  }

  function getCurrentSellPrice(uint256 amount, address token) public view returns (uint256) {
    return getSellPrice(soldNum, amount, token);
  }

  function buyToken(uint256 amount, address token, bytes32 inviter) public returns (uint256 price) {
    require(!paused, "paused");
    require(block.timestamp >= startTime && block.timestamp <= endTime, "not in sale period");
    require(tokenList[token], "token not in token list");
    price = getBuyPrice(soldNum, amount, token);
    SafeTransferLib.safeTransferFrom(token, msg.sender, pool, price);
    IRWAERC20(rwa20).mint(msg.sender, amount);
    emit NewTokenSoldMuilty(soldNum + 1, soldNum + amount, msg.sender, price, token, inviter);
    soldNum += amount;

    if (soldNum == quantity) {
      emit AllTokenSold();
      paused = true;
    }
  }

  function sellToken(uint256 amount, address token) public returns (uint256 price) {
    require(!paused, "paused");
    require(block.timestamp >= startTime && block.timestamp <= endTime, "not in sale period");
    require(tokenList[token], "token not in token list");
    price = getSellPrice(soldNum, amount, token);
    IRWAERC20(rwa20).burn(msg.sender, amount);
    IAssetPool(pool).withdrawToken(token, msg.sender, price);
    emit NewTokenBuyBackMulty(soldNum, soldNum - amount + 1, msg.sender, price, token);
    soldNum -= amount;
  }

  function changeNumParameters(
    uint256 _sellStartPrice,
    uint256 _buyStartPrice,
    uint256 _sellSlopeNum,
    uint256 _sellSlopeDen,
    uint256 _buySlopeNum,
    uint256 _buySlopeDen,
    uint256 _quantity,
    uint256 _buybackStartAmount,
    uint256 _startTime,
    uint256 _endTime
  ) public onlyOwner {
    sellStartPrice = _sellStartPrice;
    buyStartPrice = _buyStartPrice;
    sellSlopeNum = _sellSlopeNum;
    sellSlopeDen = _sellSlopeDen;
    buySlopeNum = _buySlopeNum;
    buySlopeDen = _buySlopeDen;
    quantity = _quantity;
    buybackStartAmount = _buybackStartAmount;
    startTime = _startTime;
    endTime = _endTime;
    require(_endTime > block.timestamp, "end time should be greater than current time");
    require(_startTime < _endTime, "start time should be less than end time");

    emit NewNumParameters(
      _sellStartPrice,
      _buyStartPrice,
      _sellSlopeNum,
      _sellSlopeDen,
      _buySlopeNum,
      _buySlopeDen,
      _quantity,
      _buybackStartAmount,
      _startTime,
      _endTime
    );
  }

  function changeAddressParameters(address _rwa20, address _pool) public onlyOwner {
    rwa20 = _rwa20;
    pool = _pool;

    emit NewAddressParameters(_rwa20, _pool);
  }

  function setTokenList(address[] memory _tokens, bool _status) public onlyOwner {
    for (uint256 i = 0; i < _tokens.length; i++) {
      tokenList[_tokens[i]] = _status;
    }

    emit NewTokenList(_tokens, _status);
  }

  function setPaused(bool _paused) public onlyOwner {
    paused = _paused;

    emit PauseStatusChanged(_paused);
  }
}

