interface IRWAMinter {
  struct MinterConstructParam {
    address rwa20;
    address raisePool;
    uint256 buybackStartAmount;
    uint256 sellStartPrice;
    uint256 buyStartPrice;
    uint256 sellSlopeNum;
    uint256 sellSlopeDen;
    uint256 buySlopeNum;
    uint256 buySlopeDen;
    uint256 quantity;
    uint256 startTime;
    uint256 endTime;
  }

  event NewTokenSoldMuilty(
    uint256 indexed fromNum,
    uint256 indexed toNum,
    address indexed owner,
    uint256 price,
    address token,
    bytes32 inviter
  );

  event NewTokenBuyBackMulty(
    uint256 indexed fromNum,
    uint256 indexed toNum,
    address indexed seller,
    uint256 price,
    address token
  );

  event AllTokenSold();

  event NewNumParameters(
    uint256 sellStartPrice,
    uint256 buyStartPrice,
    uint256 sellSlopeNum,
    uint256 sellSlopeDen,
    uint256 buySlopeNum,
    uint256 buySlopeDen,
    uint256 quantity,
    uint256 buybackStartAmount,
    uint256 startBlock,
    uint256 endBlock
  );

  event NewAddressParameters(address rwa20, address raisePool);

  event NewTokenList(address[] tokenlist, bool status);

  event PauseStatusChanged(bool status);

  function getBuyPrice(uint256 fromNum, uint256 amount, address token) external view returns (uint256);

  function getSellPrice(uint256 fromNum, uint256 amount, address token) external view returns (uint256);

  function getCurrentBuyPrice(uint256 amount, address token) external view returns (uint256);

  function getCurrentSellPrice(uint256 amount, address token) external view returns (uint256);

  function buyToken(uint256 amount, address token, bytes32 inviter) external returns (uint256 price);

  function sellToken(uint256 amount, address token) external returns (uint256 price);

  function changeNumParameters(
    uint256 _sellStartPrice,
    uint256 _buyStartPrice,
    uint256 _sellSlopeNum,
    uint256 _sellSlopeDen,
    uint256 _buySlopeNum,
    uint256 _buySlopeDen,
    uint256 _quantity,
    uint256 _buybackStartAmount,
    uint256 _startBlock,
    uint256 _endBlock
  ) external;

  function changeAddressParameters(address _rwa20, address _raisePool) external;

  function setTokenList(address[] memory tokenlist, bool status) external;

  function setPaused(bool status) external;
}

