contracts/NFTMarket_V1.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/access/Ownable2StepUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/cryptography/EIP712Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/IERC20Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/cryptography/ECDSAUpgradeable.sol";
import {IERC721} from "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import {IERC1155} from "@openzeppelin/contracts/token/ERC1155/IERC1155.sol";
import {PausableUpgradeable} from "@openzeppelin/contracts-upgradeable/security/PausableUpgradeable.sol";

interface IWETH is IERC20Upgradeable {
    function deposit() external payable;
    function withdraw(uint256) external;
}

interface INFTToken {
    function createToken(string memory _tokenURI, uint256 _tokenId, bytes memory _signature) external payable returns (uint256);
    function ownerOf(uint256 tokenId) external view returns (address);
    function transferFrom(address from, address to, uint256 tokenId) external;
    function safeTransferFrom(address from, address to, uint256 tokenId) external;
}

contract NFTMarket_V1 is
Initializable,
EIP712Upgradeable,
PausableUpgradeable,
Ownable2StepUpgradeable
{
    string private constant SIGNING_DOMAIN = "RedShilliz";
    string private constant SIGNATURE_VERSION = "1";
    string private constant ERC721_STANDARD = "ERC721";
    string private constant ERC1155_STANDARD = "ERC1155";

    bool public initialized;
    address public wethContractAddress;
    uint256 public serviceFeePercentage;
    uint256 public maxRoyaltyPercentage;
    address public serviceFeeOwner;
    address public tokenContractAddress;

    uint256 public constant TIMELOCK_DURATION = 1 days;
    uint256 public pendingServiceFeeOwnerTimestamp;
    address public pendingServiceFeeOwner;
    uint256 public pendingServiceFeePercentageTimestamp;
    uint256 public pendingServiceFeePercentage;
    uint256 public pendingMaxRoyaltyPercentageTimestamp;
    uint256 public pendingMaxRoyaltyPercentage;
    uint256 public pendingWethAddressTimestamp;
    address public pendingWethAddress;
    uint256 public pendingTokenContractTimestamp;
    address public pendingTokenContract;
    uint256 public pendingOwnershipTransferTimestamp;

    event MarketItemCreated(
        uint256 indexed tokenId,
        address seller,
        address owner,
        uint256 price,
        bool sold
    );

    event RoyaltyTransferred(
        address indexed royaltyReceiver,
        uint256 indexed royaltyAmount
    );

    event ThirdPartyNFTTransferred(
        address indexed nftContract,
        uint256 indexed nftTokenId,
        address indexed sender,
        address receiver
    );

    event BulkTokenCreated(
        string[] indexed tokenName,
        uint256[] indexed tokenIds,
        bytes[] indexed signatures
    );

    event EmergencyWithdrawal(string tokenType, address indexed to, uint256 amount);
    event EmergencyWithdrawalNFT(string tokenType, address indexed token, uint256 tokenId, address indexed to);
    event ContractPaused(address indexed owner);
    event ContractUnpaused(address indexed owner);
    event ServiceFeeOwnerChangeProposed(address indexed newServiceFeeOwner, uint256 effectiveTime);
    event ServiceFeeOwnerChanged(address indexed oldServiceFeeOwner, address indexed newServiceFeeOwner);
    event ServiceFeeChangeProposed(uint256 newServiceFeePercentage, uint256 effectiveTime);
    event ServiceFeeChanged(uint256 oldServiceFeePercentage, uint256 newServiceFeePercentage);
    event MaxRoyaltyPercentageChangeProposed(uint256 newMaxRoyaltyPercentage, uint256 effectiveTime);
    event MaxRoyaltyPercentageChanged(uint256 oldMaxRoyaltyPercentage, uint256 newMaxRoyaltyPercentage);
    event WethAddressChangeProposed(address indexed newWethAddress, uint256 effectiveTime);
    event WethAddressChanged(address indexed oldWethAddress, address indexed newWethAddress);
    event TokenContractChangeProposed(address indexed newTokenContract, uint256 effectiveTime);
    event TokenContractChanged(address indexed oldTokenContract, address indexed newTokenContract);
    event OwnershipTransferProposed(address indexed previousOwner, address indexed newOwner, uint256 effectiveTime);

    struct MarketItem {
        uint256 tokenId;
        address payable seller;
        address payable owner;
        uint256 price;
        bool sold;
    }

    struct NFTVoucher {
        address royaltyReceiver;
        uint256 royaltyWeight;
        uint256 tokenId;
        address offerer;
        address tokenAddress;
        uint256 listingPrice;
        uint256 startTime;
        uint256 endTime;
        string salt;
        string tokenStandard;
        bytes signature;
    }

    mapping(uint256 => MarketItem) public idToMarketItem;
    mapping(bytes => bool) private signatures;

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    function initialize(
        address payable _serviceFeeOwner,
        address _wethContractAddress,
        uint256 _serviceFeePercentage
    ) public initializer {
        __EIP712_init(SIGNING_DOMAIN, SIGNATURE_VERSION);
        __Pausable_init();
        __Ownable2Step_init();

        require(_serviceFeePercentage <= 10000, "Invalid fee percentage");
        serviceFeeOwner = _serviceFeeOwner;
        wethContractAddress = _wethContractAddress;
        serviceFeePercentage = _serviceFeePercentage;
        maxRoyaltyPercentage = 1000;
        initialized = true;
    }

    modifier onlyAfterTimelock(uint256 timestamp) {
        require(timestamp != 0 && block.timestamp >= timestamp, "Timelock not expired");
        _;
    }

    function setTokenContract(address _tokenContract) external onlyOwner {
        require(!isZeroAddress(_tokenContract), "Zero address not allowed");

        if (!initialized || tokenContractAddress == address(0)) {
            tokenContractAddress = _tokenContract;
            emit TokenContractChanged(address(0), _tokenContract);
            return;
        }

        pendingTokenContract = _tokenContract;
        pendingTokenContractTimestamp = block.timestamp + TIMELOCK_DURATION;

        emit TokenContractChangeProposed(_tokenContract, pendingTokenContractTimestamp);
    }

    function confirmTokenContractChange() external onlyOwner onlyAfterTimelock(pendingTokenContractTimestamp) {
        address oldTokenContract = tokenContractAddress;
        tokenContractAddress = pendingTokenContract;

        pendingTokenContract = address(0);
        pendingTokenContractTimestamp = 0;

        emit TokenContractChanged(oldTokenContract, tokenContractAddress);
    }

    function proposeServiceFeeOwner(address _newServiceFeeOwner) external onlyOwner {
        require(!isZeroAddress(_newServiceFeeOwner), "Zero address not allowed");
        pendingServiceFeeOwner = _newServiceFeeOwner;
        pendingServiceFeeOwnerTimestamp = block.timestamp + TIMELOCK_DURATION;

        emit ServiceFeeOwnerChangeProposed(_newServiceFeeOwner, pendingServiceFeeOwnerTimestamp);
    }

    function confirmServiceFeeOwnerChange() external onlyOwner onlyAfterTimelock(pendingServiceFeeOwnerTimestamp) {
        address oldServiceFeeOwner = serviceFeeOwner;
        serviceFeeOwner = pendingServiceFeeOwner;

        pendingServiceFeeOwner = address(0);
        pendingServiceFeeOwnerTimestamp = 0;

        emit ServiceFeeOwnerChanged(oldServiceFeeOwner, serviceFeeOwner);
    }

    function proposeServiceFee(uint256 _serviceFeePercentage) external onlyOwner {
        require(_serviceFeePercentage <= 10000, "Invalid fee percentage");
        pendingServiceFeePercentage = _serviceFeePercentage;
        pendingServiceFeePercentageTimestamp = block.timestamp + TIMELOCK_DURATION;

        emit ServiceFeeChangeProposed(_serviceFeePercentage, pendingServiceFeePercentageTimestamp);
    }

    function confirmServiceFeeChange() external onlyOwner onlyAfterTimelock(pendingServiceFeePercentageTimestamp) {
        uint256 oldServiceFeePercentage = serviceFeePercentage;
        serviceFeePercentage = pendingServiceFeePercentage;

        pendingServiceFeePercentage = 0;
        pendingServiceFeePercentageTimestamp = 0;

        emit ServiceFeeChanged(oldServiceFeePercentage, serviceFeePercentage);
    }

    function proposeMaxRoyaltyPercentage(uint256 _maxRoyaltyPercentage) external onlyOwner {
        pendingMaxRoyaltyPercentage = _maxRoyaltyPercentage;
        pendingMaxRoyaltyPercentageTimestamp = block.timestamp + TIMELOCK_DURATION;

        emit MaxRoyaltyPercentageChangeProposed(_maxRoyaltyPercentage, pendingMaxRoyaltyPercentageTimestamp);
    }

    function confirmMaxRoyaltyPercentageChange() external onlyOwner onlyAfterTimelock(pendingMaxRoyaltyPercentageTimestamp) {
        uint256 oldMaxRoyaltyPercentage = maxRoyaltyPercentage;
        maxRoyaltyPercentage = pendingMaxRoyaltyPercentage;

        pendingMaxRoyaltyPercentage = 0;
        pendingMaxRoyaltyPercentageTimestamp = 0;

        emit MaxRoyaltyPercentageChanged(oldMaxRoyaltyPercentage, maxRoyaltyPercentage);
    }

    function proposeWethAddress(address _weth) external onlyOwner {
        require(!isZeroAddress(_weth), "Zero address not allowed");
        pendingWethAddress = _weth;
        pendingWethAddressTimestamp = block.timestamp + TIMELOCK_DURATION;

        emit WethAddressChangeProposed(_weth, pendingWethAddressTimestamp);
    }

    function confirmWethAddressChange() external onlyOwner onlyAfterTimelock(pendingWethAddressTimestamp) {
        address oldWethAddress = wethContractAddress;
        wethContractAddress = pendingWethAddress;

        pendingWethAddress = address(0);
        pendingWethAddressTimestamp = 0;

        emit WethAddressChanged(oldWethAddress, wethContractAddress);
    }

    function proposeOwnershipTransfer(address newOwner) public onlyOwner {
        require(newOwner != address(0), "New owner cannot be the zero address");
        _transferOwnership(newOwner);
        pendingOwnershipTransferTimestamp = block.timestamp + TIMELOCK_DURATION;

        emit OwnershipTransferProposed(owner(), newOwner, pendingOwnershipTransferTimestamp);
    }

    function confirmOwnershipTransfer() external onlyAfterTimelock(pendingOwnershipTransferTimestamp) {
        require(msg.sender == pendingOwner(), "Only pending owner can confirm transfer");

        acceptOwnership();
        pendingOwnershipTransferTimestamp = 0;
    }

    function bulkCreateToken(
        string[] calldata _tokenURIs,
        uint256[] calldata _newTokenIds,
        bytes[] calldata _signatures
    ) external payable returns (uint256[] memory) {
        require(tokenContractAddress != address(0), "Token contract not set");

        uint256[] memory returnedTokenIds = new uint256[](_tokenURIs.length);

        for (uint256 i = 0; i < _tokenURIs.length; i++) {
            uint256 tokenId = INFTToken(tokenContractAddress).createToken{value: 0}(
                _tokenURIs[i],
                _newTokenIds[i],
                _signatures[i]
            );
            returnedTokenIds[i] = tokenId;
        }

        emit BulkTokenCreated(_tokenURIs, _newTokenIds, _signatures);
        return returnedTokenIds;
    }

    function bulkRedeem(
        bool[] calldata isThirdPartyNfts,
        address redeemer,
        string[] calldata tokenUris,
        NFTVoucher[] calldata vouchers,
        bytes[] calldata ownerSignatures
    ) external payable {
        uint256 totalRequired = 0;
        for (uint256 i = 0; i < vouchers.length; i++) {
            totalRequired += vouchers[i].listingPrice;
        }
        require(msg.value >= totalRequired, "Insufficient ETH sent for bulk redemption");

        for (uint256 i = 0; i < isThirdPartyNfts.length; i++) {
            if (isThirdPartyNfts[i]) {
                (bool success, ) = address(this).call{value: vouchers[i].listingPrice}(
                    abi.encodeWithSelector(
                        this.redeemOtherNfts.selector,
                        redeemer,
                        vouchers[i]
                    )
                );
                require(success, "redeemOtherNfts failed");
            } else {
                (bool success, ) = address(this).call{value: vouchers[i].listingPrice}(
                    abi.encodeWithSelector(
                        this.redeem.selector,
                        redeemer,
                        tokenUris[i],
                        vouchers[i],
                        ownerSignatures[i]
                    )
                );
                require(success, "redeem failed");
            }
        }

        uint256 excess = address(this).balance;
        if (excess > 0) {
            payable(msg.sender).transfer(excess);
        }
    }

    function createToken(
        string memory _tokenURI,
        uint256 _tokenId,
        bytes memory _signature
    ) public payable whenNotPaused returns (uint256) {
        require(tokenContractAddress != address(0), "Token contract not set");
        return INFTToken(tokenContractAddress).createToken{value: msg.value}(_tokenURI, _tokenId, _signature);
    }

    function redeem(
        address redeemer,
        string calldata tokenUri,
        NFTVoucher calldata voucher,
        bytes memory ownerSignature
    ) public payable whenNotPaused returns (uint256) {
        require(tokenContractAddress != address(0), "Token contract not set");
        validateVoucher(voucher);

        require(msg.value >= voucher.listingPrice, "Invalid Price amount");

        address signer = _verify(voucher);

        require(!isZeroAddress(signer), "Invalid signer address");
        require(voucher.tokenAddress == tokenContractAddress, "Invalid token address");

        signatures[voucher.signature] = true;

        uint256 serviceFee = (voucher.listingPrice * serviceFeePercentage) / 10000;

        uint256 royaltyAmount = 0;
        if (voucher.royaltyReceiver != address(0)) {
            royaltyAmount = (voucher.listingPrice * voucher.royaltyWeight) / 10000;
            require(
                voucher.royaltyWeight <= maxRoyaltyPercentage,
                "Royalty weight should not be more than 10%"
            );
        }

        uint256 sellerEarning = voucher.listingPrice - serviceFee - royaltyAmount;

        if (!tokenExists(voucher.tokenId)) {
            INFTToken(tokenContractAddress).createToken{value: 0}("", voucher.tokenId, ownerSignature);
        }

        address currentOwner = INFTToken(tokenContractAddress).ownerOf(voucher.tokenId);
        require(currentOwner == signer, "Signer must be current owner of NFT");

        idToMarketItem[voucher.tokenId] = MarketItem(
            voucher.tokenId,
            payable(signer),
            payable(redeemer),
            voucher.listingPrice,
            true
        );

        INFTToken(tokenContractAddress).safeTransferFrom(signer, redeemer, voucher.tokenId);

        emit MarketItemCreated(
            voucher.tokenId,
            signer,
            redeemer,
            sellerEarning,
            true
        );

        if (royaltyAmount > 0) {
            payable(voucher.royaltyReceiver).transfer(royaltyAmount);
            emit RoyaltyTransferred(voucher.royaltyReceiver, royaltyAmount);
        }
        payable(serviceFeeOwner).transfer(serviceFee);
        payable(signer).transfer(sellerEarning);

        return voucher.tokenId;
    }

    function acceptOffer(
        string memory tokenUri,
        NFTVoucher calldata voucher,
        bytes memory ownerSignature
    ) public whenNotPaused {
        require(tokenContractAddress != address(0), "Token contract not set");
        validateVoucher(voucher);
        address offerer = _verify(voucher);

        require(!isZeroAddress(offerer), "Invalid offerer address");
        require(voucher.tokenAddress == tokenContractAddress, "Invalid token address");
        require(offerer == voucher.offerer, "Invalid offerer address");

        signatures[voucher.signature] = true;

        validateWethBalance(offerer, voucher.listingPrice);

        uint256 serviceFee = (voucher.listingPrice * serviceFeePercentage) / 10000;

        uint256 royaltyAmount = 0;
        if (voucher.royaltyReceiver != address(0)) {
            royaltyAmount = (voucher.listingPrice * voucher.royaltyWeight) / 10000;
            require(
                voucher.royaltyWeight <= maxRoyaltyPercentage,
                "Royalty weight should not be more than 10%"
            );
        }

        uint256 sellerEarning = voucher.listingPrice - serviceFee - royaltyAmount;

        if (!tokenExists(voucher.tokenId)) {
            INFTToken(tokenContractAddress).createToken{value: 0}(tokenUri, voucher.tokenId, ownerSignature);
        }

        INFTToken(tokenContractAddress).safeTransferFrom(msg.sender, offerer, voucher.tokenId);

        idToMarketItem[voucher.tokenId] = MarketItem ({
                tokenId: voucher.tokenId,
                seller: payable(msg.sender),
                owner: payable(offerer),
                price: voucher.listingPrice,
                sold: true
            }
        );

        IWETH(wethContractAddress).transferFrom(
            offerer,
            msg.sender,
            sellerEarning
        );

        if (royaltyAmount > 0) {
            IWETH(wethContractAddress).transferFrom(
                offerer,
                voucher.royaltyReceiver,
                royaltyAmount
            );
            emit RoyaltyTransferred(voucher.royaltyReceiver, royaltyAmount);
        }

        IWETH(wethContractAddress).transferFrom(
            offerer,
            serviceFeeOwner,
            serviceFee
        );

        emit MarketItemCreated(
            voucher.tokenId,
            msg.sender,
            offerer,
            sellerEarning,
            true
        );
    }

    function acceptOtherNftsOffer(NFTVoucher calldata voucher) public whenNotPaused {
        validateVoucher(voucher);

        address offerer = _verify(voucher);

        require(!isZeroAddress(offerer), "Invalid offerer address");
        require(offerer == voucher.offerer, "Invalid offerer address");

        signatures[voucher.signature] = true;

        validateWethBalance(offerer, voucher.listingPrice);

        uint256 serviceFee = (voucher.listingPrice * serviceFeePercentage) / 10000;

        uint256 royaltyAmount = 0;
        if (voucher.royaltyReceiver != address(0)) {
            royaltyAmount = (voucher.listingPrice * voucher.royaltyWeight) / 10000;
            require(
                voucher.royaltyWeight <= maxRoyaltyPercentage,
                "Royalty weight should not be more than 10%"
            );
        }

        uint256 sellerEarning = voucher.listingPrice - serviceFee - royaltyAmount;

        transferThirdPartyNfts(
            msg.sender,
            offerer,
            voucher.tokenStandard,
            voucher.tokenId,
            voucher.tokenAddress
        );

        IWETH(wethContractAddress).transferFrom(
            offerer,
            msg.sender,
            sellerEarning
        );

        if (royaltyAmount > 0) {
            IWETH(wethContractAddress).transferFrom(
                offerer,
                voucher.royaltyReceiver,
                royaltyAmount
            );
            emit RoyaltyTransferred(voucher.royaltyReceiver, royaltyAmount);
        }

        IWETH(wethContractAddress).transferFrom(
            offerer,
            serviceFeeOwner,
            serviceFee
        );
    }

    function redeemOtherNfts(
        address redeemer,
        NFTVoucher calldata voucher
    ) public whenNotPaused payable {
        validateVoucher(voucher);
        require(msg.value >= voucher.listingPrice, "Invalid Price amount");

        signatures[voucher.signature] = true;

        address signer = _verify(voucher);

        require(!isZeroAddress(signer), "Invalid signer address");

        uint256 serviceFee = (voucher.listingPrice * serviceFeePercentage) / 10000;

        uint256 royaltyAmount = 0;
        if (voucher.royaltyReceiver != address(0)) {
            royaltyAmount = (voucher.listingPrice * voucher.royaltyWeight) / 10000;
            require(
                voucher.royaltyWeight <= maxRoyaltyPercentage,
                "Royalty weight should not be more than 10%"
            );
        }

        uint256 sellerEarning = voucher.listingPrice - serviceFee - royaltyAmount;

        transferThirdPartyNfts(
            signer,
            redeemer,
            voucher.tokenStandard,
            voucher.tokenId,
            voucher.tokenAddress
        );

        if (royaltyAmount > 0) {
            payable(voucher.royaltyReceiver).transfer(royaltyAmount);
            emit RoyaltyTransferred(voucher.royaltyReceiver, royaltyAmount);
        }
        payable(serviceFeeOwner).transfer(serviceFee);
        payable(signer).transfer(sellerEarning);
    }

    function tokenExists(uint256 tokenId) internal view returns (bool) {
        try INFTToken(tokenContractAddress).ownerOf(tokenId) returns (address) {
            return true;
        } catch {
            return false;
        }
    }

    function _hash(
        NFTVoucher calldata voucher
    ) internal view returns (bytes32) {
        return
            _hashTypedDataV4(
            keccak256(
                abi.encode(
                    keccak256(
                        "NFTVoucher(address royaltyReceiver,uint256 royaltyWeight,uint256 tokenId,address offerer,address tokenAddress,uint256 listingPrice,uint256 startTime,uint256 endTime,string salt,string tokenStandard)"
                    ),
                    voucher.royaltyReceiver,
                    voucher.royaltyWeight,
                    voucher.tokenId,
                    voucher.offerer,
                    voucher.tokenAddress,
                    voucher.listingPrice,
                    voucher.startTime,
                    voucher.endTime,
                    keccak256(bytes(voucher.salt)),
                    keccak256(bytes(voucher.tokenStandard))
                )
            )
        );
    }

    function getWethBalance(
        address userAddress
    ) internal view returns (uint256) {
        return IWETH(wethContractAddress).balanceOf(userAddress);
    }

    function getAllowance(address userAddress) internal view returns (uint256) {
        return IWETH(wethContractAddress).allowance(userAddress, address(this));
    }

    function getChainID() external view returns (uint256) {
        return getContractChainId();
    }

    function _verify(
        NFTVoucher calldata voucher
    ) public view returns (address) {
        bytes32 digest = _hash(voucher);
        return ECDSAUpgradeable.recover(digest, voucher.signature);
    }

    function validateVoucher(NFTVoucher calldata voucher) internal view {
        require(
            signatures[voucher.signature] == false,
            "Signature is already used."
        );

        require(
            block.timestamp >= voucher.startTime &&
            block.timestamp <= voucher.endTime,
            "Bid is either expired or not started yet."
        );

        require(voucher.listingPrice > 0, "Price must be at least 1 wei");
    }

    function validateWethBalance(
        address offerer,
        uint256 listingPrice
    ) internal view {
        uint256 balance = getWethBalance(offerer);
        uint256 allowance = getAllowance(offerer);

        require(
            balance >= listingPrice,
            string(abi.encodePacked("Buyer has insufficient WETH balance"))
        );

        require(
            allowance >= listingPrice,
            string(abi.encodePacked("Buyer has insufficient allowance"))
        );
    }

    function transferThirdPartyNfts(
        address fromAddress,
        address toAddress,
        string memory tokenStandard,
        uint256 tokenId,
        address tokenAddress
    ) internal {
        if (
            keccak256(abi.encodePacked(tokenStandard)) ==
            keccak256(abi.encodePacked(ERC721_STANDARD))
        ) {
            IERC721(tokenAddress).safeTransferFrom(
                fromAddress,
                toAddress,
                tokenId
            );
        } else if (
            keccak256(abi.encodePacked(tokenStandard)) ==
            keccak256(abi.encodePacked(ERC1155_STANDARD))
        ) {
            require(
                IERC1155(tokenAddress).balanceOf(fromAddress, tokenId) > 0,
                "Only owner can accept offer"
            );

            IERC1155(tokenAddress).safeTransferFrom(
                fromAddress,
                toAddress,
                tokenId,
                1,
                ""
            );
        } else {
            revert("Invalid ERC Token standard");
        }

        emit ThirdPartyNFTTransferred(tokenAddress, tokenId, fromAddress, toAddress);
    }

    function getContractChainId() internal view returns (uint256) {
        uint256 id;
        assembly {
            id := chainid()
        }
        return id;
    }

    function isZeroAddress(address addr) public pure returns (bool) {
        return addr == address(0);
    }

    function emergencyWithdrawETH(address payable to, uint256 amount) external onlyOwner {
        require(!isZeroAddress(to), "Cannot withdraw to zero address");
        require(amount > 0, "Amount must be greater than zero");
        require(amount <= address(this).balance, "Insufficient balance");

        to.transfer(amount);

        emit EmergencyWithdrawal("ETH", to, amount);
    }

    function emergencyWithdrawToken(address token, address to, uint256 amount) external onlyOwner {
        require(!isZeroAddress(token), "Invalid token address");
        require(!isZeroAddress(to), "Cannot withdraw to zero address");
        require(amount > 0, "Amount must be greater than zero");

        IERC20Upgradeable(token).transfer(to, amount);

        emit EmergencyWithdrawal(token == wethContractAddress ? "WETH" : "ERC20", to, amount);
    }

    function emergencyWithdrawERC721(address token, address to, uint256 tokenId) external onlyOwner {
        require(!isZeroAddress(token), "Invalid token address");
        require(!isZeroAddress(to), "Cannot withdraw to zero address");

        IERC721(token).safeTransferFrom(address(this), to, tokenId);

        emit EmergencyWithdrawalNFT("ERC721", token, tokenId, to);
    }

    function emergencyWithdrawERC1155(address token, address to, uint256 tokenId, uint256 amount) external onlyOwner {
        require(!isZeroAddress(token), "Invalid token address");
        require(!isZeroAddress(to), "Cannot withdraw to zero address");
        require(amount > 0, "Amount must be greater than zero");

        IERC1155(token).safeTransferFrom(address(this), to, tokenId, amount, "");

        emit EmergencyWithdrawalNFT("ERC1155", token, tokenId, to);
    }

    function pause() external onlyOwner {
        _pause();

        emit ContractPaused(msg.sender);
    }

    function unpause() external onlyOwner {
        _unpause();

        emit ContractUnpaused(msg.sender);
    }
}
