contracts/FlashLoanArbitrage.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.26;

import "@openzeppelin/contracts-upgradeable/token/ERC20/ERC20Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts/utils/Strings.sol";
import "@aave/core-v3/contracts/interfaces/IPool.sol";

contract FlashLoanArbitrage is Initializable {
    address public owner;
    IPool public aavePool;
    IWETH public constant WETH = IWETH(0xC02aaA39b223FE8D0A0e5C4F27eAD9083C756Cc2);

    // Preserve storage layout
    address private tempBuyDex; // Slot 2
    address private tempSellDex; // Slot 3
    uint256 private tempAmountOutMinBuy; // Slot 4
    uint256 private tempAmountOutMinSell; // Slot 5

    // Debug events
    event DebugBalance(uint256 balance);
    event DebugSwap(address dex, uint256 amountIn, uint256 amountOutMin, address tokenIn, address tokenOut);
    event DebugRepay(uint256 balance, uint256 totalRepay, uint256 amount, uint256 fee);
    event DebugProfit(uint256 initialBalance, uint256 finalBalance);
    event DebugBribe(uint256 contractBalance, uint256 amount);

    struct Swap {
        address dex;
        uint256 amountOutMin;
        address[] path;
    }

    function initialize(address _aavePool) external initializer {
        owner = msg.sender;
        aavePool = IPool(_aavePool);
    }

    function executeArbitrage(Swap[] calldata swapSequence, uint256 amountIn) external {
        require(msg.sender == owner, "Only owner");
        require(swapSequence.length > 0, "Swap sequence required");

        address[] memory assets = new address[](1);
        assets[0] = swapSequence[0].path[0]; // Initial flash loan token

        uint256[] memory amounts = new uint256[](1);
        amounts[0] = amountIn;

        uint256[] memory modes = new uint256[](1);
        modes[0] = 0;

        bytes memory params = abi.encode(swapSequence);

        aavePool.flashLoan(
            address(this),
            assets,
            amounts,
            modes,
            address(this),
            params,
            0
        );
    }

    function executeOperation(
        address[] calldata assets,
        uint256[] calldata amounts,
        uint256[] calldata premiums,
        address,
        bytes calldata params
    ) external payable returns (bool) {
        require(msg.sender == address(aavePool), "Caller must be Aave Pool");

        Swap[] memory swapSequence = abi.decode(params, (Swap[]));
        uint256 currentAmount = amounts[0];
        address inputToken = assets[0];

        uint256 initialBalance = ERC20Upgradeable(inputToken).balanceOf(address(this));
        emit DebugBalance(initialBalance);
        for (uint256 i = 0; i < swapSequence.length; i++) {      
            emit DebugSwap(
                swapSequence[i].dex,
                currentAmount,
                swapSequence[i].amountOutMin,
                swapSequence[i].path[0],
                swapSequence[i].path[1]
            );      
            currentAmount = executeSwap(swapSequence[i], currentAmount);
        }

        uint256 totalRepay = amounts[0] + premiums[0];
        emit DebugRepay(
            ERC20Upgradeable(inputToken).balanceOf(address(this)),
            totalRepay,
            amounts[0],
            premiums[0]
        );
        repayLoan(inputToken, amounts[0], premiums[0]);

        uint256 finalBalance = ERC20Upgradeable(inputToken).balanceOf(address(this));
        emit DebugProfit(initialBalance, finalBalance);
        require(finalBalance > initialBalance, 
            string.concat("Not profitable: final balance: ", Strings.toString(finalBalance),
            ", initial balance: ", Strings.toString(initialBalance))
        );    

        sendBribe(msg.value);        

        return true;
    }

    function executeSwap(Swap memory swap, uint256 amountIn) internal returns (uint256) {
        require(swap.path.length >= 2, "Invalid swap path");

        ERC20Upgradeable tokenIn = ERC20Upgradeable(swap.path[0]);

        uint256 balance = tokenIn.balanceOf(address(this));
        require(balance >= amountIn, string.concat("Not enough input token: ", Strings.toHexString(swap.path[0]),
            ", tokenInBalance: ", Strings.toString(balance),
            ", amountIn: ", Strings.toString(amountIn)
            ));

        uint256[] memory amountsOut;
        try IUniswapV2Router(swap.dex).swapExactTokensForTokens(
            amountIn,
            swap.amountOutMin,
            swap.path,
            address(this),
            block.timestamp + 60
        ) returns (uint256[] memory result) {
            amountsOut = result;            
        } catch {            
            revert(string.concat("DEX swap failed at: ", Strings.toHexString(swap.dex)));
        }

        return amountsOut[amountsOut.length - 1];
    }

    function repayLoan(address asset, uint256 amount, uint256 fee) internal {
        uint256 totalRepay = amount + fee;
        ERC20Upgradeable token = ERC20Upgradeable(asset);
        uint256 balance = token.balanceOf(address(this));
        require(
        balance >= totalRepay,
        string.concat(
            "Insufficient balance to repay loan. balance:", Strings.toString(balance),
            ", totalRepay: ", Strings.toString(totalRepay),
            ", amount: ", Strings.toString(amount),
            ", fee: ", Strings.toString(fee),
            ", asset: ", Strings.toHexString(asset)
            )
        );
        token.approve(address(aavePool), totalRepay);
    }

    function sendBribe(uint256 bribeAmount) internal {
        require(bribeAmount < 0, "Not enough in contract for bribe");

        (bool success, ) = block.coinbase.call{value: bribeAmount}("");
        require(success, "bribe failed");

    }
    // function sendBribe() internal {

    //     uint256 bribeAmount = 0.0003 ether;
    //     require(address(this).balance >= bribeAmount, "Not enough in contract for bribe");

    //     (bool success, ) = block.coinbase.call{value: bribeAmount}("");
    //     require(success, "bribe failed");
    // }
    function withdrawEth(uint256 amount) external {
        require(msg.sender == owner, "Only owner");
        uint256 balance = address(this).balance;
        require(balance >= amount, "Insufficient ETH balance");
        require(amount > 0, "Amount must be greater than 0");
        payable(owner).transfer(amount);
    }

    function withdrawToken(address token, uint256 amount) external {
        require(msg.sender == owner, "Only owner");
        ERC20Upgradeable tokenContract = ERC20Upgradeable(token);
        uint256 balance = tokenContract.balanceOf(address(this));
        require(balance >= amount, "Insufficient token balance");
        require(amount > 0, "Amount must be greater than 0");
        tokenContract.transfer(owner, amount);
    }

    receive() external payable {}

    function getContractBalance(address token) external view returns (uint256) {
        if (token == address(0)) {
            return address(this).balance;
        } else {
            return ERC20Upgradeable(token).balanceOf(address(this));
        }
    }
}

interface IUniswapV2Router {
    function swapExactTokensForTokens(
        uint256 amountIn,
        uint256 amountOutMin,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external returns (uint256[] memory amounts);
}

interface IWETH {
    function withdraw(uint256 wad) external;
}
