contracts/JilaiCrowdsale/JilaiCrowdSale..content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.27;

import "@openzeppelin/contracts/interfaces/IERC20.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/ReentrancyGuardUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/PausableUpgradeable.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "../JilaiVesting/JilaiVesting.sol";

/**
 * @title JilaiCrowdSale
 * @notice A crowdsale contract for Jilai tokens in exchange for ETH across multiple stages
 * @dev Implements stage-based pricing, DEX unlock, vesting, and token forwarding using OpenZeppelin upgradeable contracts
 */
contract JilaiCrowdSale is
    Initializable,
    OwnableUpgradeable,
    ReentrancyGuardUpgradeable,
    UUPSUpgradeable,
    PausableUpgradeable
{
    IERC20 public jilaiToken;
    JilaiVesting public vestingContract;
    uint256 public vestingMonths;

    using SafeERC20 for IERC20;

    struct UserInfo {
        uint256 ethContributed;
        uint256 jilaiReceived;
        uint256 dexUnlockedTokens;
        uint256 vestedTokens;
    }

    struct Stage {
        uint256 rate; // Price in wei per Jilai token (18 decimals)
        uint256 tokensForSale; // Total tokens available in stage (18 decimals)
        uint256 dexUnlockPercent; // Percentage of tokens unlocked immediately
        uint256 tokensSold; // Number of tokens sold in stage (18 decimals)
    }

    mapping(address => UserInfo) public users;
    mapping(address => uint256) public investments; //Tracks ETH Contibutions in wei
    address[] public buyers;

    Stage[] public stages;
    uint256 public currentStage;
    uint256 public totalTokensSold;
    bool public isFinalized;
    uint256 public saleEndTime;
    uint256 private constant MINIMUM_TOKEN_AMOUNT = 1 * 10 ** 18;

    event TokensPurchased(
        address indexed buyer,
        uint256 indexed totalAmount,
        uint256 indexed dexUnlockedAmount,
        uint256 vestedAmount
    );
    event WithdrawToken(
        address indexed tokenContract,
        address indexed recipient,
        uint256 amount
    );
    event StageAdvanced(uint256 indexed newStage);
    event StageUpdated(
        uint256 indexed stageIndex,
        uint256 rate,
        uint256 tokensForSale,
        uint256 dexUnlockPercent
    );
    event RewardTokenUpdated(address indexed newTokenAddress);
    event AutoTokensAdded(uint256 _stageIndex, uint256 requiredTokens);
    event Finalized();
    event SaleEnded(uint256 indexed endTime);
    event TokensForwarded(
        uint256 indexed fromStage,
        uint256 indexed toStageOrOwner,
        uint256 amount
    );
    /**
     * @dev Modifier to make a function callable only while the sale is open.
     */
    modifier onlyWhileOpen() {
        require(!isFinalized, "Sale is finalized");
        require(currentStage < stages.length, "All stages completed");
        _;
    }

    /**
     * @notice Disables initializers to prevent contract from being initialized again.
     * @custom:oz-upgrades-unsafe-allow constructor
     */
    constructor() {
        _disableInitializers();
    }

    /**
     * @notice Authorizes an upgrade to a new contract implementation.
     * @dev Restricted to only the contract owner.
     * @param newImplementation Address of the new implementation contract.
     */
    function _authorizeUpgrade(
        address newImplementation
    ) internal override onlyOwner {}

    function initialize(
        address _jilaiToken,
        address _vestingContract
    ) public initializer {
        __Ownable_init(msg.sender);
        __ReentrancyGuard_init();
        __UUPSUpgradeable_init();
        __Pausable_init();

        require(_jilaiToken != address(0), "Invalid Jilai Token Address!");
        require(
            _vestingContract != address(0),
            "Invalid Vesting Contract Address"
        );

        jilaiToken = IERC20(_jilaiToken);
        vestingContract = JilaiVesting(_vestingContract);
        vestingMonths = 36; // Default vesting period is 36 months

        // 12 stages with adjusted prices
        stages.push(Stage(50000000000000000, 120000000 * 10 ** 18, 30, 0)); // Stage 1 (0.05 ETH)
        stages.push(Stage(70000000000000000, 110000000 * 10 ** 18, 32, 0)); // Stage 2 (0.07 ETH)
        stages.push(Stage(90000000000000000, 100000000 * 10 ** 18, 34, 0)); // Stage 3 (0.09 ETH)
        stages.push(Stage(110000000000000000, 100000000 * 10 ** 18, 36, 0)); // Stage 4 (0.11 ETH)
        stages.push(Stage(130000000000000000, 100000000 * 10 ** 18, 38, 0)); // Stage 5 (0.13 ETH)
        stages.push(Stage(160000000000000000, 90000000 * 10 ** 18, 40, 0)); // Stage 6 (0.16 ETH)
        stages.push(Stage(180000000000000000, 90000000 * 10 ** 18, 42, 0)); // Stage 7 (0.18 ETH)
        stages.push(Stage(210000000000000000, 90000000 * 10 ** 18, 44, 0)); // Stage 8 (0.21 ETH)
        stages.push(Stage(240000000000000000, 90000000 * 10 ** 18, 46, 0)); // Stage 9 (0.24 ETH)
        stages.push(Stage(270000000000000000, 70000000 * 10 ** 18, 48, 0)); // Stage 10 (0.27 ETH)
        stages.push(Stage(300000000000000000, 70000000 * 10 ** 18, 50, 0)); // Stage 11 (0.300 ETH)
        stages.push(Stage(330000000000000000, 70000000 * 10 ** 18, 52, 0)); // Stage 12 (0.330 ETH)

        currentStage = 0; // Start at Stage 1 (index 0)

        // //Add Tokens for the Stage automatically
        // _autoAddTokensForStage(0);
    }

    function updateStage(
        uint256 _stageIndex,
        uint256 _rate,
        uint256 _tokensForSale,
        uint256 _dexUnlockPercent
    ) external onlyOwner {
        require(_stageIndex < stages.length, "Invalid stage index");
        require(_rate > 0, "Rate must be greater than 0");
        require(_tokensForSale > 0, "Tokens For sale must be greater than 0");
        require(
            _dexUnlockPercent > 0 && _dexUnlockPercent <= 100,
            "Invalid DEX unlock Percentage"
        );

        Stage storage stage = stages[_stageIndex];
        stage.rate = _rate;
        stage.tokensForSale = _tokensForSale;
        stage.dexUnlockPercent = _dexUnlockPercent;

        emit StageUpdated(
            _stageIndex,
            _rate,
            _tokensForSale,
            _dexUnlockPercent
        );
    }

    /**
     * @notice Calculates the number of tokens a buyer will receive for a given USDT amount.
     * @param ethAmount The amount of USDT invested.
     * @return The amount of Jilai tokens received.
     */
    function _getTokenAmount(uint256 ethAmount) public view returns (uint256) {
        require(ethAmount >= 1000000000000000, "ETH amount too low");
        return (ethAmount * 10 ** 18) / stages[currentStage].rate;
    }

    /**
     * @notice Allows a user to buy Jilai tokens using ETH
     */
    function buyTokens()
        external
        payable
        nonReentrant
        onlyWhileOpen
        whenNotPaused
    {
        uint256 ethAmount = msg.value;
        require(ethAmount > 0, "ETH amount must be greater than 0");
        Stage storage stage = stages[currentStage];
        uint256 tokenAmount = _getTokenAmount(ethAmount);
        require(tokenAmount > 0, "Token amount must be greater than 0");

        uint256 remainingTokens = stage.tokensForSale - stage.tokensSold;
        require(remainingTokens > 0, "No Tokens Left in the Sale!");
        require(
            tokenAmount <= remainingTokens,
            "Requested amount exceeds stage token limit"
        );
        // Ensure contract has enough tokens
        require(
            jilaiToken.balanceOf(address(this)) >= tokenAmount,
            "Insufficient token balance"
        );

        // Calculate DEX unlock and vested amounts
        uint256 dexUnlockAmount = (tokenAmount * stage.dexUnlockPercent) / 100;
        uint256 vestingAmount = tokenAmount - dexUnlockAmount;

        stage.tokensSold += tokenAmount;
        totalTokensSold += tokenAmount;

        UserInfo storage user = users[msg.sender];
        if (user.ethContributed == 0) {
            buyers.push(msg.sender);
        }
        user.ethContributed += ethAmount;
        user.jilaiReceived += tokenAmount;
        user.dexUnlockedTokens += dexUnlockAmount;
        user.vestedTokens += vestingAmount;
        investments[msg.sender] += ethAmount;

        // Transfer DEX unlocked tokens directly to user
        jilaiToken.safeTransfer(msg.sender, dexUnlockAmount);

        // Transfer vested tokens to vesting contract
        jilaiToken.safeTransfer(address(vestingContract), vestingAmount);
        vestingContract.addTokenGrant(msg.sender, vestingAmount, false);

        emit TokensPurchased(
            msg.sender,
            tokenAmount,
            dexUnlockAmount,
            vestingAmount
        );
        remainingTokens = stage.tokensForSale - stage.tokensSold;
        if (remainingTokens < MINIMUM_TOKEN_AMOUNT) {
            if (remainingTokens > 0) {
                if (currentStage < stages.length - 1) {
                    stages[currentStage + 1].tokensForSale += remainingTokens;
                    emit TokensForwarded(
                        currentStage,
                        currentStage + 1,
                        remainingTokens
                    );
                } else {
                    jilaiToken.safeTransfer(owner(), remainingTokens);
                    emit TokensForwarded(currentStage, 0, remainingTokens);
                }
            }
            stage.tokensSold = stage.tokensForSale;
            if (currentStage < stages.length - 1) {
                currentStage++;
                emit StageAdvanced(currentStage);
            } else {
                isFinalized = true;
                saleEndTime = block.timestamp;
                _handleSaleEnd();
            }
        }
    }

    function _handleSaleEnd() private {
        uint256 monthTimeInSeconds = vestingContract.monthTimeInSeconds();
        for (uint256 i = 0; i < buyers.length; i++) {
            address buyer = buyers[i];
            if (users[buyer].vestedTokens > 0) {
                vestingContract.setGrantStartTime(
                    buyer,
                    saleEndTime + monthTimeInSeconds
                );
            }
        }

        uint256 remainingTokens = jilaiToken.balanceOf(address(this));
        if (remainingTokens > 0) {
            jilaiToken.safeTransfer(owner(), remainingTokens);
            emit TokensForwarded(currentStage, 0, remainingTokens);
        }

        emit Finalized();
        emit SaleEnded(saleEndTime);
    }

    function finalize() external onlyOwner {
        require(!isFinalized, "Sale Already Finalized!");
        require(currentStage >= stages.length - 1, "Not all stages completed!");

        isFinalized = true;
        saleEndTime = block.timestamp;
        _handleSaleEnd();
    }

    /**
     * @dev Change vesting in months.
     * @param vestingInMonths vesting in months
     */
    function _changeVestingInMonths(uint256 vestingInMonths) internal virtual {
        vestingMonths = vestingInMonths;
    }

    function getVestingMonths() public view returns (uint256) {
        return vestingMonths;
    }

    function getSaleEndTime() public view returns (uint256) {
        return saleEndTime;
    }

    function isSaleEnded() public view returns (bool, uint256) {
        return (isFinalized, saleEndTime);
    }


    function changeVestingInMonths(
        uint256 vestingInMonths
    ) external virtual onlyOwner onlyWhileOpen whenNotPaused {
        require(vestingInMonths > 0, "vestingInMonths cannot be 0");
        _changeVestingInMonths(vestingInMonths);
    }

    /**
     * @dev Pause the contract, preventing token purchases and transfers.
     * See {ERC20Pausable-_pause}.
     */
    function pauseContract() external virtual onlyOwner {
        _pause();
    }

    /**
     * @dev Unpause the contract, allowing token purchases and transfers to resume.
     * See {ERC20Pausable-_unpause}.
     */
    function unPauseContract() external virtual onlyOwner {
        _unpause();
    }

    function getCurrentStageDetails() external view returns (Stage memory) {
        return stages[currentStage];
    }

    /**
     * @dev Allows the owner to withdraw ERC-20 tokens from this contract.
     * @param _tokenContract The address of the ERC-20 token contract.
     * @param _amount The amount of tokens to withdraw.
     * @notice The '_tokenContract' address should not be the zero address.
     */
    function withdrawToken(
        address _tokenContract,
        uint256 _amount
    ) external onlyOwner nonReentrant {
        require(_tokenContract != address(0), "Address cant be zero address");
        IERC20 tokenContract = IERC20(_tokenContract);
        tokenContract.safeTransfer(msg.sender, _amount);
        emit WithdrawToken(_tokenContract, msg.sender, _amount);
    }

    function withdrawETH() external onlyOwner {
        uint256 balance = address(this).balance;
        require(balance > 0, "No ETH to Withdraw!");
        (bool success, ) = owner().call{value: balance}("");
        require(success, "ETH Transfer Failed!");
    }

    function setRewardToken(address _newJilaiToken) external onlyOwner {
        require(_newJilaiToken != address(0), "Invalid Token Address!");
        require(_newJilaiToken != address(jilaiToken), "Same Token Address");
        jilaiToken = IERC20(_newJilaiToken);

        emit RewardTokenUpdated(_newJilaiToken);
    }

    function getRewardToken() external view returns (address) {
        return address(jilaiToken);
    }

    //Stop sale function
    function stopSale() external onlyOwner {
        require(!isFinalized, "Sale Already Finalized!");

        isFinalized = true;
        saleEndTime = block.timestamp;
        _handleSaleEnd();
    }

    /**
     * @notice Returns the ETH balance of the contract
     * @return The ETH balance in wei
     */
    function getETHBalance() external view returns (uint256) {
        return address(this).balance;
    }
}

contracts/JilaiVesting/JilaiVesting.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.27;

import "@openzeppelin/contracts/interfaces/IERC20.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/ReentrancyGuardUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "../JilaiCrowdsale/JilaiCrowdSale.sol";

contract JilaiVesting is
    Initializable,
    OwnableUpgradeable,
    ReentrancyGuardUpgradeable,
    UUPSUpgradeable
{
    using SafeERC20 for IERC20;

    enum Role {
        None,
        Founder,
        User
    }
    struct Grant {
        uint256 startTime;
        uint256 amount;
        uint256 vestingDuration;
        uint256 monthsClaimed;
        uint256 totalClaimed;
        address recipient;
        Role role;
        uint256 cliffPeriod;
        bool isFounder;
    }
    event TokensVested(address indexed beneficiary, uint256 amount);
    event FounderAdded(
        address indexed founder,
        uint256 amount,
        uint256 vestingDuration,
        uint256 lockInPeriod
    );
    event GrantAdded(address indexed recipient);
    event GrantTokensClaimed(
        address indexed recipient,
        uint256 amountClaimed,
        uint256 vestingStartTime,
        uint256 currentTime,
        uint256 elapsedMonths
    );
    event MonthTimeUpdated(uint256 _intervalTime);
    event GrantRevoked(
        address recipient,
        uint256 amountVested,
        uint256 amountNotVested
    );

    event WithdrawToken(
        address indexed tokenContract,
        address indexed recipient,
        uint256 amount
    );

    IERC20 public jilaiToken;

    mapping(address => Grant) private tokenGrants;
    mapping(address => Role) public roles;
    address public crowdsale_address;
    uint256 public monthTimeInSeconds;

    event CrowdsaleAddressUpdated(
        address indexed previousAddress,
        address indexed newAddress
    );
    event VestingStarted(
        address recipient,
        uint256 startTime,
        uint256 saleEndTime
    );

    ///@custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    function initialize(address _jilaiToken) public initializer {
        require(_jilaiToken != address(0), "Invalid token address");
        __Ownable_init(msg.sender);
        __ReentrancyGuard_init();
        __UUPSUpgradeable_init();

        jilaiToken = IERC20(_jilaiToken);
        monthTimeInSeconds = 2629743 seconds; // Standard month duration
    }

    function _authorizeUpgrade(
        address newImplementation
    ) internal override onlyOwner {}

    modifier onlyCrowdsale() {
        require(
            msg.sender == crowdsale_address,
            "Only crowdsale contract can call this function"
        );
        _;
    }

    function addCrowdsaleAddress(
        address newCrowdsaleAddress
    ) external onlyOwner {
        require(newCrowdsaleAddress != address(0), "Invalid address");
        address previousCrowdsaleAddress = crowdsale_address;
        crowdsale_address = newCrowdsaleAddress;
        emit CrowdsaleAddressUpdated(
            previousCrowdsaleAddress,
            newCrowdsaleAddress
        );
    }

    // Fetch vestingMonths from JilaiCrowdSale
    function getVestingMonths() public view returns (uint256) {
        require(crowdsale_address != address(0), "Crowdsale address not set");
        JilaiCrowdSale crowdsale = JilaiCrowdSale(crowdsale_address);
        return crowdsale.vestingMonths();
    }

    function isSaleEnded() public view returns (bool, uint256) {
        JilaiCrowdSale crowdsale = JilaiCrowdSale(crowdsale_address);
        return (crowdsale.isFinalized(), crowdsale.getSaleEndTime());
    }

    function addTokenGrant(
        address _recipient,
        uint256 _amount,
        bool _isFounder
    ) external nonReentrant onlyCrowdsale {
        require(_recipient != address(0), "Invalid recipient address");
        require(_amount > 0, "Amount must be greater than 0");
        require(
            !tokenGrants[_recipient].isFounder && !_isFounder,
            "Founder can't able to participate."
        );

        uint256 vestingDuration = getVestingMonths();
        require(vestingDuration > 0, "Vesting months not set in crowdsale");

        Grant storage grant = tokenGrants[_recipient];

        if (grant.amount == 0) {
            // First time grant
            tokenGrants[_recipient] = Grant({
                startTime: 0,
                amount: _amount,
                vestingDuration: vestingDuration,
                monthsClaimed: 0,
                totalClaimed: 0,
                recipient: _recipient,
                role: Role.User,
                cliffPeriod: 0,
                isFounder: _isFounder
            });
        } else {
            // For additional purchases, add to the total amount
            grant.amount += _amount;
        }

        roles[_recipient] = Role.User;
        emit GrantAdded(_recipient);
    }

    function claimVestedTokens() external nonReentrant {
        (bool saleEnded, ) = isSaleEnded();
        require(saleEnded, "Sale has not ended yet!");
        Grant storage grant = tokenGrants[msg.sender];

        require(grant.amount > 0, "No grant exists");
        require(grant.startTime > 0, "Vesting not started");
        require(block.timestamp >= grant.startTime, "Vesting not started");

        uint256 monthsVested;
        uint256 amountVested;
        (monthsVested, amountVested) = calculateGrantClaim(msg.sender);

        require(amountVested > 0, "No tokens vested");

        grant.monthsClaimed += monthsVested;
        grant.totalClaimed += amountVested;

        jilaiToken.safeTransfer(msg.sender, amountVested);
        emit GrantTokensClaimed(
            msg.sender,
            amountVested,
            grant.startTime,
            block.timestamp,
            monthsVested
        );
    }

    function calculateGrantClaim(
        address _recipient
    ) public view returns (uint256, uint256) {
        Grant storage grant = tokenGrants[_recipient];
        (bool saleEnded, ) = isSaleEnded();

        if (
            !saleEnded ||
            grant.startTime == 0 ||
            block.timestamp < grant.startTime
        ) {
            return (0, 0);
        }
        // Calculate total elapsed months, capped at vesting duration
        uint256 elapsedMonths = ((block.timestamp - grant.startTime) /
            monthTimeInSeconds) + 1;
        elapsedMonths = elapsedMonths > grant.vestingDuration
            ? grant.vestingDuration
            : elapsedMonths;

        // If all months have elapsed, return remaining grant
        if (elapsedMonths >= grant.vestingDuration) {
            uint256 remainingGrant = grant.amount - grant.totalClaimed;
            return (
                grant.vestingDuration - grant.monthsClaimed,
                remainingGrant
            );
        }

        // Calculate vested months and amount
        uint256 monthsVested = elapsedMonths - grant.monthsClaimed;

        // For the final month, ensure we distribute remaining tokens
        if (grant.monthsClaimed + monthsVested >= grant.vestingDuration) {
            return (monthsVested, grant.amount - grant.totalClaimed);
        }

        uint256 amountVested = (monthsVested * grant.amount * 1e18) /
            grant.vestingDuration;
        amountVested /= 1e18;

        return (monthsVested, amountVested);
    }

    /**
     * @dev update the time gap between the distribution
     * @param _intervalTime update the time in seconds
     */
    function updateMonthsTime(
        uint256 _intervalTime
    ) external onlyOwner nonReentrant {
        require(_intervalTime > 0, "Interval time must be greater than 0");
        monthTimeInSeconds = _intervalTime;
        emit MonthTimeUpdated(monthTimeInSeconds);
    }

    function currentTime() private view returns (uint256) {
        return block.timestamp;
    }

    function addFounder(
        address _founder,
        uint256 _amount,
        uint256 _vestingDuration,
        uint256 _lockInPeriod
    ) external onlyOwner nonReentrant {
        require(_founder != address(0), "Invalid Founder Address");
        require(_amount > 0, "Amount must be greater than 0");
        require(_vestingDuration > 0, "Vesting Duration greater than 0");
        require(roles[_founder] == Role.None, "Address already has a role");
        require(tokenGrants[_founder].amount == 0, "Grant already exists");

        uint256 amountVestedPerMonth = _amount / _vestingDuration;
        require(amountVestedPerMonth > 0, "Amount too small for Vesting");

        tokenGrants[_founder] = Grant({
            startTime: currentTime() + _lockInPeriod + monthTimeInSeconds,
            amount: _amount,
            vestingDuration: _vestingDuration,
            monthsClaimed: 0,
            totalClaimed: 0,
            recipient: _founder,
            role: Role.Founder,
            cliffPeriod: _lockInPeriod,
            isFounder: true
        });

        roles[_founder] = Role.Founder;

        emit FounderAdded(_founder, _amount, _vestingDuration, _lockInPeriod);
    }

    function isFounder(address founder) external view returns (bool) {
        Grant storage tokenGrant = tokenGrants[founder];
        return tokenGrant.isFounder;
    }

    function getGrantDetails(
        address _recipient
    )
        external
        view
        returns (
            uint256 amount,
            uint256 vestingDuration,
            uint256 startTime,
            uint256 monthsClaimed,
            uint256 totalClaimed,
            uint256 remainingAmount,
            uint256 nextClaim,
            Role role,
            uint256 cliffPeriod
        )
    {
        Grant storage grant = tokenGrants[_recipient];
        require(grant.amount > 0, "Grant does not exist");
        uint256 _nextClaim = 0; // Default to 0
        if (grant.startTime != 0 && grant.totalClaimed < grant.amount) {
            // Grant exists and vesting incomplete
            uint256 startTimeOfUser = grant.startTime;
            uint256 finalDate = startTimeOfUser +
                ((grant.vestingDuration - 1) * monthTimeInSeconds);

            if (block.timestamp < startTimeOfUser) {
                _nextClaim = startTimeOfUser; // Vesting hasn’t started
            } else if (block.timestamp <= finalDate) {
                // Find next claim date within vesting period
                while (startTimeOfUser <= block.timestamp) {
                    startTimeOfUser += monthTimeInSeconds;
                }
                _nextClaim = startTimeOfUser;
            }
            // If block.timestamp > finalDate, remaining tokens are claimable now, so no next claim (stays 0)
        }

        return (
            grant.amount,
            grant.vestingDuration,
            grant.startTime,
            grant.monthsClaimed,
            grant.totalClaimed,
            grant.amount - grant.totalClaimed,
            _nextClaim,
            grant.role,
            grant.cliffPeriod
        );
    }

    function isVestingComplete(
        address _recipient
    ) external view returns (bool) {
        Grant storage grant = tokenGrants[_recipient];
        return grant.totalClaimed >= grant.amount && grant.amount > 0;
    }

    function getRole(address user) external view returns (Role) {
        require(user != address(0), "Invalid user address");
        return roles[user];
    }

    // New function added
    function getVestingTiming(
        address _recipient
    )
        external
        view
        returns (
            uint256 startTime,
            uint256 currenttime,
            uint256 elapsedTime,
            uint256 elapsedMonths,
            bool hasStarted
        )
    {
        Grant storage grant = tokenGrants[_recipient];
        if (grant.amount == 0) return (0, 0, 0, 0, false);

        currenttime = block.timestamp;
        startTime = grant.startTime;
        hasStarted = startTime != 0 && currenttime >= startTime;

        if (hasStarted) {
            elapsedTime = currenttime - startTime;
            elapsedMonths = (elapsedTime / monthTimeInSeconds) + 1;
        }

        return (startTime, currenttime, elapsedTime, elapsedMonths, hasStarted);
    }

    function setGrantStartTime(
        address _recipient,
        uint256 _startTime
    ) external onlyCrowdsale {
        require(_recipient != address(0), "Invalid recipient address");
        require(_startTime > 0, "Invalid start time");
        Grant storage grant = tokenGrants[_recipient];
        require(grant.amount > 0, "No grant exists");
        grant.startTime = _startTime;
        emit VestingStarted(_recipient, _startTime, _startTime);
    }

    /**
     * @dev Allows the owner to withdraw ERC-20 tokens from this contract.
     * @param _tokenContract The address of the ERC-20 token contract.
     * @param _amount The amount of tokens to withdraw.
     * @notice The '_tokenContract' address should not be the zero address.
     */
    function withdrawToken(
        address _tokenContract,
        uint256 _amount
    ) external onlyOwner nonReentrant {
        require(_tokenContract != address(0), "Address cant be zero address");
        IERC20 tokenContract = IERC20(_tokenContract);
        tokenContract.safeTransfer(msg.sender, _amount);
        emit WithdrawToken(_tokenContract, msg.sender, _amount);
    }
}
