// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@aave/core-v3/contracts/flashloan/base/FlashLoanSimpleReceiverBase.sol";
import "@aave/core-v3/contracts/interfaces/IPoolAddressesProvider.sol";
import "@uniswap/v2-periphery/contracts/interfaces/IUniswapV2Router02.sol";

contract FlashloanArbitrage is FlashLoanSimpleReceiverBase, Ownable {
    using SafeERC20 for IERC20;

    // DEX router addresses
    address public immutable uniswapRouter;
    address public immutable sushiswapRouter;

    // Token addresses
    address public immutable weth;
    address public immutable usdc;
    address public immutable dai;

    // Events
    event ArbitrageExecuted(
        address token,
        address buyDex,
        address sellDex,
        uint256 amount,
        uint256 profit,
        uint256 gasCost,
        uint256 netProfit
    );

    constructor(
        address _addressProvider,
        address _uniswapRouter,
        address _sushiswapRouter,
        address _weth,
        address _usdc,
        address _dai
    ) FlashLoanSimpleReceiverBase(IPoolAddressesProvider(_addressProvider)) Ownable(msg.sender) {
        uniswapRouter = _uniswapRouter;
        sushiswapRouter = _sushiswapRouter;
        weth = _weth;
        usdc = _usdc;
        dai = _dai;
    }

    function executeArbitrage(
        uint256 amount,
        address buyDex,
        address sellDex
    ) external onlyOwner {
        require(
            buyDex == uniswapRouter || buyDex == sushiswapRouter,
            "Invalid buy DEX"
        );
        require(
            sellDex == uniswapRouter || sellDex == sushiswapRouter,
            "Invalid sell DEX"
        );
        require(buyDex != sellDex, "Buy and sell DEX must be different");

        // Execute flash loan
        POOL.flashLoanSimple(
            address(this),
            weth,
            amount,
            abi.encode(buyDex, sellDex),
            0
        );
    }

    function executeOperation(
        address asset,
        uint256 amount,
        uint256 premium,
        address initiator,
        bytes calldata params
    ) external override returns (bool) {
        require(msg.sender == address(POOL), "Caller must be pool");
        require(initiator == address(this), "Initiator must be this contract");
        require(asset == weth, "Asset must be WETH");

        // Decode parameters
        (address buyDex, address sellDex) = abi.decode(params, (address, address));

        // Record initial balance
        uint256 initialBalance = IERC20(weth).balanceOf(address(this));

        // Approve DEXs to spend WETH
        IERC20(weth).approve(buyDex, amount);
        IERC20(weth).approve(sellDex, amount);

        // Get USDC path
        address[] memory path = new address[](2);
        path[0] = weth;
        path[1] = usdc;

        // Swap WETH to USDC on buy DEX
        IUniswapV2Router02(buyDex).swapExactTokensForTokens(
            amount,
            0, // Accept any amount of USDC
            path,
            address(this),
            block.timestamp
        );

        // Get USDC balance
        uint256 usdcBalance = IERC20(usdc).balanceOf(address(this));

        // Approve USDC spending
        IERC20(usdc).approve(sellDex, usdcBalance);

        // Swap back to WETH on sell DEX
        address[] memory reversePath = new address[](2);
        reversePath[0] = usdc;
        reversePath[1] = weth;

        IUniswapV2Router02(sellDex).swapExactTokensForTokens(
            usdcBalance,
            0, // Accept any amount of WETH
            reversePath,
            address(this),
            block.timestamp
        );

        // Calculate profit
        uint256 finalBalance = IERC20(weth).balanceOf(address(this));
        uint256 profit = finalBalance - initialBalance;

        // Calculate gas cost (approximate)
        uint256 gasCost = tx.gasprice * (gasleft() + 50000); // Add buffer for remaining operations
        uint256 netProfit = profit > gasCost ? profit - gasCost : 0;

        // Approve repayment
        uint256 amountToRepay = amount + premium;
        require(finalBalance >= amountToRepay, "Insufficient balance for repayment");
        IERC20(asset).approve(address(POOL), amountToRepay);

        emit ArbitrageExecuted(
            weth,
            buyDex,
            sellDex,
            amount,
            profit,
            gasCost,
            netProfit
        );

        return true;
    }

    // Emergency functions
    function withdrawToken(address token, uint256 amount) external onlyOwner {
        IERC20(token).transfer(owner(), amount);
    }

    function withdrawETH() external onlyOwner {
        payable(owner()).transfer(address(this).balance);
    }
}
