// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import { SafeTransferLib } from "solady/utils/SafeTransferLib.sol";
import { IRWAERC20 } from "./interfaces/IRWAERC20.sol";
import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import { EIP712 } from "solady/utils/EIP712.sol";
import { IERC20Metadata } from "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";

import { IRWAAirdrop } from "./interfaces/IRWAAirdrop.sol";
import { IAssetPool } from "./interfaces/IAssetPool.sol";

import { OwnableUpgradeable } from "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";

import { ECDSA } from "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";

//import "forge-std/test.sol";

contract RWAAirdrop is IRWAAirdrop, OwnableUpgradeable, EIP712 {
  bytes32 internal constant _CLAIM_TYPEHASH =
    keccak256("Claim(uint256 amount,address receiver,address token,uint256 nonce)");

  address public signer;

  address public pool;

  mapping(address => uint256) private _nonces;

  function initialize(address _signer, address _pool) public initializer {
    __Ownable_init(msg.sender);
    signer = _signer;
    pool = _pool;
  }

  function claim(uint256 amount, address token, bytes memory signature) public {
    bytes32 digest = _hashTypedData(
      keccak256(abi.encode(_CLAIM_TYPEHASH, amount, msg.sender, token, _useNonce(msg.sender)))
    );
    address recoveredAddress = ECDSA.recover(digest, signature);
    require(recoveredAddress == signer, "Invalid signature");
    IAssetPool(pool).withdrawToken(token, msg.sender, amount);
    emit RewardClaimed(msg.sender, amount, token);
  }

  function _domainNameAndVersion() internal pure override returns (string memory name, string memory version) {
    name = "DTTAirdrop";
    version = "1";
  }

  function nonces(address owner) public view returns (uint256) {
    return _nonces[owner];
  }

  function setSigner(address _signer) public onlyOwner {
    signer = _signer;
  }

  function setPool(address _pool) public onlyOwner {
    pool = _pool;
  }

  function incNonce(address account) public onlyOwner {
    _nonces[account]++;
  }

  /**
   * @dev "Consume a nonce": return the current value and increment.
   *
   * _Available since v4.1._
   */
  function _useNonce(address owner) internal virtual returns (uint256 current) {
    current = _nonces[owner];
    _nonces[owner]++;
  }
}

