contracts/IMinutesNetworkToken.sol.content:
// SPDX-License-Identifier: MIT
// Compatible with OpenZeppelin Contracts ^5.0.0
pragma solidity ^0.8.22;

import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {IERC165} from "@openzeppelin/contracts/utils/introspection/IERC165.sol";

interface IMinutesNetworkToken is IERC20, IERC165
{
    // Node definition: each node is associated with an IMinutesNetworkNode contract.
    struct Node {
        address addr;
        string name;
        uint256 minStake;
    }

    // Struct to track node staking.
    struct StakingInfo {
        uint256 amount;
        uint256 lowest;  // lowest stake recorded since the epoch started.
        uint256 lowestEpoch; // epoch when "lowest" was last updated.
    }

    function nodes(uint256 _nodeIndex) external view returns (address, string memory, uint256);
}

contracts/MinutesNetworkTokenUpgrade-v1..content:
// SPDX-License-Identifier: MIT
// Compatible with OpenZeppelin Contracts ^5.0.0
pragma solidity ^0.8.22;

import {AccessControlUpgradeable} from "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import {ERC20Upgradeable} from "@openzeppelin/contracts-upgradeable/token/ERC20/ERC20Upgradeable.sol";
import {ERC20BurnableUpgradeable} from "@openzeppelin/contracts-upgradeable/token/ERC20/extensions/ERC20BurnableUpgradeable.sol";
import {ERC20PermitUpgradeable} from "@openzeppelin/contracts-upgradeable/token/ERC20/extensions/ERC20PermitUpgradeable.sol";
import {Initializable} from "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import {UUPSUpgradeable} from "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import {ERC165} from "@openzeppelin/contracts/utils/introspection/ERC165.sol";
import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {IERC165} from "@openzeppelin/contracts/utils/introspection/IERC165.sol";
import {MerkleProof} from "@openzeppelin/contracts/utils/cryptography/MerkleProof.sol";
import {Strings} from "@openzeppelin/contracts/utils/Strings.sol";
import {IMinutesNetworkToken} from "./IMinutesNetworkToken.sol";
import {IMinutesNetworkNode} from "./IMinutesNetworkNode.sol";
import {StakingInfoMap} from "./StakingInfoMap.sol";

contract MinutesNetworkToken_v1_01_00 is
Initializable,
ERC20Upgradeable,
ERC20BurnableUpgradeable,
AccessControlUpgradeable,
ERC20PermitUpgradeable,
UUPSUpgradeable,
IMinutesNetworkToken
{
    using StakingInfoMap for StakingInfoMap.AddressMap;

    // Role definitions.
    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");
    bytes32 public constant EPOCH_FINALIZER_ROLE = keccak256("EPOCH_FINALIZER_ROLE");

    // Supply cap constants and types.
    uint256 public constant MAX_SUPPLY = 500_000_000 * 1e18;
    enum Funds {
        Operational,
        NetworkExpansion,
        Team,
        Advisors
    }
    struct FundAllocation {
        uint256 maxSupply;
        uint256 minted;
    }

    // Public stake constants
    uint256 public constant PUBLIC_NODE_INDEX = 0;
    uint256 public constant PUBLIC_NODE_TOKEN_ID = 0;

    // Internal constants.
    bytes private constant EPOCH_REWARD_PREFIX = bytes("epoch_"); // ensure no collisions for epoch reward identifier hashes
    bytes4 private constant nodeInterfaceId = type(IMinutesNetworkNode).interfaceId;

    // Reward pool address.
    address public rewardPool;

    // Epoch and epoch reward management.
    uint256 public currentEpoch;
    uint256 public nextEpochTimestamp;
    uint256 public rewardExpiryDuration;

    // Supply cap tracking.
    mapping(Funds => FundAllocation) public allocations;

    // Nodes that can be staked against using MNT.
    Node[] public nodes;

    // Node staking: tracked by node index and token ID.
    mapping(uint256 => mapping(uint256 => StakingInfo)) public nodeStakes;

    // Public staking: by address.
    StakingInfoMap.AddressMap internal publicStakes;

    // Struct to hold information about a reward set.
    struct Reward {
        bytes rewardId;
        uint256 unlockTime; // restricts claiming a reward until the given timestamp.
        uint256 totalRewards;
        uint256 rewardsGiven;
        uint256 expiry; // if 0, never expires.
        bytes32 merkleRoot;
        // Mapping to track claimed rewards (each leaf is marked claimed).
        mapping(bytes32 => bool) claimed;
        uint256 claimedCount;
    }
    // Rewards stored by a unique hash identifier generated from the rewardId.
    mapping(bytes32 => Reward) private rewards;

    // Struct used for batch reward claims.
    struct ClaimData {
        bytes32 rewardHash;
        uint256 nonce;
        address account;
        uint256 amount;
        bytes32[] merkleProof;
        address to;
        bool autoStake;
        uint256 nodeIndex;
        uint256 tokenId;
    }

    // Generated when tokens are allocated for a epoch.
    event EpochRewardsAllocated(uint256 indexed epoch, bytes32 rewardHash, uint256 totalRewards, uint256 burned);
    // Generated when a reward is manually added.
    event RewardAllocated(bytes32 rewardHash);
    // Generated when a non-staking reward is claimed.
    event RewardClaimed(bytes32 indexed rewardHash, uint256 indexed nonce, address account, uint256 amount);
    // Generated when the epoch reward expiry date is updated.
    event NewEpochRewardDuration(uint256 secs);
    // Generated when tokens are minted from an allocation
    event FundAllocationMinted(Funds indexed fund, address to, uint256 amount);
    // Generated when a stake against a node increases.
    event StakeIncreased(uint256 indexed nodeIndex, uint256 indexed tokenId, uint256 amount, uint256 totalStaked);
    // Generated when a stake against a node decreases.
    event StakeDecreased(uint256 indexed nodeIndex, uint256 indexed tokenId, uint256 amount, uint256 totalStaked);
    // Generated when a new Node contract is added to the token.
    event NodeAdded(uint256 nodeIndex, address addr, string name, uint256 minStake);
    // Generated when a Node contract is updated.
    event NodeUpdated(uint256 nodeIndex, address addr, string name, uint256 minStake);

    error InvalidNode();
    error InvalidEpochTimestamp();
    error EpochNotEnded();
    error RewardPoolInsufficientBalance();
    error RewardExists();
    error RewardNotExists();
    error RewardLocked();
    error RewardExpired();
    error RewardAlreadyClaimed();
    error LowBalance();
    error InvalidProof();
    error AutoStakeNotAllowed();
    error BadSender();
    error InvalidStake();
    error InvalidNodeIndex();
    error InvalidTokenID();
    error NotOwner();
    error NotANode();
    error FundAllocated();
    error ExceedsMaxSupply();
    error BadID();
    error ReservedID();

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    /**
     * @notice UUPS initializer in place of the constructor.
     *
     * @param _name ERC20 token name.
     * @param _symbol ERC20 token symbol.
     * @param _rewardPoolAddress The address that will contain the reward tokens.
     * @param _rewardExpiryDuration The time in seconds that an epoch reward will be claimable.
     * @param _validationNode The node to assign as the validation node - used for public staking.
     */
    function initialize(
        string memory _name,
        string memory _symbol,
        address _rewardPoolAddress,
        uint256 _rewardExpiryDuration,
        Node memory _validationNode
    )
    initializer public
    {
        __ERC20_init(_name, _symbol);
        __ERC20Burnable_init();
        __AccessControl_init();
        __ERC20Permit_init(_name);
        __UUPSUpgradeable_init();

        // Set up roles.
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(MINTER_ROLE, msg.sender);
        _grantRole(EPOCH_FINALIZER_ROLE, msg.sender);

        // Set up "constructor" variables
        rewardPool = _rewardPoolAddress;
        currentEpoch = 0;
        nextEpochTimestamp = block.timestamp;
        rewardExpiryDuration = _rewardExpiryDuration;

        // Ensure the node matches the expected interface.
        if (!IMinutesNetworkNode(_validationNode.addr).supportsInterface(nodeInterfaceId)) {
            revert InvalidNode();
        }
        // Reserve node index 0 for the Validation node.
        nodes.push(_validationNode);
    }

    /**
     * @notice Updates the reward pool address.
     *
     * @param _newRewardPool The new reward pool address.
     */
    function setRewardPool(address _newRewardPool) public onlyRole(DEFAULT_ADMIN_ROLE) {
        rewardPool = _newRewardPool;
    }

    /**
     * @notice Updates the reward expiry duration.
     *
     * @param _newDuration The new duration, in seconds, of the reward validity period.
     */
    function setRewardExpiryDuration(uint256 _newDuration) public onlyRole(DEFAULT_ADMIN_ROLE) {
        rewardExpiryDuration = _newDuration;
        emit NewEpochRewardDuration(_newDuration);
    }

    /**
     * @notice Gets the list of nodes that can be staked against.
     *
     * @return An array containing the nodes  that can be staked against.
     */
    function getNodes() public view returns (Node[] memory) {
        return nodes;
    }

    /**
     * @notice Finalizes the current epoch.
     * - Increments the epoch counter.
     * - Burns the specified rewardAmount from the reward pool.
     * - Records the provided merkleRoot in a new RewardInfo struct.
     * - Sets the next epoch finalization time.
     * - Emits a EpochRewardsAllocated event.
     *
     * @param _rewardAmount The maximum amount that will be mintable for this reward set.
     * @param _burnAmount The amount of tokens that will be burned for this epoch.
     * @param _merkleRoot The merkle root of the reward set.
     * @param _nextEpochTimestamp The earliest date the next epoch can be finalized.
     */
    function finalizeEpoch(
        uint256 _rewardAmount,
        uint256 _burnAmount,
        bytes32 _merkleRoot,
        uint256 _nextEpochTimestamp
    ) public onlyRole(EPOCH_FINALIZER_ROLE) {
        if (block.timestamp >= _nextEpochTimestamp) {
            revert InvalidEpochTimestamp();
        }
        if (block.timestamp < nextEpochTimestamp) {
            revert EpochNotEnded();
        }

        uint256 _toBurn = _rewardAmount + _burnAmount;
        if (this.balanceOf(rewardPool) < _toBurn) {
            revert RewardPoolInsufficientBalance();
        }

        // Generate the reward hash identifier for the current epoch.
        bytes memory _rewardId = abi.encodePacked(EPOCH_REWARD_PREFIX, Strings.toString(currentEpoch));
        bytes32 _rewardHash = _generateRewardHash(_rewardId, true);

        // Burn the reward amount from the reward pool.
        _burn(rewardPool, _toBurn);

        // Initialize the reward info.
        Reward storage _info = rewards[_rewardHash];
        _info.rewardId = _rewardId;
        _info.unlockTime = 0; // There is no need to lock the rewards to a future date.
        _info.totalRewards = _rewardAmount;
        _info.expiry = block.timestamp + rewardExpiryDuration;
        _info.merkleRoot = _merkleRoot;
        _info.rewardsGiven = 0;

        emit EpochRewardsAllocated(currentEpoch, _rewardHash, _rewardAmount, _burnAmount);

        // Increment the epoch and set the next timestamp.
        currentEpoch++;
        nextEpochTimestamp = _nextEpochTimestamp;
    }

    /**
     * @notice Add a new reward entry.
     * @param _rewardId The unique reward ID.
     * @param _totalRewards Total reward tokens allocated.
     * @param _unlockTime When the reward becomes claimable.
     * @param _expiry Expiry timestamp (if 0 then never expires).
     * @param _merkleRoot The merkle root of the reward set
     */
    function addReward(
        string calldata _rewardId,
        uint256 _totalRewards,
        uint256 _unlockTime,
        uint256 _expiry,
        bytes32 _merkleRoot
    ) public onlyRole(MINTER_ROLE) {
        // Compute a unique reward hash identifier. Prevent epoch-reserved award IDs.
        bytes32 _rewardHash = _generateRewardHash(bytes(_rewardId), false);

        // Ensure no reward exists for this rewardHash.
        Reward storage r = rewards[_rewardHash];
        if (r.merkleRoot != 0) {
            revert RewardExists();
        }

        // Initialize the reward data.
        r.rewardId = bytes(_rewardId);
        r.unlockTime = _unlockTime;
        r.totalRewards = _totalRewards;
        r.rewardsGiven = 0;
        r.expiry = _expiry;
        r.merkleRoot = _merkleRoot;
        emit RewardAllocated(_rewardHash);
    }

    /**
     * @notice Allows for starting or ending a reward set outside of the planned schedule.
     *
     * @param _rewardId The unique reward ID.
     * @param _expiry The new expiry timestamp.
     */
    function updateRewardExpiry(string calldata _rewardId, uint256 _expiry) public onlyRole(MINTER_ROLE) {
        // Grab the reward has and ensure it's not staking rewards being updated.
        bytes32 _rewardHash = _generateRewardHash(bytes(_rewardId), false);
        Reward storage _info = rewards[_rewardHash];
        if (_info.merkleRoot == 0) {
            revert RewardNotExists();
        }
        // Update the expiry.
        _info.expiry = _expiry;
    }

    /**
     * @notice Retrieves reward info for the given reward set.
     *
     * @param _rewardId The unique reward ID.
     * @return The reward set unlock time.
     * @return The total rewards in this set.
     * @return The amount of rewards minted.
     * @return The expiry timestamp for this reward set.
     * @return The merkle root of this reward set.
     * @return The number of rewards claimed for this reward set.
     */
    function reward(string calldata _rewardId)
    public
    view
    returns (uint256, uint256, uint256, uint256, bytes32, uint256)
    {
        bytes32 _rewardHash = _generateRewardHash(bytes(_rewardId), true);
        Reward storage _info = rewards[_rewardHash];
        if (_info.merkleRoot == 0) {
            revert RewardNotExists();
        }
        return (
            _info.unlockTime,
            _info.totalRewards,
            _info.rewardsGiven,
            _info.expiry,
            _info.merkleRoot,
            _info.claimedCount
        );
    }

    /**
     * @notice Overloaded to get reward info by hash.
     *
     * @param _rewardHash The hash of the reward.
     * @return The reward set unlock time.
     * @return The total rewards in this set.
     * @return The amount of rewards minted.
     * @return The expiry timestamp for this reward set.
     * @return The merkle root of this reward set.
     * @return The number of rewards claimed for this reward set.
     */
    function reward(bytes32 _rewardHash)
    public
    view
    returns (uint256, uint256, uint256, uint256, bytes32, uint256)
    {
        Reward storage _info = rewards[_rewardHash];
        if (_info.merkleRoot == 0) {
            revert RewardNotExists();
        }
        return (
            _info.unlockTime,
            _info.totalRewards,
            _info.rewardsGiven,
            _info.expiry,
            _info.merkleRoot,
            _info.claimedCount
        );
    }

    /**
     * @notice Claim a mint from a reward set.
     *
     * @param _rewardHash The hashed reward identifier.
     * @param _nonce The unique nonce for this reward.
     * @param _account The account of the node staker.
     * @param _amount The reward amount.
     * @param _merkleProof The merkle proof validating this reward.
     * @param _to The address that should receive the reward.
     */
    function claim(
        bytes32 _rewardHash,
        uint256 _nonce,
        address _account,
        uint256 _amount,
        bytes32[] calldata _merkleProof,
        address _to
    ) public {
        _claimReward(_rewardHash, _nonce, _account, _amount, _merkleProof, _to, false, 0, 0);
    }

    /**
     * @notice Claim and auto-stake minted epoch rewards.
     *
     * @param _rewardHash The hashed reward identifier.
     * @param _nonce The unique nonce for this reward.
     * @param _account The account to receive the reward.
     * @param _amount The reward amount.
     * @param _merkleProof The merkle proof validating this reward.
     * @param _nodeIndex The node index.
     * @param _tokenId The token ID associated with the node.
     */
    function claimAndStake(
        bytes32 _rewardHash,
        uint256 _nonce,
        address _account,
        uint256 _amount,
        bytes32[] calldata _merkleProof,
        uint256 _nodeIndex,
        uint256 _tokenId
    ) public {
        // Claim the rewards and have them minted to the contract and increase stake.
        _claimReward(_rewardHash, _nonce, _account, _amount, _merkleProof, address(this), true, _nodeIndex, _tokenId);
    }

    /**
     * @notice Claim multiple rewards in a single transaction. Saves gas when a user wants to make multiple claims.
     *         Note: If this transaction reverts it can incur a large amount of gas if claiming many rewards. Use with
     *         Caution.
     *
     * @param _claims An array of ClaimData structs.
     */
    function claimAll(ClaimData[] calldata _claims) public {
        for (uint256 i = 0; i < _claims.length; i++) {
            ClaimData calldata c = _claims[i];
            if (c.autoStake) {
                _claimReward(
                    c.rewardHash,
                    c.nonce,
                    c.account,
                    c.amount,
                    c.merkleProof,
                    address(this),
                    c.autoStake,
                    c.nodeIndex,
                    c.tokenId
                );
            } else {
                _claimReward(c.rewardHash, c.nonce,c.account, c.amount, c.merkleProof, c.to, false, 0, 0);
            }
        }
    }

    /**
     * @dev Internal function to claim reward mints.
     *
     * @param _rewardHash The hashed reward identifier.
     * @param _nonce The unique nonce for this reward.
     * @param _account The this reward was generated for.
     * @param _amount The reward amount.
     * @param _merkleProof The merkle proof validating this reward.
     * @param _to The address that should receive the reward.
     * @param _stake Whether to auto-stake the rewards (only Epoch rewards).
     * @param _nodeIndex The node index.
     * @param _tokenId The token ID associated with the node.
     */
    function _claimReward(
        bytes32 _rewardHash,
        uint256 _nonce,
        address _account,
        uint256 _amount,
        bytes32[] calldata _merkleProof,
        address _to,
        bool _stake,
        uint256 _nodeIndex,
        uint256 _tokenId
    ) internal {
        // Ensure only the original account is claiming this reward.
        if (msg.sender != _account) {
            revert BadSender();
        }
        Reward storage _info = rewards[_rewardHash];
        // Make sure reward set exists.
        if (_info.merkleRoot == 0) {
            revert RewardNotExists();
        }
        // Make sure the rewards can be claimed.
        if (block.timestamp < _info.unlockTime) {
            revert RewardLocked();
        }
        // If the rewards expire, ensure they are still valid.
        if (_info.expiry != 0 && block.timestamp > _info.expiry) {
            revert RewardExpired();
        }
        // For non–node rewards, leafs omit the node information.
        bytes32 _leaf = keccak256(bytes.concat(keccak256(abi.encode(_rewardHash, _nonce, _account, _amount))));
        // Ensure the reward hasn't been claimed yet.
        if (_info.claimed[_leaf]) {
            revert RewardAlreadyClaimed();
        }
        // Verify the leaf and proof against the root.
        if (!MerkleProof.verify(_merkleProof, _info.merkleRoot, _leaf)) {
            revert InvalidProof();
        }
        // Mark the reward as claimed.
        _info.claimed[_leaf] = true;
        _info.claimedCount++;
        // Ensure we aren't giving out more rewards than allocated.
        uint256 _newRewardsGiven = _info.rewardsGiven + _amount;
        if (_newRewardsGiven > _info.totalRewards) {
            revert LowBalance();
        }
        // Update the total rewards given.
        _info.rewardsGiven = _newRewardsGiven;
        if (_stake) {
            // Make sure it's an epoch reward.
            if (!_hasEpochRewardPrefix(_info.rewardId)) {
                revert AutoStakeNotAllowed();
            }
            // Make sure the account still owns the node, or it's a public node.
            _requireSenderOwnsNode(_nodeIndex, _tokenId);
            // Increase stake, applying rewards to current epoch.
            // Handle the public node separately to allow for mintToNodeStake.
            if (_nodeIndex == PUBLIC_NODE_INDEX && _tokenId == PUBLIC_NODE_TOKEN_ID) {
                _increasePublicStake(msg.sender, _amount, true);
            }
            _increaseStake(_nodeIndex, _tokenId, _amount, true);
        }
        // Mint the rewards to the account.
        _mint(_to, _amount);
        emit RewardClaimed(_rewardHash, _nonce, _account, _amount);
    }

    /**
     * @notice Returns the staking information for the given node.
     *
     * @param _nodeIndex The node index.
     * @param _tokenId The token ID associated with the node.
     * @return Struct containing the staking information.
     */
    function getStake(uint256 _nodeIndex, uint256 _tokenId) public view returns (StakingInfo memory) {
        _requireValidTokenId(_nodeIndex, _tokenId);
        return nodeStakes[_nodeIndex][_tokenId];
    }

    /**
     * @notice Returns public stake information for the given address.
     *
     * @param _account The address to check.
     * @return Struct containing the public staking information.
     */
    function getPublicStake(address _account) public view returns (StakingInfo memory) {
        (, StakingInfo memory _info) = publicStakes.tryGet(_account);
        return _info;
    }

    /**
     * @notice Returns all addresses that have tokens staked against the public node.
     *
     * @return An array of addresses with tokens staked against the public node.
     */
    function getPublicStakeAddresses() public view returns (address[] memory) {
        return publicStakes.keys();
    }

    /**
     * @notice Increase the staking amount on the specified node. Staked tokens will be transferred from the caller to
     *         the contract address for holding. You may only stake nodes that you own.
     *
     * @param _nodeIndex The node index. Use 0 for the public node.
     * @param _tokenId The token ID associated with the node. Use 0 for the public node.
     * @param _amount The amount to be staked.
     */
    function increaseStake(uint256 _nodeIndex, uint256 _tokenId, uint256 _amount) public {
        // Require the staker to be the owner unless it is the public node
        _requireSenderOwnsNode(_nodeIndex, _tokenId);
        // Transfer the staked tokens to the contract.
        _transfer(msg.sender, address(this), _amount);
        // Increase the stake, and do not count towards current epoch rewards
        // Handle the public node separately to allow for mintToNodeStake.
        if (_nodeIndex == PUBLIC_NODE_INDEX && _tokenId == PUBLIC_NODE_TOKEN_ID) {
            _increasePublicStake(msg.sender, _amount, false);
        }
        _increaseStake(_nodeIndex, _tokenId, _amount, false);
    }

    /**
     * @notice Decrease the staking amount on the specified node. Nodes have a minimum stake that they cannot be staked
     *         below unless fully unstaked. The public node does not have this limitation. Unstaked tokens will be
     *         transferred from the contract address back to the owner. You may only unstake nodes that you own.
     *
     * @param _nodeIndex The node index. Use 0 for the public node.
     * @param _tokenId The token ID associated with the node. Use 0 for the public node.
     * @param _amount The amount to be staked.
     */
    function decreaseStake(uint256 _nodeIndex, uint256 _tokenId, uint256 _amount) public {
        // Require the staker to be the owner unless it is the public node
        _requireSenderOwnsNode(_nodeIndex, _tokenId);
        // Decrease stake
        _decreaseStake(_nodeIndex, _tokenId, _amount);
        // Transfer the staked tokens back to the owner.
        _transfer(address(this), msg.sender, _amount);
    }

    /**
     * @dev Internal function to increase staking on a node.
     *
     * @custom:changelog v1.00.01 - Nodes owned by the contract are transferred to the staker upon staking.
     *
     * @param _nodeIndex The node index.
     * @param _tokenId The token ID associated with the node.
     * @param _amount The amount to stake.
     * @param _applyToCurrentEpoch Ensures the staking is counted against the current epoch.
     */
    function _increaseStake(
        uint256 _nodeIndex,
        uint256 _tokenId,
        uint256 _amount,
        bool _applyToCurrentEpoch
    ) internal {
        _requireValidTokenId(_nodeIndex, _tokenId);
        // Increase the staking for the node.
        StakingInfo storage _stakeInfo = nodeStakes[_nodeIndex][_tokenId];
        // If the last time the stake was modified was during a previous epoch, update the lowest to the current stake
        // amount before incrementing and set it to the current epoch.
        if (_stakeInfo.lowestEpoch != currentEpoch) {
            _stakeInfo.lowestEpoch = currentEpoch;
            _stakeInfo.lowest = _stakeInfo.amount;
        }
        // Make sure we are staking at least the minimum required amount.
        // Public node will never have a minimum stake.
        uint256 _newStakeAmount = _stakeInfo.amount + _amount;
        if (
            _newStakeAmount < nodes[_nodeIndex].minStake &&
            (_nodeIndex != PUBLIC_NODE_INDEX || _tokenId != PUBLIC_NODE_TOKEN_ID)
        ) {
            revert InvalidStake();
        }
        // Increase the stake.
        _stakeInfo.amount = _newStakeAmount;
        // Decrease the minimum stake amount if this somehow causes the amount to fall below the current minimum.
        if (_stakeInfo.amount < _stakeInfo.lowest) {
            _stakeInfo.lowest = _stakeInfo.amount;
        }
        // Apply this stake to the current epoch if required.
        if (_applyToCurrentEpoch) {
            _stakeInfo.lowest += _amount;
        }
        // Transfer the node to the new owner if "unowned"
        IMinutesNetworkNode _node = IMinutesNetworkNode(nodes[_nodeIndex].addr);
        if (_node.ownerOf(_tokenId) == address(this)) {
            _node.safeTransferFrom(address(this), msg.sender, _tokenId);
        }
        emit StakeIncreased(_nodeIndex, _tokenId, _amount, _newStakeAmount);
    }

    /**
     * @dev Internal function to increase staking on a public node. Isolated to allow mintToNodeStake to public node.
     *
     * @param _account The account adding to the stake value.
     * @param _amount The amount to stake.
     * @param _applyToCurrentEpoch Ensures the staking is counted against the current epoch.
     */
    function _increasePublicStake(
        address _account,
        uint256 _amount,
        bool _applyToCurrentEpoch
    ) internal {
        (, StakingInfo memory _publicInfo) = publicStakes.tryGet(_account);
        // If the last time the stake was modified was during a previous epoch, update the lowest to the current
        // stake amount before incrementing and set it to the current epoch.
        if (_publicInfo.lowestEpoch != currentEpoch) {
            _publicInfo.lowestEpoch = currentEpoch;
            _publicInfo.lowest = _publicInfo.amount;
        }
        // Increase stake
        _publicInfo.amount += _amount;
        // Decrease the minimum stake amount if this somehow causes the amount to fall below the current
        // minimum.
        if (_publicInfo.amount < _publicInfo.lowest) {
            _publicInfo.lowest = _publicInfo.amount;
        }
        // Apply this stake to the current epoch if required.
        if (_applyToCurrentEpoch) {
            _publicInfo.lowestEpoch = currentEpoch;
            _publicInfo.lowest += _amount;
        }
        publicStakes.set(_account, _publicInfo);
    }

    /**
     * @dev Internal function to decrease staking on a node.
     *
     * @custom:changelog v1.00.01 - Nodes completely unstaked will be revoked so other users may stake them.
     *
     * @param _nodeIndex The node index.
     * @param _tokenId The token ID associated with the node.
     * @param _amount The amount to unstake.
     */
    function _decreaseStake(
        uint256 _nodeIndex,
        uint256 _tokenId,
        uint256 _amount
    ) internal {
        _requireValidTokenId(_nodeIndex, _tokenId);
        // Handle the public node separately.
        if (_nodeIndex == PUBLIC_NODE_INDEX && _tokenId == PUBLIC_NODE_TOKEN_ID) {
            (, StakingInfo memory _publicInfo) = publicStakes.tryGet(msg.sender);
            // If the last time the stake was modified was during a previous epoch, update the lowest to the current stake
            // amount before incrementing and set it to the current epoch.
            if (_publicInfo.lowestEpoch != currentEpoch) {
                _publicInfo.lowestEpoch = currentEpoch;
                _publicInfo.lowest = _publicInfo.amount;
            }
            // Underflow protection (>=0.8) saves a require check
            uint256 _newAmount = _publicInfo.amount -= _amount;
            // Remove the entry if staking is decreased to 0
            if (_newAmount == 0) {
                publicStakes.remove(msg.sender);
            } else {
                // Decrease the minimum stake if required.
                if (_publicInfo.amount < _publicInfo.lowest) {
                    _publicInfo.lowest = _publicInfo.amount;
                }
                publicStakes.set(msg.sender, _publicInfo);
            }
        }

        // Decrease the staking for the node.
        StakingInfo storage _stakeInfo = nodeStakes[_nodeIndex][_tokenId];
        // Ensure there's enough staked.
        if (_amount > _stakeInfo.amount) {
            revert InvalidStake();
        }
        // Make sure we aren't going below the minimum stake, unless fully unstaked or public node.
        // Public node will never have a minimum stake.
        Node storage _nodeData = nodes[_nodeIndex];
        uint256 _newStakeAmount = _stakeInfo.amount - _amount;
        if (
            _newStakeAmount != 0 && _newStakeAmount < _nodeData.minStake &&
            (_nodeIndex != PUBLIC_NODE_INDEX || _tokenId != PUBLIC_NODE_TOKEN_ID)
        ) {
            revert InvalidStake();
        }
        // If the last time the stake was modified was during a previous epoch, update the lowest to the current stake
        // amount before incrementing and set it to the current epoch.
        if (_stakeInfo.lowestEpoch != currentEpoch) {
            _stakeInfo.lowestEpoch = currentEpoch;
            _stakeInfo.lowest = _stakeInfo.amount;
        }
        // Decrease the stake.
        _stakeInfo.amount = _newStakeAmount;
        // Decrease the minimum stake if required.
        if (_stakeInfo.amount < _stakeInfo.lowest) {
            _stakeInfo.lowest = _stakeInfo.amount;
        }
        // If the node is unstaked, transfer ownership to the contract if not the pubic node.
        if (_stakeInfo.amount == 0 && (_nodeIndex != PUBLIC_NODE_INDEX || _tokenId != PUBLIC_NODE_TOKEN_ID)) {
            IMinutesNetworkNode _node = IMinutesNetworkNode(_nodeData.addr);
            _node.transferFrom(msg.sender, address(this), _tokenId);
        }
        emit StakeDecreased(_nodeIndex, _tokenId, _amount, _newStakeAmount);
    }

    /**
     * @notice Function to make sure a node is valid and return the owner.
     *
     * @param _nodeIndex The node index.
     * @param _tokenId The token ID associated with the node.
     * @return The address of the node holder.
     */
    function getOwnerOfNode(uint256 _nodeIndex, uint256 _tokenId) public view returns (address) {
        if (_nodeIndex >= nodes.length) {
            revert InvalidNodeIndex();
        }
        _requireValidTokenId(_nodeIndex, _tokenId);
        return IMinutesNetworkNode(nodes[_nodeIndex].addr).ownerOf(_tokenId);
    }

    /**
     * @dev Ensures the token actually exists for a given node.
     *
     * @param _nodeIndex The node index.
     * @param _tokenId The token ID associated with the node.
     */
    function _requireValidTokenId(uint256 _nodeIndex, uint256 _tokenId) internal view {
        if (_nodeIndex >= nodes.length) {
            revert InvalidNodeIndex();
        }
        IMinutesNetworkNode _node = IMinutesNetworkNode(nodes[_nodeIndex].addr);
        if (_tokenId >= _node.mintedCount()) {
            revert InvalidTokenID();
        }
    }

    /**
     * @dev Ensures the sender owns the given node, or the node is public.
     *
     * @custom:changelog v1.00.01 - Nodes owned by the staking contract are considered "unowned"
     *
     * @param _nodeIndex The node index.
     * @param _tokenId The token ID associated with the node.
     */
    function _requireSenderOwnsNode(uint256 _nodeIndex, uint256 _tokenId) internal view {
        if (_nodeIndex != PUBLIC_NODE_INDEX || _tokenId != PUBLIC_NODE_TOKEN_ID) {
            address _owner = getOwnerOfNode(_nodeIndex, _tokenId);
            if (msg.sender != _owner && address(this) != _owner) {
                revert NotOwner();
            }
        }
    }

    /**
     * @notice Management function to add a node to the contract.
     *
     * @param _addr The address of the node contract.
     * @param _name The friendly name for consumers to identify the contract.
     * @param _minStake The minimum amount of tokens that must be staked against these nodes.
     */
    function addNode(
        address _addr,
        string memory _name,
        uint256 _minStake
    ) public onlyRole(DEFAULT_ADMIN_ROLE) {
        // Ensure the node matches the expected interface.
        if (!IMinutesNetworkNode(_addr).supportsInterface(nodeInterfaceId)) {
            revert InvalidNode();
        }
        nodes.push(Node(_addr, _name, _minStake));
        emit NodeAdded(nodes.length - 1, _addr, _name, _minStake);
    }

    /**
     * @notice Management function to update a node.
     *
     * @param _nodeIndex The node index.
     * @param _addr The address of the node contract.
     * @param _name The friendly name for consumers to identify the contract.
     * @param _minStake The minimum amount of tokens that must be staked against these nodes.
     */
    function updateNode(
        uint256 _nodeIndex,
        address _addr,
        string memory _name,
        uint256 _minStake
    ) public onlyRole(DEFAULT_ADMIN_ROLE) {
        if (_nodeIndex >= nodes.length) {
            revert InvalidNodeIndex();
        }
        if (!IMinutesNetworkNode(_addr).supportsInterface(nodeInterfaceId)) {
            revert InvalidNode();
        }
        Node storage nodeData = nodes[_nodeIndex];
        nodeData.addr = _addr;
        nodeData.name = _name;
        nodeData.minStake = _minStake;
        emit NodeUpdated(_nodeIndex, _addr, _name, _minStake);
    }

    /**
     * @notice Mints functions outside of any allocated pools.
     *
     * @param _to The address to mint to.
     * @param _amount The amount to mint.
     */
    function mint(address _to, uint256 _amount) public onlyRole(MINTER_ROLE) {
        _mint(_to, _amount);
    }

    /**
     * @notice Mints tokens staked directly against the given node. This will consider the tokens as staked from the
     *         start of the epoch, and count against that epoch's rewards. This will be used to allocate initial stakes.
     *
     * @param _nodeIndex The node index.
     * @param _tokenId The token ID associated with the node.
     * @param _amount The amount to be minted and staked.
     */
    function mintToNodeStake(
        uint256 _nodeIndex,
        uint256 _tokenId,
        uint256 _amount
    ) public onlyRole(DEFAULT_ADMIN_ROLE) {
        _mint(address(this), _amount);
        _increaseStake(_nodeIndex, _tokenId, _amount, true);
    }

    /**
     * @notice Mints tokens staked directly against the public node. This will consider the tokens as staked from the
     *         start of the epoch, and count against that epoch's rewards. This will be used to allocate initial stakes.
     *
     * @param _account The address that has staked the tokens.
     * @param _amount The amount to be minted and staked.
     */
    function mintToPublicNodeStake(
        address _account,
        uint256 _amount
    ) public onlyRole(DEFAULT_ADMIN_ROLE) {
        _mint(address(this), _amount);
        _increasePublicStake(_account, _amount, true);
        _increaseStake(PUBLIC_NODE_INDEX, PUBLIC_NODE_TOKEN_ID, _amount, true);
    }

    /**
     * @notice Sets the allocation details for a given fund.
     *
     * @param _fund The fund to set.
     * @param _maxSupply The maximum amount of tokens this allocation can mint.
     */
    function setAllocation(Funds _fund, uint256 _maxSupply) public onlyRole(DEFAULT_ADMIN_ROLE) {
        FundAllocation storage _allocation = allocations[_fund];
        if (_allocation.maxSupply != 0) {
            revert FundAllocated();
        }
        _allocation.maxSupply = _maxSupply;
    }

    /**
     * @notice Mint tokens from an allocation. Ensures the allocations reserved supply is not exceeded.
     *
     * @param _fund The fund to mint an allocation from.
     * @param _to The address to mint to.
     * @param _amount The amount to mint.
     */
    function mintAllocation(Funds _fund, address _to, uint256 _amount) public onlyRole(MINTER_ROLE) {
        FundAllocation storage _allocation = allocations[_fund];
        uint256 _newMinted = _allocation.minted + _amount;
        if (_newMinted > _allocation.maxSupply) {
            revert LowBalance();
        }
        _allocation.minted = _newMinted;
        _mint(_to, _amount);
        emit FundAllocationMinted(_fund, _to, _amount);
    }

    /**
     * @notice Increases the amount minted from an allocation without actually minting the tokens.
     *
     * @param _fund The fund to burn an allocation from.
     * @param _amount The amount to burn.
     */
    function mintAndBurnAllocation(Funds _fund, uint256 _amount) public onlyRole(MINTER_ROLE) {
        FundAllocation storage _allocation = allocations[_fund];
        uint256 _newMinted = _allocation.minted + _amount;
        if (_newMinted > _allocation.maxSupply) {
            revert LowBalance();
        }
        _allocation.minted = _newMinted;
        emit FundAllocationMinted(_fund, address(0), _amount);
    }

    /// @dev Override the _update function ensures maximum supply is never exceeded when minting.
    function _update(address _from, address _to, uint256 _value) internal override {
        // Is this a mint?
        if (_from == address(0) && totalSupply() + _value > MAX_SUPPLY) {
            revert ExceedsMaxSupply();
        }
        super._update(_from, _to, _value);
    }

    /**
     * @dev Converts a reward ID into a reward hash.
     *
     * @param _id The reward ID.
     * @return A hash generated from the reward ID.
     */
    function generateRewardHash(string calldata _id) public pure returns (bytes32) {
        return _generateRewardHash(bytes(_id), true);
    }

    /**
     * @dev Converts and validates a reward ID into a reward hash. Ensures there are no collisions with epoch reward
     *      hashes.
     *
     * @param _id The reward ID.
     * @param _isEpoch Flag to disable checking for epoch naming collisions.
     * @return A hash generated from the reward ID.
     */
    function _generateRewardHash(bytes memory _id, bool _isEpoch) internal pure returns (bytes32) {
        // Make sure the id isn't empty.
        if (_id.length == 0) {
            revert BadID();
        }
        // Make sure the id doesn't start with the epoch reserved prefix.
        if (!_isEpoch && _hasEpochRewardPrefix(_id)) {
            revert ReservedID();
        }
        return keccak256(_id);
    }

    function _hasEpochRewardPrefix(bytes memory _id) internal pure returns (bool) {
        uint256 _prefixLength = EPOCH_REWARD_PREFIX.length;
        if (_id.length >= _prefixLength) {
            bool identical = true;
            for (uint256 _o = 0; _o < _prefixLength;) {
                if (_id[_o] != EPOCH_REWARD_PREFIX[_o]) {
                    identical = false;
                    break;
                }
                unchecked {
                    _o++;
                }
            }
            return identical;
        } else {
            return false;
        }
    }

    function _authorizeUpgrade(address newImplementation)
    internal
    onlyRole(DEFAULT_ADMIN_ROLE)
    override
    {}

    /// @dev Implement ERC165
    function supportsInterface(bytes4 _interfaceId)
    public
    view
    override(IERC165, AccessControlUpgradeable)
    returns (bool)
    {
        return
            _interfaceId == type(IMinutesNetworkToken).interfaceId ||
            super.supportsInterface(_interfaceId);
    }
}

