contracts/MarketplaceV4.sol.content:
// SPDX-License-Identifier: Apache-2.0
pragma solidity ^0.8.11;

import "@thirdweb-dev/contracts/extension/ContractMetadata.sol";
import "@thirdweb-dev/contracts/extension/Permissions.sol";
import "@thirdweb-dev/contracts/extension/Royalty.sol";
import "@thirdweb-dev/contracts/extension/Ownable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";

// import "@openzeppelin/contracts/interfaces/IERC165.sol";
// import "@openzeppelin/contracts/interfaces/IERC2981.sol";
import {IERC721} from "@openzeppelin/contracts/token/ERC721/IERC721.sol";

contract NFTDomainsMarketplaceV4 is
    ContractMetadata,
    Permissions,
    Royalty,
    Ownable,
    Initializable,
    UUPSUpgradeable
{
    /*//////////////////////////////////////////////////////////////
                            State variables
    //////////////////////////////////////////////////////////////*/

    /// @dev The address of the native token wrapper contract.
    address private constant NATIVE_TOKEN_WRAPPER =
        0xEeeeeEeeeEeEeeEeEeEeeEEEeeeeEeeeeeeeEEeE;

    /// @dev The max bps of the contract. So, 10000 == 100 %
    uint64 public constant MAX_BPS = 10000;

    /// @dev The marketplace fee in basis points.
    uint64 private platformFeeBps;

    /// @dev The address that receives all platform fees from all sales.
    address private platformFeeRecipient;

    /// @dev Total number of listings ever created in the marketplace.
    uint256 public totalListings;

    /// @dev Contract level metadata.
    // string public contractURI;

    /// @dev Collection level metadata for listing types.
    string public listingMetadata;

    /// @dev The type of listing. (0 = Direct, 1 = Auction)
    enum ListingType {
        Direct,
        Auction
    }

    /// @dev The information related to a listing.
    struct Listing {
        uint256 listingId;
        address tokenOwner;
        address assetContract;
        uint256 tokenId;
        address currency;
        uint256 pricePerToken;
        uint256 startTime;
        uint256 endTime;
        bool reserved;
        address reservedAddress;
        ListingType listingType;
        uint256 minimumBid;
        address highestBidder;
        uint256 highestBid;
        bool settled;
    }

    /// @dev The information related to a bid on an auction.
    struct Bid {
        address bidder;
        uint256 bidAmount;
        uint256 timestamp;
    }

    /// @dev Mapping from listing ID => listing info
    mapping(uint256 => Listing) private listings;

    /// @dev Mapping from listing ID => whether the listing is active
    mapping(uint256 => bool) private listingActive;

    /// @dev Mapping from listing ID => mapping from bidder address to bid info
    mapping(uint256 => mapping(address => Bid)) private bids;

    /// @dev Mapping from tokenId => listingId
    mapping(uint256 => uint256) public tokenToListing;

    /*//////////////////////////////////////////////////////////////
                                Events
    //////////////////////////////////////////////////////////////*/

    event ListingAdded(
        uint256 indexed listingId,
        address indexed assetContract,
        address indexed tokenOwner,
        Listing listing
    );

    event ListingUpdated(
        uint256 indexed listingId,
        address indexed tokenOwner,
        Listing listing
    );

    event ListingRemoved(uint256 indexed listingId, address indexed tokenOwner);

    event NewBid(
        uint256 indexed listingId,
        address indexed bidder,
        uint256 bidAmount,
        uint256 timestamp
    );

    event AuctionSettled(
        uint256 indexed listingId,
        address indexed seller,
        address indexed buyer,
        uint256 price
    );

    event NewSale(
        uint256 indexed listingId,
        address indexed assetContract,
        address indexed buyer,
        uint256 tokenId,
        uint256 price
    );

    /*//////////////////////////////////////////////////////////////
                               Modifiers
    //////////////////////////////////////////////////////////////*/

    modifier onlyListingOwner(uint256 _listingId) {
        require(listings[_listingId].tokenOwner == msg.sender, "!owner");
        _;
    }

    modifier onlyActiveListings(uint256 _listingId) {
        require(listingActive[_listingId], "inactive listing");
        _;
    }

    /*//////////////////////////////////////////////////////////////
                        Constructor + initializer
    //////////////////////////////////////////////////////////////*/

    /// @dev Initializes the contract, like a constructor.
    function initialize(
        address _defaultAdmin,
        string memory _contractURI,
        // address[] memory _trustedForwarders,
        address _platformFeeRecipient,
        uint256 _platformFeeBps
    ) external initializer {
        // Initialize inherited contracts - add the init functions for each inherited contract
        // __Ownable_init();
        _setupOwner(_defaultAdmin);

        // Set contract URI
        contractURI = _contractURI;

        // Set platform fee recipient and bps
        platformFeeRecipient = _platformFeeRecipient;
        platformFeeBps = uint64(_platformFeeBps);

        // Grant the default admin role to the deployer
        _setupRole(DEFAULT_ADMIN_ROLE, _defaultAdmin);
    }

    /// @dev Required for UUPS proxy pattern
    function _authorizeUpgrade(
        address newImplementation
    ) internal override onlyRole(DEFAULT_ADMIN_ROLE) {}

    /*//////////////////////////////////////////////////////////////
                        Approval functions
//////////////////////////////////////////////////////////////*/

    // function approveMarketplaceForNFT(
    //     address _assetContract,
    //     uint256 _tokenId
    // ) external {
    //     IERC721 nftContract = IERC721(_assetContract);
    //     require(nftContract.ownerOf(_tokenId) == msg.sender, "Not token owner");
    //     nftContract.approve(address(this), _tokenId);
    // }

    /*//////////////////////////////////////////////////////////////
                        Direct listing functions
    //////////////////////////////////////////////////////////////*/

    function createListing(
        address _assetContract,
        uint256 _tokenId,
        address _currency,
        uint256 _pricePerToken,
        uint256 _startTime,
        uint256 _endTime,
        bool _reserved,
        address _reservedAddress,
        ListingType _listingType,
        uint256 _minimumBid
    ) external {
        require(_pricePerToken > 0, "price must be > 0");
        require(_startTime < _endTime, "invalid times");
        require(_startTime > block.timestamp, "start time in past");
        require(
            listingActive[tokenToListing[_tokenId]] == false,
            "listing for token already exists"
        );

        // Verify ownership of the NFT
        IERC721 nftContract = IERC721(_assetContract);
        require(
            nftContract.ownerOf(_tokenId) == msg.sender,
            "not owner of NFT"
        );

        // Check if the contract is approved to transfer the NFT
        require(
            nftContract.isApprovedForAll(msg.sender, address(this)) ||
                nftContract.getApproved(_tokenId) == address(this),
            "marketplace not approved"
        );

        // Create listing
        totalListings += 1;
        uint256 listingId = totalListings;

        listings[listingId] = Listing({
            listingId: listingId,
            tokenOwner: msg.sender,
            assetContract: _assetContract,
            tokenId: _tokenId,
            currency: _currency,
            pricePerToken: _pricePerToken,
            startTime: _startTime,
            endTime: _endTime,
            reserved: _reserved,
            reservedAddress: _reservedAddress,
            listingType: _listingType,
            minimumBid: _minimumBid,
            highestBidder: address(0),
            highestBid: 0,
            settled: false
        });

        listingActive[listingId] = true;
        tokenToListing[_tokenId] = listingId;

        emit ListingAdded(
            listingId,
            _assetContract,
            msg.sender,
            listings[listingId]
        );
    }

    function updateListing(
        uint256 _listingId,
        uint256 _pricePerToken,
        address _currency,
        uint256 _startTime,
        uint256 _endTime,
        bool _reserved,
        address _reservedAddress
    ) external onlyListingOwner(_listingId) onlyActiveListings(_listingId) {
        Listing storage listing = listings[_listingId];

        // Verify ownership of the NFT
        IERC721 nftContract = IERC721(listing.assetContract);
        require(
            nftContract.ownerOf(listing.tokenId) == msg.sender,
            "not owner of NFT"
        );

        require(_pricePerToken > 0, "price must be > 0");
        require(_startTime < _endTime, "invalid times");
        require(_startTime > block.timestamp, "start time in past");

        listing.pricePerToken = _pricePerToken;
        listing.currency = _currency;
        listing.startTime = _startTime;
        listing.endTime = _endTime;
        listing.reserved = _reserved;
        listing.reservedAddress = _reservedAddress;

        emit ListingUpdated(_listingId, msg.sender, listing);
    }

    function cancelListing(
        uint256 _listingId
    ) external onlyListingOwner(_listingId) onlyActiveListings(_listingId) {
        listingActive[_listingId] = false;
        emit ListingRemoved(_listingId, msg.sender);
    }

    function buy(
        uint256 _listingId,
        address _buyer,
        address _currency,
        uint256 _totalPrice
    ) external payable onlyActiveListings(_listingId) {
        Listing memory listing = listings[_listingId];
        require(
            listing.listingType == ListingType.Direct,
            "not a direct listing"
        );
        require(block.timestamp >= listing.startTime, "listing not active yet");
        require(block.timestamp <= listing.endTime, "listing expired");
        require(_currency == listing.currency, "invalid currency");
        require(_totalPrice == listing.pricePerToken, "invalid price");
        require(_buyer != listing.tokenOwner, "owner cannot buy listing");

        // Verify seller still owns the NFT
        IERC721 nftContract = IERC721(listing.assetContract);
        require(
            nftContract.ownerOf(listing.tokenId) == listing.tokenOwner,
            "seller no longer owns NFT"
        );

        if (listing.reserved) {
            require(_buyer == listing.reservedAddress, "not reserved buyer");
        }

        // Mark listing as inactive after purchase
        listingActive[_listingId] = false;

        // Handle fees
        uint256 platformFeeAmount = (_totalPrice * platformFeeBps) / MAX_BPS;
        uint256 sellerAmount = _totalPrice - platformFeeAmount;

        // Transfer payment
        if (listing.currency == NATIVE_TOKEN_WRAPPER) {
            require(msg.value == _totalPrice, "insufficient payment");
            // Transfer platform fee
            (bool success1, ) = platformFeeRecipient.call{
                value: platformFeeAmount
            }("");
            require(success1, "fee transfer failed");
            // Transfer to seller
            (bool success2, ) = listing.tokenOwner.call{value: sellerAmount}(
                ""
            );
            require(success2, "seller transfer failed");
        } else {
            // ERC20 payment
            // Implement ERC20 transfers here
        }

        // Transfer NFT
        nftContract.safeTransferFrom(
            listing.tokenOwner,
            _buyer,
            listing.tokenId
        );

        emit NewSale(
            _listingId,
            listing.assetContract,
            _buyer,
            listing.tokenId,
            _totalPrice
        );
    }

    /*//////////////////////////////////////////////////////////////
                        Auction listing functions
    //////////////////////////////////////////////////////////////*/

    function placeBid(
        uint256 _listingId,
        uint256 _bidAmount
    ) external payable onlyActiveListings(_listingId) {
        Listing storage listing = listings[_listingId];
        require(listing.listingType == ListingType.Auction, "not an auction");
        require(block.timestamp >= listing.startTime, "auction not active yet");
        require(block.timestamp <= listing.endTime, "auction expired");
        require(_bidAmount >= listing.minimumBid, "bid below minimum");
        require(_bidAmount > listing.highestBid, "bid too low");

        // Verify seller still owns the NFT
        IERC721 nftContract = IERC721(listing.assetContract);
        require(
            nftContract.ownerOf(listing.tokenId) == listing.tokenOwner,
            "seller no longer owns NFT"
        );

        // Store previous highest bidder to refund them
        address previousHighestBidder = listing.highestBidder;
        uint256 previousHighestBid = listing.highestBid;

        // Update highest bid
        listing.highestBidder = msg.sender;
        listing.highestBid = _bidAmount;

        // Store bid info
        bids[_listingId][msg.sender] = Bid({
            bidder: msg.sender,
            bidAmount: _bidAmount,
            timestamp: block.timestamp
        });

        // Handle ETH payments
        if (listing.currency == NATIVE_TOKEN_WRAPPER) {
            require(msg.value == _bidAmount, "insufficient payment");

            // Refund previous highest bidder if exists
            if (previousHighestBidder != address(0)) {
                (bool success, ) = previousHighestBidder.call{
                    value: previousHighestBid
                }("");
                require(success, "refund failed");
            }
        } else {
            // ERC20 payments
            // Implement ERC20 transfers here
        }

        emit NewBid(_listingId, msg.sender, _bidAmount, block.timestamp);
    }

    function settleAuction(
        uint256 _listingId
    ) external onlyActiveListings(_listingId) {
        Listing storage listing = listings[_listingId];
        require(listing.listingType == ListingType.Auction, "not an auction");
        require(block.timestamp > listing.endTime, "auction not ended");
        require(!listing.settled, "already settled");
        require(listing.highestBidder != address(0), "no bids");

        // Verify seller still owns the NFT
        IERC721 nftContract = IERC721(listing.assetContract);
        require(
            nftContract.ownerOf(listing.tokenId) == listing.tokenOwner,
            "seller no longer owns NFT"
        );

        listing.settled = true;
        listingActive[_listingId] = false;

        // Calculate fees
        uint256 platformFeeAmount = (listing.highestBid * platformFeeBps) /
            MAX_BPS;
        uint256 sellerAmount = listing.highestBid - platformFeeAmount;

        // Transfer payment
        if (listing.currency == NATIVE_TOKEN_WRAPPER) {
            // Transfer platform fee
            (bool success1, ) = platformFeeRecipient.call{
                value: platformFeeAmount
            }("");
            require(success1, "fee transfer failed");
            // Transfer to seller
            (bool success2, ) = listing.tokenOwner.call{value: sellerAmount}(
                ""
            );
            require(success2, "seller transfer failed");
        } else {
            // ERC20 payment
            // Implement ERC20 transfers here
        }

        // Transfer NFT
        nftContract.safeTransferFrom(
            listing.tokenOwner,
            listing.highestBidder,
            listing.tokenId
        );

        emit AuctionSettled(
            _listingId,
            listing.tokenOwner,
            listing.highestBidder,
            listing.highestBid
        );
    }

    /*//////////////////////////////////////////////////////////////
                        Getter functions
    //////////////////////////////////////////////////////////////*/

    function getListing(
        uint256 _listingId
    ) external view returns (Listing memory) {
        return listings[_listingId];
    }

    function isListingActive(uint256 _listingId) external view returns (bool) {
        return listingActive[_listingId];
    }

    function getAllListings() external view returns (Listing[] memory) {
        uint256 activeListingsCount = 0;

        // First, count the number of active listings
        for (uint256 i = 0; i <= totalListings; i++) {
            if (listingActive[i]) {
                activeListingsCount++;
            }
        }

        // Create an array of the correct size
        Listing[] memory activeListings = new Listing[](activeListingsCount);

        // Fill the array with active listings
        uint256 currentIndex = 0;
        for (uint256 i = 0; i < totalListings; i++) {
            if (listingActive[i]) {
                activeListings[currentIndex] = listings[i];
                currentIndex++;
            }
        }

        return activeListings;
    }

    function getBidForListing(
        uint256 _listingId,
        address _bidder
    ) external view returns (Bid memory) {
        return bids[_listingId][_bidder];
    }

    /*//////////////////////////////////////////////////////////////
                        Admin functions
    //////////////////////////////////////////////////////////////*/

    function setPlatformFeeInfo(
        address _platformFeeRecipient,
        uint256 _platformFeeBps
    ) external onlyRole(DEFAULT_ADMIN_ROLE) {
        require(_platformFeeBps <= MAX_BPS, "exceeds max bps");
        platformFeeRecipient = _platformFeeRecipient;
        platformFeeBps = uint64(_platformFeeBps);
    }

    function _canSetContractURI() internal view override returns (bool) {
        return hasRole(DEFAULT_ADMIN_ROLE, msg.sender);
    }

    function _canSetOwner() internal view override returns (bool) {
        return hasRole(DEFAULT_ADMIN_ROLE, msg.sender);
    }

    function _canSetRoyaltyInfo() internal view override returns (bool) {
        return hasRole(DEFAULT_ADMIN_ROLE, msg.sender);
    }

    /*//////////////////////////////////////////////////////////////
                        Royalty functions
    //////////////////////////////////////////////////////////////*/

    // function setDefaultRoyaltyInfo(
    //     address _royaltyRecipient,
    //     uint256 _royaltyBps
    // ) external onlyRole(DEFAULT_ADMIN_ROLE) {
